INSERT INTO `songs` (`id`, `title`, `lyric`, `Author`, `statusSongs`) VALUES
(6, 'Pai nosso', 'Pai nosso do Céu\r\nSanto é o Teu Nome\r\n\r\nQue venha Teu Reino\r\nSeja Feita Tua vontade\r\n\r\nNa Terra assim como nos Céus\r\nQue venha o Céu\r\n\r\nPois Teu é o Reino\r\nTeu é o Poder\r\nTua é a Glória\r\nPra sempre Amém\r\n\r\nQue venha o Céu', NULL, 1),
(10, 'Rompendo em Fé', 'Cada vez que a minha fé é provada\r\n\r\nTu me dás a chance de crescer um pouco mais\r\n\r\nAs montanhas e vale\r\nDesertos e mares \r\n\r\nque atravesso\r\nMe levam pra perto de Ti\r\n\r\nMinhas provações não são maiores do que o meu Deus\r\n\r\nE não vão me impedir de caminhar\r\n\r\nSe diante de mim não se abrir o mar\r\n\r\nDeus vai me fazer andar por sobre as águas\r\n\r\nRompendo em fé\r\nMinha vida se revestirá do Teu poder\r\n\r\nRompendo em fé\r\nCom ousadia vou mover no sobrenatural\r\n\r\nVou lutar e vencer, vou plantar e colher\r\n\r\nA cada dia vou viver rompendo em fé', NULL, 1),
(11, 'Seja Exaltado', 'Eu louvo o Teu nome. Louvo o Teu nome\r\n\r\nO nome que está sobre todo nome\r\n\r\nToda honra ao Teu nome. \r\nHonras ao Teu nome\r\n\r\nO nome que é maior que todo nome\r\n\r\nSeja Exaltado\r\nSeja Exaltado\r\nSeja Exaltado\r\nExaltado\r\n\r\nTeu nome é vida,\r\nTeu nome é a Esperança em mim. \r\nA esperança em mim\r\n\r\nTeu nome é amor, \r\nO amor que sempre me encontra. \r\nSempre me encontra', 'Sozo', 1),
(12, 'Pela fé', 'Pela fé \r\nNo Filho de Deus\r\nSou vencedor\r\n\r\nTodo mal\r\nAfasta de Mim\r\nCristo Senhor\r\n\r\nTudo posso em Jesus,\r\nMeu fiel e bom pastor\r\n\r\nDigno é de receber todo louvor', 'Adhemar de Campos', 1),
(13, 'Quem é este o Rei da glória', 'Quem é este \r\no Rei da Glória?\r\n\r\nAbram-se os \r\nPortais Eternos\r\n\r\nAbram-se os portais\r\nEternos portais\r\n\r\nPra que entre \r\no Rei da Glória!', 'Sozo (Jonh Mark)', 1),
(14, 'Tão profundo (tao profundo)', 'Nada nos separará\r\nDos laços do teu grande amor\r\n\r\nDe longe ouvimos o amor que chama\r\nPaixão profunda, bondade e graça\r\nSe derramando\r\n\r\nÉ tão profundo, tão imenso e cobre-nos\r\nÉ furioso, poderoso e abraça-nos\r\n\r\nSó ele pode devolver a vida aos corações\r\n\r\nO Pai que seu filho nos deu\r\nO Filho que por todos morreu\r\n\r\nEm nos derrama amor e graça\r\nE nos convida á sua mesa\r\nNos alcançando\r\n\r\nÉ tão profundo, tão imenso e cobre-nos\r\nÉ furioso, poderoso e abraça-nos\r\n\r\nSó ele pode devolver a vida aos corações', 'Vineyard', 1),
(15, 'Vou crer', 'Colocar minha fé em Ti\r\n\r\nCaminhar sem ao lado olhar\r\n\r\nEu creio e confio em Ti\r\n\r\nNão vou vacilar\r\nNão vou temer\r\nVou crer\r\n\r\nOh Deus abre a porta para mim\r\nAo lugar que tens só pra mim\r\n\r\nLeva-me até o centro do teu coração\r\n\r\nGuia-me segurando em minhas mãos\r\n\r\nCarrega-me, quando não posso mais andar\r\n\r\nProtege-me, Leão da Tribo de Judá\r\n\r\nNão vou vacilar, não vou temer\r\nVou crer\r\n\r\nAcima do medo\r\nAcima da dor\r\nAcima da aflição\r\n\r\nDe comunhão', 'André Valadão', 1),
(16, 'Glória, te darei a Glória', 'O meu coração se inclina a adorar\r\nAquele que primeiro me amou\r\n\r\nFez de um perdido um filho a enxergar\r\nA majestade do Seu redentor\r\n\r\nImenso prazer pra mim é te ter Jesus\r\n\r\nDesde agora e para sempre te adorarei\r\n\r\nGlória, te darei a Glória\r\nTodos os meus dias\r\n\r\nEnquanto eu viver\r\nEu quero dar-te Glória\r\n\r\nGlória, te darei a Glória\r\nTodos os meus dias\r\n\r\nEnquanto eu viver\r\nEu quero dar-te Glória\r\n\r\nPreço muito alto Ele pagou por mim\r\nTrouxe-me a alegria de viver\r\n\r\nEnvia-me ao mundo para apregoar\r\nA graça e o amor que vem de ti\r\n\r\nImenso prazer\r\nPra mim é te ter Jesus\r\n\r\nDesde agora e para sempre Te adorarei\r\n\r\nGlória, te darei a Glória\r\nTodos os meus dias\r\n\r\nEnquanto eu viver\r\nEu quero dar-te Glória\r\n\r\nGlória, te darei a Glória\r\nTodos os meus dias\r\n\r\nEnquanto eu viver\r\nEu quero dar-te Glória\r\n\r\nGlória, te darei a Glória\r\nTodos os meus dias\r\n\r\nEnquanto eu viver\r\nEu quero dar-te Glória\r\n\r\nGlória, te darei a Glória\r\nTodos os meus dias\r\n\r\nEnquanto eu viver\r\nEnquanto eu viver', 'Lucas Souza', 1),
(18, 'O Hino', 'Posso ouvir os passos do meu Rei\r\nPosso ouvir seu coração bater\r\n\r\nDa escuridão me libertou\r\nEu posso ouvir a voz do meu Senhor\r\n\r\nDizendo \r\nFilho meu\r\nChegou sua vez\r\n\r\nVocê nasceu para brilhar num tempo assim\r\n\r\nAcorda\r\nFilho meu\r\nChegou sua vez\r\n\r\nVocê nasceu para brilhar num tempo assim\r\n\r\nPosso ouvir um poderoso som\r\nPosso ouvir o clamor da geração\r\n\r\nTrazendo cura e libertação\r\nQuero ouvi-lo quando me  chamar dizendo\r\n\r\nEsse é o hino dessa geração\r\n\r\nEis-me aqui Pai\r\nme envia\r\n\r\nTodos precisam deste amor\r\nVocê me cativou\r\n\r\nEu sou realeza\r\nEu tenho um destino\r\n\r\nEu já fui liberto\r\nEu vou moldar a história\r\n\r\nEu vou mudar o mundo', 'Jake Hamilton', 1),
(19, 'Aba Pai', 'Pai, Aba Pai\r\n\r\nO amor que achei\r\nJamais acaba\r\n\r\nPai, fica mais\r\nSenta aqui\r\nTua voz me acalma\r\n\r\nPai, Aba Pai\r\n\r\nQue bom saber\r\nQue és meu Pai\r\nE que jamais\r\nMe deixará\r\n\r\nMas que bom saber\r\nQue És meu Pai', NULL, 1),
(20, 'Abraço', 'Quero sentir Teu abraço\r\nSeus braços ao meu redor\r\nTeu coração batendo\r\nJunto ao meu\r\n\r\nE Ele me diz: Tudo vai ficar bem\r\n\r\nQuero ver Tua face\r\nQuerendo ver o que eu posso ser\r\n\r\nQuero ver o que Tu podes ver\r\nNo espelho de Seus olhos\r\n\r\nE Ele me diz\r\n\r\nTudo vai ficar bem\r\n\r\nPois eu sei. Se o Teu olhar está nos pássaros. Teu coração está em mim\r\n\r\nE eu não quero ter que esperar pra ir para o Céu quando eu morrer \r\nEu quero ir agora. Nós vamos ir agora\r\n\r\nEsse é o som do Céu invadindo a Terra', NULL, 1),
(21, 'Agnus Dei', 'Aleluia\r\nSanto És Tu\r\nOh Cordeiro de Deus\r\n\r\nSanto, Santo\r\nÉ o Senhor Poderoso\r\n\r\nDigno Tu És\r\nDe todo louvor', NULL, 1),
(22, 'Até que a casa esteja cheia', 'Desse amor\r\nSou mais uma vítima\r\nEle me tem\r\nDesde o primeiro dia\r\n\r\nNosso encontro\r\nJá se aproxima\r\nEu posso Te ouvir\r\nChamar meu nome\r\n\r\nVou me deitar\r\nE com os olhos percorrer\r\nO caminho do sol\r\n\r\nEu vejo correr\r\nComo alguém que\r\nNunca se cansou\r\n\r\nSim eu quero ser\r\nEssa peça que funciona\r\nAté Teu sonho se realizar\r\n\r\nNo silêncio\r\nEu sinto Tua brisa\r\nE quando penso em Ti\r\nTua mão me acaricia\r\n\r\nVem de dentro\r\nCada cor desse jardim\r\nDesde que a luz nasceu\r\nNada é tão real pra mim\r\n\r\nVou me deixar levar\r\nComo uma nuvem pelo vento e ver\r\nA Terra passar\r\nDo alto é simples como deve ser\r\n\r\nSim eu quero ter\r\nUm coração ligado ao Teu\r\nPra que onde eu for\r\nSeja você em meu lugar\r\n\r\nQue das minha feridas\r\nSaia poder pra curar\r\nE cada hora perdida\r\nMe lembre que não é\r\nPra eu parar\r\n\r\nAté que a casa esteja cheia\r\nAté que o Teu Reino venha\r\nE que cada coisa esteja em seu lugar\r\n\r\nToda Terra cheia\r\nDa Sua Glória', NULL, 1),
(23, 'Baseado em que', 'É tanta onda\r\nQue eu nem sei\r\nBarraca, fogueira\r\nGrama molhada\r\nE a rapaziada\r\n\r\nUm chegado me pede\r\nUm baseado\r\nE eu pergunto a você\r\nBaseado em quê?\r\n\r\nE nela eu viajo\r\n\r\nDoidera é abrir o mar\r\nCom um cajado\r\n\r\nÉ muito fácil\r\nJoão comia gafanhoto\r\n\r\nVer gnomo\r\nNa janela pendurado\r\n\r\nFez pegar fogo no molhado\r\n\r\nSe baseia na videira\r\nSe liga na Palavra\r\nQue é a verdadeira\r\nEstou ficando viciado\r\n\r\nÉ ter dinheiro\r\nVende drogas pra fazer\r\nSua riqueza\r\n\r\nJá passou por uma agulha\r\nO camelo\r\nE o rico ficou de bobeira\r\n\r\nOnda chapado\r\nMas chapado vive só\r\nLevando vaca\r\n\r\nFalava com Balaão\r\nJesus andava\r\nSobre as águas\r\n\r\nTira onda com essa onda de gandaia\r\nSe tu quer "caya"\r\nCaia na real\r\n\r\nA melhor onda\r\nÉ a PALAVRA!', NULL, 1),
(24, 'Caia Fogo', 'Diante da Sua glória\r\n\r\nSou teu templo\r\nTeu sacrifício\r\nO teu altar\r\nVem queimar em mim\r\n\r\nQueima esse altar\r\nMostra pra esse povo\r\nQue há Deus em Israel', NULL, 1),
(25, 'Canção da Liberdade', 'E a Liberdade será\r\nA canção do meu coração\r\n\r\nEu sou Livre\r\nLivre pra te Adorar\r\n\r\nE o véu do Templo se rasgou\r\nE eu posso entrar\r\n\r\nNo Santo Lugar\r\nNas alturas onde Tu estás\r\n\r\nNão vou mais olhar atrás\r\nMeu passado não importa mais\r\n\r\nEu sou livre', NULL, 1),
(26, 'Closer', 'Senhor o Teu amor me arrebatou\r\nMeu desejo é estar\r\nContigo pra sempre\r\n\r\nEntão traga-me pra mais perto\r\nLeva-me mais profundo\r\nEu quero conhecer\r\nTeu coração\r\n\r\nO Teu amor é tão mais doce\r\nQue tudo que já provei\r\n\r\nEu quero conhecer\r\nTeu coração\r\n\r\nOoh, Grande é o Teu amor por mim', NULL, 1),
(27, 'Como prometes', 'Agita as águas \r\nparadas da minha alma\r\n\r\nMe leva com \r\no teu rio que \r\ntraz vida\r\n\r\nNão tenho as palavras\r\nCertas há dizer\r\n\r\nPra provocarte a\r\nQuerer-me mais\r\nDo que já me queres\r\n\r\nEntão vem\r\nComo prometeste\r\nDerrama o \r\nteu Espirito\r\n\r\nInvade a minha escuridão\r\nAonde me escondo\r\n\r\nMe abraça com Teus braços\r\nCom Tua paz Senhor\r\n\r\nMe alcança quando \r\nme escondo\r\nSim me alcança\r\nQuando fujo\r\n\r\nSenhor vem encher\r\nMinh''alma de amor\r\n\r\nEntão vem\r\nComo prometeste\r\nDerrama o \r\nteu Espirito\r\n\r\nTeu amor é como\r\nDe um pai, de um irmão\r\n\r\nComo a garra de um leão\r\nAssim como nenhum outro\r\n\r\nViolentamente\r\nPersegues e \r\nme abraças\r\n\r\nMe envolves\r\nEm quem Tu És', NULL, 1),
(28, 'Dependo de Ti', 'Louvar-Te é bem mais\r\nDo que eu mereço\r\nAdorar-Te mais profundo\r\nDo que eu pensei\r\n\r\nServir-Te é tudo\r\nO que eu espero\r\nE o que mais quero\r\nÉ estar mais junto a Ti\r\n\r\nDerramar perante Ti\r\nOs meus anseios\r\nA minha oferta nessa hora\r\nÉ a verdade\r\n\r\nE te contar os meus\r\npequenos sonhos\r\nAh Senhor\r\nComo eu dependo de Ti\r\n\r\nPara me curar\r\nPara me ensinar\r\nO caminho que devo seguir\r\n\r\nPodes me usar\r\nComo Tu bem queres\r\nEu abro o meu coração', NULL, 1),
(29, 'Diante de Ti', 'Vem Senhor encher esse lugar\r\nCom tua Glória\r\n\r\nFala-me eu quero Te ouvir\r\n\r\nToca-me eu quero Te sentir\r\n\r\nVem e abraça-me\r\n\r\nTodo dia é dia\r\nDe adorar ao Senhor\r\n\r\nEu conto os segundos\r\nSó pra Te encontrar\r\n\r\nE quando estou em Tua presença\r\n\r\nDá vontade de pular\r\nDá vontade dançar\r\n\r\nDá vontade de gritar\r\nDá vontade de correr\r\n\r\nDiante de Ti', NULL, 1),
(30, 'Ele Nos Ama', 'Ele se importa comigo\r\nMe ama como um furacão\r\n\r\nSou como uma árvore que se move pelo seu vento de misericórdia\r\n\r\nE quando esqueço de minhas aflições ofuscadas por Sua Glória\r\n\r\nPosso perceber O quão Lindo Tu és\r\nE quão grande seus afetos por mim\r\n\r\nOh como Ele nos ama tanto,\r\n\r\nOh como Ele nos ama,\r\nComo Ele nos ama tanto\r\n\r\nEle nos ama,\r\nOh! como Ele nos ama,\r\n\r\nOh! como Ele nos ama,\r\nOh! Como Ele ama!\r\n\r\nSomos sua herança\r\nEle é o nosso galardão\r\n\r\nSeu olhar de graça\r\nNos atrai a redenção\r\n\r\nSe a graça é como\r\nUm oceano estamos afogando\r\n\r\nO céu se une a Terra\r\nComo um beijo apaixonado\r\n\r\nMeu coração dispara\r\nEm meu peito acelerado\r\n\r\nNão perco meu tempo\r\nEm lamentos\r\n\r\nQuando me lembro\r\nQue Ele', NULL, 1),
(31, 'Ensina-me a Te amar mais', 'Meu maior desejo\r\nÉ Te amar\r\nJesus\r\n\r\nAleluia\r\n\r\nA Te amar mais', NULL, 1),
(32, 'Espirito Santo Vem', 'Espirito Santo\r\n\r\nTeus presentes\r\nPara mim\r\nO maior amor\r\nQue conheci\r\nNão tem fim\r\n\r\nMe encontrar contigo\r\nÉ o meu prazer\r\nTeu Espirito em mim\r\nÉ o que me faz viver\r\n\r\nQuem é como\r\nO Senhor?\r\nIrmão ninguém\r\nNão desejo outra coisa', NULL, 1),
(33, 'Eu Amo Sua Presença', 'Outro bem além de Ti\r\nEu não vou mais procurar,\r\nO que só em Ti eu posso encontrar\r\n\r\nHá mais luz na sua presença do que em tudo que eu já vi\r\nEu amo sua presença', NULL, 1),
(34, 'Eu Corro para Ti', 'Nada mais me acontece\r\nTudo está tão vazio\r\n\r\nProcuro e não te acho\r\nPor que\r\nEstás tão distante?\r\n\r\nEu olho para o lado e vejo\r\nFaz tempo que eu não\r\nTe busco\r\n\r\nMinha alma está sedenta\r\nEu quero a Tua presença\r\n\r\nEu corro para Ti\r\nNão sei viver sem Ti\r\nSem ter o teu amor\r\nNão posso respirar\r\n\r\nNão há outro lugar\r\nSó em Teus braços\r\nEu encontro o que\r\nPreciso ter\r\n\r\nEnche-me \r\nCom Tua presença\r\nVem transborda\r\nA minha vida\r\n\r\nMe restaura com Teu fogo\r\nEu quero começar denovo', NULL, 1),
(35, 'Eu Fui Comprado', 'Justificado\r\nA minha vida\r\nFoi paga lá na cruz\r\n\r\nTá tudo pago\r\nFoi apagado\r\nPelo meu Jesus\r\n\r\nTodo mundo pulando\r\nNa presença de Deus é', NULL, 1),
(36, 'Eu Quero Mais', 'Uma fome tomou conta de mim\r\nUm desejo por mais de ti\r\nEu preciso de algo novo\r\nEu preciso ser tocado\r\n\r\nDia a dia estou sendo tocado\r\nMeu coração esta queimando\r\nCom um novo clamor:\r\n\r\nMais!\r\nEu quero mais!\r\nEu quero mais!\r\nQuero mais de ti Senhor!', NULL, 1),
(37, 'Eu Sou Livre', 'Os cegos verão por Ti\r\nOs mudos cantarão\r\nOs mortos viverão\r\nOs povos te adorarão\r\n\r\nAs trevas fugirão\r\nPor Ti eu grito\r\nEu sou livre\r\n\r\nLivre pra correr\r\nLivre pra dançar\r\nLivre pra viver por Ti\r\nEu sou livre', NULL, 1),
(38, 'Eu vou queimar até o fim', 'Envolto em glória\r\nMeu amado vem\r\nEstou indo ao seu encontro\r\n\r\nSorrindo e cantando\r\nCanções de amor\r\nEu me entrego\r\nAos seus braços\r\n\r\nEu vou queimar até o fim\r\nPor Ti\r\nNada pode apagar\r\nA chama que arde em mim\r\n\r\nEu vou Te amar\r\nEnquanto eu viver\r\nNada vai me separar\r\nDo Teu grande amor\r\nPor mim\r\n\r\nAleluia\r\nMeu amado vem', NULL, 1),
(39, 'Faz Chover', 'Faz chover\r\nAbra as comportas do céu\r\n\r\nDo tamanho da mão\r\nDe um homem\r\nMas este é o sinal\r\nQue a Tua chuva vai descer\r\n\r\nSe pedirmos Jesus virá\r\nComo chuva descerá', NULL, 1),
(40, 'Fez um caminho pra mim', 'Pelo vermelho de Sua túnica\r\nA cada prego\r\nA cada martelar\r\n\r\nPelas feridas\r\nQue afligiram o Senhor\r\nEu posso ouvir\r\nO Seu clamor\r\n\r\nPai eu desejo\r\nQue eles sejam em mim\r\nPara que vejam Tua glória\r\n\r\nMinha vergonha não\r\nSe encontra mais em mim\r\nPara liberdade\r\nMe libertou\r\n\r\nFez um caminho pra mim\r\nPor onde eu não poderia ir\r\n\r\nEu sou Teu\r\nTu és meu\r\nE seremos juntos\r\nPra sempre!', NULL, 1),
(41, 'Fogo que Consome', 'Fogo que consome\r\nNós te desejamos\r\nChamas do amor\r\nBatiza-nos\r\n\r\nNos dá mais\r\nPaixão por Ti\r\n\r\nQuão profundo\r\nAlto, largo é\r\nTeu amor', NULL, 1),
(42, 'Isaias 9', 'Um menino nasceu\r\nComo um filho se nos deu\r\n\r\nEle é o próprio Deus\r\nQue vive em mim\r\n\r\nDebaixo de Tuas asas\r\nEu me escondi\r\n\r\nE o seu nome é \r\nMaravilhoso\r\nGlória ao Príncipe da Paz\r\n\r\nO céu começa a se abrir\r\nToda Terra se dobrou a Ti\r\n\r\nCristo Rei dos reis\r\nVeio nos buscar\r\n\r\nLeva-nos em Tuas mãos\r\nPelas portas da cidade\r\n\r\nNa Nova Jerusalém\r\nTua noiva vai entrar', NULL, 1),
(43, 'Liberdade', 'Onde o Espirito de Deus está\r\nEu sou livre\r\n\r\nVocê que tem fome\r\nSeja livre\r\n\r\nVocê que está cansado\r\nSeja livre\r\n\r\nLiberdade está nesse lugar\r\nCom misericórdia e graça\r\n\r\nFluindo sobre nós\r\nSomos livres\r\n\r\nJesus reina nesse lugar\r\nCom misericórdia e graça\r\n\r\nFluindo sobre nós\r\nSomos livres\r\n\r\nOnde o Espírito de Deus está\r\nEu sou livre\r\n\r\nOlhe para o Céu\r\nSeja livre\r\n\r\nOlhe para Jesus\r\nSeja livre', NULL, 1),
(44, 'Love Came Down', 'Se o meu coração está\r\nSem ouvir a Tua voz\r\n\r\nEu me agarro a verdade\r\nMesmo se não ver\r\n\r\nSe a tempestade vem\r\nE é dificil caminhar\r\n\r\nVou me levantar a fé\r\nE crer em Ti\r\n\r\nVou-me relembrar sua \r\nobra por mim\r\n\r\nE tudo o que tenho\r\nAtravés da cruz\r\n\r\nTeu amor me resgatou\r\nTeu amor me libertou\r\n\r\nE hoje eu sou\r\nEu sou pra sempre Teu\r\n\r\nNa alegria ou na dor\r\nCantarei a me lembrar\r\n\r\nQue eu sou Teu\r\nEu sou pra sempre Teu\r\n\r\nSe o meu coração está\r\nPreenchido por Tua paz\r\n\r\nQuando sinto Tuas mãos\r\nA me agraciar\r\n\r\nPermaneço a Ti amar\r\nQuebrantado aos Teus pés\r\n\r\nVou erguer o meu louvor\r\nE crer em Ti\r\n\r\nEu sou Teu\r\nEu sou Teu\r\nPara sempre\r\nJesus, eu sou Teu', NULL, 1),
(45, 'Mais amor', 'Mais amor\r\nMais poder\r\nMais de Ti\r\nEm minha vida\r\n\r\nEu te adorarei\r\nCom todo\r\nMeu coração\r\n\r\nEu te adorarei\r\nCom toda\r\nMinha mente\r\n\r\nEu te adorarei\r\nCom toda\r\nMinha força\r\n\r\nPois Tu és meu Deus', NULL, 1),
(46, 'Majestade Santa', 'Majestade Santa\r\n\r\nDeixa o fogo descer\r\nDeixa o vento soprar\r\nDeixa a Glória Invadir', NULL, 1),
(47, 'Me Mostre Sua Glória', 'Eu não temerei\r\n\r\nMe mostre Sua Glória\r\n\r\nDaquele que amo\r\nE ficar em Tua presença\r\nÉ o meu lugar', NULL, 1),
(48, 'Meu Alvo', 'Estou subindo\r\nPra um lugar mais alto\r\n\r\nEu já queimei\r\nAs pontes com o passado\r\n\r\nE os meus olhos\r\nVejo um futuro\r\n\r\nTudo novo se fez\r\nTudo novo se faz\r\n\r\nE dessa estrada\r\nEu não me desvio\r\nNunca mais\r\n\r\nEstou firme\r\nEu não me desvio\r\nNunca mais\r\n\r\nVou avançar\r\nEu vou crescer\r\n\r\nNinguém vai me deter\r\nMeu alvo é Cristo\r\n\r\nEstou subindo\r\nPra um lugar mais alto\r\n\r\nEu já desisti\r\nDe andar sozinho\r\n\r\nCristo vive em mim\r\n\r\nE os meus pés\r\nEstão no caminho', NULL, 1),
(49, 'Nível Raso', 'Me mantendo firme\r\nMesmo quando esse chão\r\nSe move\r\nOlhar pra baixo não resolve\r\nSó assombra\r\n\r\nDe onde estou já não há volta\r\nE não há nada a minha volta\r\n\r\nEu não consigo ver\r\nNão sei onde está Você\r\nMe sinto só\r\nE começo a esperar pelo pior\r\n\r\nA luz do barco está bem longe\r\nEnquanto minha voz não sai\r\nO medo me responde\r\n\r\nEu fui além do que é possível\r\nAo homem\r\nMas eu parei e agora\r\nAs águas me consomem\r\n\r\nPensamentos maus\r\nPesam meus temores\r\nDesprezam o que me fez sair\r\nO que me trouxe até aqui\r\n\r\nPois sem fé é impossível\r\nAgradar a Deus\r\nE eu não quero terminar assim\r\n\r\nEu não posso me conformar\r\nCom esse nível raso\r\nO meu poder humano\r\nNão tem poder pra trazer\r\nO Teu Reino aqui\r\n\r\nMinha Porção vem do Céu\r\nEu tenho fome de Ti Senhor\r\n\r\nUm vazio que eu sinto\r\nQuando preencho o vazio\r\nDo abismo\r\nPorque eu não quis mudar\r\n\r\nSó vai passar se a porta\r\nAinda estiver aberta pra mim\r\nSe houver uma chance\r\nPra recuperar\r\nO tempo que perdi enfim\r\n\r\nEnvolto em algas e águas amargas\r\nSenti na pele a dor das almas\r\nNão alcançadas\r\nPor mim esquecidas\r\n\r\nFariam de mim um homicída\r\nE a vida que eu quis tanto\r\nPreservar lhes traria a vida\r\n\r\nSó tenho tempo pra orar\r\nSerá?\r\nQue a voz que me chamou\r\nAinda pode me escutar\r\n\r\nSó há um jeito de sair\r\nContinuarei daqui\r\nComo se fosse quando\r\nA missão Ele me trouxe', NULL, 1),
(50, 'No teu jardim', 'Todo Poderoso\r\nSeja o centro do meu ser\r\n\r\nMe toma em tuas mãos\r\nE mostra-me o teu reino\r\n\r\nCom o meu corpo\r\nEu te glorificarei\r\n\r\nTe dou meu coração\r\nFaz nele a tua tenda\r\n\r\nO teu altar\r\nA tua casa\r\n\r\nQue eu seja o lugar \r\nOnde tu és adorado \r\nNoite e dia\r\n\r\nEu estou em ti\r\nTu estás em mim\r\nComo foi no teu jardim Senhor\r\n\r\nTu és Santo\r\n\r\nTu és Santo\r\nTu és Santo Deus', 'Rodolfo Abrantes', 1),
(52, 'O que eu sonho ver', 'Estás acima de todos\r\nTu és o mais importante\r\nNão há ninguem além de Ti\r\nQue se assente no trono\r\n\r\nSinto que vou alcançar-Te, óh Deus\r\nMeu sonho é te ver tão perto\r\n\r\nMostra-me o que\r\nEu tenho sonhado\r\nE o que eu sonho\r\nÉ estar nos Céus e ver\r\n\r\nVer!\r\nVer o Filho reinando pra sempre\r\nSoberano com o Pai sobre todos\r\n\r\nVer Teu povo adorando o Cordeiro\r\nTua Glória Brilhando\r\nComo o Sol\r\n\r\nTua igreja cantando\r\nQue és Santo\r\nTua Beleza enchendo o lugar\r\n\r\nTeu Amor\r\nIndo em forma de cura\r\nE os perdidos\r\nRendidos no altar', NULL, 1),
(53, 'O que Tua Glória fez comigo', 'Faço tudo\r\nMas vem novamente\r\n\r\nNa mirra ardente\r\nMas peço que\r\nSua presença aumente\r\n\r\nNão temerei\r\nNa Tua fumaça de glória\r\nEu entrarei\r\n\r\nLonge do Santo dos Santos\r\nNão sei mais viver\r\n\r\nQuem já pisou\r\nNo Santo dos Santos\r\nEm outro lugar\r\nNão sabe viver\r\n\r\nClama pela glória\r\na glória de Deus\r\n\r\nSanto', NULL, 1),
(54, 'Obsessão', 'O que eu faço\r\nCom a minha obsessão?\r\nPelas coisas que não vejo\r\n\r\nExiste algo mau\r\nEm meu ser?\r\nEsse é o vento\r\nQue sopra as árvores?\r\n\r\nAs vezes mais que a lua\r\nEstás tão longe\r\nAs vezes mais que a pele\r\nEstás tão perto\r\n\r\nMas Tu me rendes\r\nComo um nevoeiro\r\nMe queimando\r\nCom um beijo\r\n\r\nMeu coração queima por Ti\r\n\r\nE pra sempre\r\nMe incendeie\r\nMe incendeie\r\nCom o fogo do Teu amor', NULL, 1),
(55, 'Oh meu irmãozinho', 'Eu to aqui pra te dizer\r\n\r\nQue eu era pecadore\r\nMas agora sou lavado\r\nPelo Sangue de Jesus\r\n\r\nJesus Ama você\r\nSe você ama Ele também\r\nTira o Pé do chão e vem', NULL, 1),
(56, 'Our Father', 'Our Father in Heaven\r\n\r\n(Pai nosso do Céu)\r\n\r\nHallowed be Your name\r\n\r\n(Santo é o Teu nome)\r\n\r\nYour Kingdom come quickly\r\n\r\n(Que venha Teu Reino)\r\n\r\nYour will be done the same\r\n\r\n(Seja feita a Tua vontade)\r\n\r\nOn Earth as it is in Heaven\r\n\r\n(Na Terra assim como no Céu)\r\n\r\nLet Heaven come to\r\n\r\n(Que venha o Céu)', NULL, 1),
(58, 'Pelo Senhor', 'Pelo Senhor\r\nMarchamos sim\r\nO seu exército\r\nPoderoso é\r\n\r\nSua glória\r\nSerá vista\r\nEm toda Terra\r\n\r\nVamos cantar\r\nO canto da vitória\r\nGlória a Deus\r\nVencemos a batalha\r\n\r\nToda arma\r\nContra nós perecerá\r\n\r\nO nosso general\r\nÉ Cristo\r\nSeguimos\r\nOs seus passos\r\n\r\nNenhum inimigo\r\nNos resistirá\r\n\r\nPelo Messias\r\nMarchamos sim\r\nEm suas mãos\r\nA chave da vitória\r\n\r\nQue nos leva\r\nA possuir\r\nA terra prometida', NULL, 1),
(59, 'Poe um Fogo', 'Não desejo outro lugar para estar\r\n\r\nDo que no seu amor\r\n\r\nPõe um fogo\r\nEm meu coração\r\n\r\nQue eu não possa conter\r\nQue eu não tenha o controle\r\n\r\nEu quero mais de Ti Deus', NULL, 1),
(60, 'Quando olho em Teus olhos', 'Quando olho em Teus olhos\r\nVejo a grandeza\r\nDo Teu amor\r\n\r\nDiante do mar\r\nDe alegria e perdão\r\n\r\nQuando olho em Teus olhos\r\nMeu coração se entrega\r\nAo Teu\r\n\r\nPor Tua presença\r\nAmado meu\r\n\r\nQuando olho em Teus olhos\r\nTua chama acende\r\nEm meu coração\r\n\r\nA fazer Tua vontade\r\nO Teu querer\r\n\r\nMinha vida é mudada\r\nDe Glória em glória\r\nPareço contigo\r\n\r\nJesus\r\nO sonho da minha vida\r\nÉ ser igual a Ti', NULL, 1),
(61, 'Que Vem do Céu', 'Nova chance, novo Reino\r\nNovo recomeço\r\nQue vem do Céu\r\n\r\nE a vida que sonhei\r\nQue vem do Céu\r\n\r\nNovo dom, nova graça\r\nNovo jeito de pensar\r\nLiberdade, Santidade\r\nAlegria ao coração\r\nQue vem do Céu', NULL, 1),
(62, 'Quebrantado', 'Eu olho para a cruz\r\nE para a Cruz eu vou\r\n\r\nDo seu sofrer participar\r\nDa sua obra \r\neu vou cantar\r\n\r\nMeu salvador\r\nNa cruz mostrou\r\nO amor do Pai \r\no Justo Deus\r\n\r\nPela cruz me chamou\r\nGentilmente me atraiu e eu,\r\n\r\nSem palavras me aproximo. Quebrantado por Seu Amor\r\n\r\nImerecida vida, de graça recebi.  Por sua cruz. Da morte me livrou\r\n\r\nTrouxe-me a vida.  Eu estava condenado\r\n\r\nMas agora pela cruz\r\nEu fui reconciliado\r\n\r\nPela cruz me chamou\r\nGentilmente me atraiu\r\n\r\nE eu,\r\nSem palavras \r\nme aproximo\r\n\r\nQuebrantado \r\npor Seu Amor\r\n\r\nImpressionante \r\nÉ o Seu Amor\r\n\r\nMe redimiu \r\ne me mostrou\r\nO quanto é Fiel', NULL, 1),
(63, 'Quem é como o Senhor', 'Os reinos deste mundo\r\nSe prostam e Te adoram\r\nTeu trono está firmado\r\nEm majestade e poder\r\nJesus\r\n\r\nQuem é como o Senhor?\r\nEterno Rei\r\n\r\nAleluia', NULL, 1),
(65, 'Salmos 23', 'O Senhor é o meu pastor\r\nEm Ti sou satisfeito\r\n\r\nLeva-me às águas tranquilas\r\nRefrigera a minha alma\r\n\r\nNo vale escuro da morte\r\nSeguras em minhas mãos\r\n\r\nE na mais alta montanha\r\nAo meu lado sempre estás\r\n\r\nLevas-me para assentar à Tua mesa\r\nCom um banquete cheio de bondade\r\n\r\nLavas-me com o sangue precioso\r\nChamas-me de filho amado\r\n\r\nEu sou tudo para Ti\r\nEu sou tudo para Ti\r\nEu sou tudo Jesus\r\n\r\nTu és tudo para mim\r\nTu és tudo para mim\r\nTu és tudo Jesus', 'Zoe Lily', 1),
(66, 'Santidade ao Senhor', 'Podes me ouvir de longe\r\nVem bem perto\r\nPra falar\r\n\r\nAo meu coração\r\nResponde\r\nSe estou pronto\r\nPra ficar\r\n\r\nLigado em Ti\r\nTeus olhos nos meus\r\nEu volto a respirar\r\n\r\nMe entrego mais\r\nPois só\r\nO Seu Espírito faz\r\n\r\nMeu dia melhor\r\nTu és o motivo\r\nDe eu me sentir\r\nCada vez mais vivo\r\n\r\nTe chamo de Pai\r\nTu és tudo\r\nO que eu preciso\r\n\r\nRei eterno\r\nE meu Deus vivo\r\n\r\nDe águas vivas\r\nÉs a fonte\r\nDos meus sonhos\r\nO maior\r\n\r\nJesus Cristo\r\nÉ a ponte\r\nQue nos fez\r\nComo a um só\r\n\r\nNo santo lugar\r\nPra adorar a Deus\r\nSantidade ao Senhor\r\nVai me levar\r\n\r\nTu és o meu Deus\r\nE eu sou Teu', NULL, 1),
(67, 'Santo', 'Outra canção\r\nQue lhe descreva\r\nEm perfeição\r\n\r\nSanto\r\nDeus poderoso\r\n\r\nNão há outro igual Tú es Santo', NULL, 1),
(68, 'Santo Espírito', 'Não há nada igual\r\nNão há nada melhor\r\n\r\nA que se comparar\r\nEsperança viva\r\nTua presença\r\n\r\nEu provei e vi\r\nO mais doce amor\r\n\r\nQue liberta o meu ser\r\nE a vergonha desfaz\r\nTua presença\r\n\r\nSanto Espírito\r\nÉs bem vindo aqui\r\nVem inundar\r\nEncher este lugar\r\n\r\nÉ o desejo do meu coração\r\nSermos inundados\r\nPor Tua glória Senhor\r\n\r\nVamos provar\r\nO quão real é tua Presença\r\n\r\nVamos provar\r\nDa sua Glória e Bondade', NULL, 1),
(70, 'Só Podia ser Jesus', 'Minha cabeça\r\nRodava\r\nNo peito o coração\r\nDoía\r\n\r\nSentia apodrecer\r\nPor dentro\r\nToda minha alegria\r\n\r\nSem forças\r\nEu caminhava\r\nAs trevas\r\nMe perseguiam\r\n\r\nE o ódio fazendo horror\r\nViver assim\r\nNão podia\r\n\r\nE uma luz então brilhou\r\nDas trevas\r\nMe libertou\r\n\r\nSenti\r\nDo Espírito de Deus\r\nTudo em mim\r\nSe acendeu\r\n\r\nMas só podia\r\nSer Jesus', NULL, 1),
(71, 'Som da Graça', 'Graça\r\nFavor de Deus\r\nQue eu não mereço\r\n\r\nMisericórdia\r\nO castigo merecido\r\nQue Ele não me deu\r\n\r\nE isso que sustenta\r\nTodo meu viver\r\n\r\nAmanheceu\r\nE as misericórdias de Deus\r\nSe renovaram sobre mim\r\n\r\nE essa é a causa\r\nDe eu não ser consumido\r\nComo entender?\r\n\r\nComo entender\r\nQue um Deus maior\r\nQue todo universo\r\n\r\nSe preocupa\r\nCom uma simples\r\nPoeirinha como eu?\r\n\r\nTua fidelidade é além\r\nDe tudo o que eu conheço\r\nQuero você Deus\r\n\r\nNão consigo entender\r\nA graça\r\nNão consigo explicar\r\nA graça\r\n\r\nMas posso sentir\r\nEm meu coração\r\nQue Deus me ama como sou\r\n\r\nTudo vai ficar bem\r\nJesus está comigo eu sei\r\n\r\nTudo vai ficar tão bem\r\nJesus é meu amigo eu sei', NULL, 1),
(72, 'Sou Grato', 'Sou grato por tudo o que tenho\r\n\r\nO tesouro maior deste mundo\r\n\r\nMe foi dado por herança eterna\r\n\r\nMaior prova de um amor tão profundo\r\n\r\nTenho vida, alegria em todo tempo\r\n\r\nTenho amigos, familia, muitos irmãos\r\n\r\nFoi Jesus meu amigo, verdadeiro\r\n\r\nQue fez tudo ao me dar a Salvação\r\n\r\nLouvarei ao Senhor em todo tempo\r\n\r\nSeu Louvor estará continuamente\r\n\r\nEm meus lábios e também no coração\r\n\r\nJesus Cristo será sempre minha canção\r\n\r\nYe Ye Yehovah', NULL, 1),
(73, 'Toda Terra', 'Toda Terra\r\nEstá cheia\r\nDa Sua glória\r\nSanto\r\n\r\nTua Glória\r\nSanto', NULL, 1),
(74, 'Toda Terra está Cheia', 'Santo\r\nToda Terra\r\nEstá cheia\r\n\r\nSanto\r\nDa Sua glória\r\n\r\nSanto\r\nÉ o Senhor', NULL, 1),
(75, 'Tu não desistirás', 'Tu não desistirás até\r\nQue tenhas tudo\r\nMeu coração é Teu\r\n\r\nMe põem como um selo\r\nEm Teu coração\r\nComo um selo\r\nEm Teu braço\r\n\r\nPois o amor\r\nÉ forte como a morte\r\nPaixão cruél\r\nComo um sepulcro\r\n\r\nE as muitas águas\r\nNão podem afogar\r\nEsse amor\r\n\r\nVenha ser o fogo\r\nDentro de mim\r\nVenha ser a chama\r\nEm meu coração\r\n\r\nVenha ser o fogo\r\nDentro de mim\r\nAté que nos tornemos um', NULL, 1),
(76, 'Vai Valer a Pena', 'Não compreendo\r\nOs teus Caminhos\r\n\r\nMas te darei\r\nA minha canção\r\nDoces palavras te darei\r\n\r\nMe sustentas\r\nEm minha dor\r\n\r\nE isso me leva mais\r\nPerto de Ti\r\n\r\nMais perto dos\r\nTeus caminhos\r\n\r\nE ao redor de cada esquina\r\n\r\nEm cima\r\nDe cada montanha\r\n\r\nEu não procuro por coroas\r\nOu pelas águas\r\nDas fontes\r\n\r\nDesesperado\r\nEu Te busco\r\n\r\nFrenético acredito\r\nQue a visão da Tua face\r\nÉ tudo o que eu preciso\r\n\r\nE eu te direi\r\n\r\nQue vai valer a pena\r\nVai valer a pena\r\nVai valer a pena mesmo\r\n\r\nQuando o grande dia chegar\r\nQuando o grande dia chegar e ele vem\r\n\r\nQuando o grande dia chegar\r\n\r\nEu cantarei\r\nEu cantarei\r\nEu cantarei\r\n\r\nSenhor valeu a pena\r\nSenhor valeu a pena\r\nSenhor valeu a pena mesmo', NULL, 1),
(77, 'Vem me encher', 'Tu provéns o Fogo\r\nEu proverei \r\no sacrifício \r\n\r\nTu provéns o Espírito\r\nEu me abrirei a Ti\r\n\r\nVem me encher \r\nó Deus', NULL, 1),
(78, 'Vem me Socorrer', 'Não tenho um tom\r\nNão tenho palavras\r\nNão tenho um acorde\r\nQue me socorra agora\r\n\r\nTudo foi embora\r\nSó tenho Você\r\n\r\nHavia o silêncio\r\nQue mostrou meus vícios\r\nMe agarro contigo\r\nVem me socorra agora\r\n\r\nTudo foi embora\r\nSó tenho Você\r\nAmor agora\r\n\r\nE essa não é mais\r\nUma canção de amor\r\n\r\nEu canto pra ti\r\nSei onde estou\r\nOlhando pra mim\r\nPosso saber que nada sou\r\n\r\nEu grito pra ti\r\nVem me socorrer\r\nOlhando pra mim\r\nPosso saber\r\nQue nada posso fazer', NULL, 1),
(79, 'Vem sobre nós', 'Vem sobre nós\r\nMostra-nos o coração\r\nDo Pai\r\n\r\nOnde minha casa está\r\nOnde eu vou morar\r\nBem perto de Ti\r\nPra sempre e sempre', NULL, 1),
(80, 'Pra Sempre - (Forever)', 'Lua e estrelas choram\r\nO sol se apagou\r\nO salvador do mundo se entregou\r\n\r\nSeu corpo lá na cruz\r\nSeu sangue derramou\r\nToda maldição Ele levou\r\n\r\nUm ultimo suspiro\r\nO céu olhou para longe\r\nO Filho de Deus na escuridão\r\n\r\nNo tumulo a batalha\r\nA guerra contra a morte\r\nO poder do inferno foi quebrado\r\n\r\nO chão estremeceu\r\nSepulcro se abriu\r\nNada pode superar o Seu perfeito Amor\r\n\r\nÓ morte onde estás?\r\nNosso Rei ressuscitou\r\nTe deixou derrotada\r\n\r\nPra sempre é glorificado\r\nPra sempre Ele é exaltado\r\n\r\nPra sempre ressuscitou\r\nE vive, Ele vive\r\n\r\nO chão estremeceu\r\nSepulcro se abriu\r\nNada pode superar o Seu perfeito Amor\r\n\r\nÓ morte onde estás?\r\nNosso Rei ressuscitou\r\nTe deixou derrotada\r\n\r\nPra sempre é glorificado\r\nPra sempre Ele é exaltado\r\n\r\nPra sempre ressuscitou\r\nE vive, Ele vive\r\n\r\nCantamos Aleluia\r\nO Cordeiro venceu', NULL, 1),
(81, 'Quão Grande é o meu Deus', 'Quão Grande é o meu Deus, \r\nCantarei quão Grande é o meu Deus \r\n\r\nE todos hão de ver\r\nQuão Grande é o meu Deus \r\n\r\nCom esplendor de um Rei \r\nE majestade e luz\r\n\r\nFaz a Terra se alegrar\r\nFaz a Terra se alegrar\r\n\r\nEle é a própria luz\r\ne as trevas vão fugir\r\n\r\nTremer com sua voz\r\nTremer com sua voz\r\n\r\nQuão Grande é o meu Deus, \r\nCantarei quão Grande é o meu Deus \r\n\r\nE todos hão de ver\r\nQuão Grande é o meu Deus \r\n\r\nPor gerações Ele é\r\nO tempo está em suas mãos\r\n\r\nO começo e o fim\r\nO começo e o fim\r\n\r\nTrês se formam em um\r\nFilho, Espirito e Pai\r\n\r\nCordeiro e Leão\r\nCordeiro e Leão\r\n\r\nQuão Grande é o meu Deus, \r\nCantarei quão Grande é o meu Deus \r\n\r\nE todos hão de ver\r\nQuão Grande é o meu Deus \r\n\r\nSobre todo nome é o Teu\r\nTu és digno do Louvor\r\n\r\nEu cantarei\r\nQuão grande é o meu Deus\r\n\r\nQuão Grande é o meu Deus,\r\nCantarei quão Grande é o meu Deus \r\n\r\nE todos hão de ver\r\nQuão Grande é o meu Deus', NULL, 1),
(82, 'Fogo de Deus', 'Há muito mais que isso\r\nEspirito Santo, sopra em nós\r\n\r\nHá muito mais que isso\r\nEspirito Santo, esperamos por Ti\r\n\r\nEnche-nos outra vez\r\n\r\nFogo de Deus acende em nós\r\nPaixão pelo Teu nome\r\n\r\nEspirito de Deus\r\nDerrama aqui O Teu poder\r\nO Teu amor em nós\r\n\r\nVento impetuoso enche-nos com o Teu poder\r\nVem libertar os cativos\r\nQueremos viver pra o Teu louvor\r\n\r\nDerrama a Tua glória\r\n\r\nFogo de Deus acende em nós paixão pelo Teu nome\r\n\r\nEspirito de Deus derrama aqui\r\nO Teu poder. O Teu amor em nós\r\n\r\nVem em acende em nós Senhor\r\nVem em acende em nós paixão pelo Teu nome\r\n', NULL, 1),
(83, 'Espirito Santo de Deus', 'Espirito Santo de Deus\r\nVem sobre nós\r\nMostra-nos o coração do Pai\r\n\r\nOnde eu vou morar\r\nOnde a minha casa está\r\n\r\nOnde eu vou morar\r\nBem perto de Ti\r\nPra sempre e Sempre\r\n\r\n', 'Rodolfo Abrantes', 1),
(84, 'Meu coração Queima por Ti', 'Meu coração \r\nQueima por Ti\r\n\r\nE pra sempre \r\nMe Incendei \r\nCom o Fogo do Teu Amor', NULL, 1),
(85, 'Deus me Ama', 'Mesmo sendo assim, pobre pecador Deus me ama\r\n\r\nMesmo sendo falho, mesmo sem merecer Deus me ama\r\n\r\nSe eu estou forte, se eu estou de pé Deus me ama\r\n\r\nSe eu estou fraco, se eu estou caído\r\nEle não deixa de me amar\r\n\r\nSem o seu amor, sem o seu perdão,\r\n o que seria de mim\r\n\r\nDeus me amou tanto que entregou seu filho\r\nPara morrer em meu lugar\r\n\r\nDeus me ama, e o seu amor é tão grande incondicional\r\n\r\nDeus me ama, e Ele está sempre de braços abertos pra mim\r\n\r\nSem o seu amor, sem o seu perdão,\r\n o que seria de mim\r\n\r\nDeus me amou tanto que entregou seu filho\r\nPara morrer em meu lugar\r\n\r\nDeus me ama, e o seu amor é tão grande incondicional\r\n\r\nDeus me ama, e Ele está sempre de braços abertos pra mim', NULL, 1),
(86, 'Tu És Bom', 'Lindo Jesus\r\nRei do meu coração\r\nComo eu amo os Teus caminhos\r\n\r\nLindo Jesus\r\nEstrela da manhã\r\nEu cantarei a Ti\r\n\r\nO que posso Te dar\r\nPor tudo que Tu és\r\nO Senhor me libertou\r\nE me curou\r\n\r\nTu és bom, em todo tempo és bom\r\nJustiça está em Ti\r\nTeu amor é pra sempre\r\n\r\nTu és bom, em todo tempo és bom\r\nJustiça está em Ti\r\nTeu amor é pra sempre\r\n\r\nO Teu amor é pra sempre', 'Jason Lee Jones e Fred Arrais', 1),
(87, 'Sorte de Bençãos', 'Por onde eu for a Tua benção\r\nMe seguirá\r\n\r\nOnde eu colocar as minhas mãos\r\nProsperá\r\n\r\nA minha entrada e a minha saída\r\nBendita será\r\n\r\nPois sobre mim\r\nHá uma promessa\r\n\r\nProsperarei\r\nTransbordarei\r\n\r\nOs meus celeiros fartamente se encherão\r\n\r\nA minha casa tera sempre, tua provisão\r\n\r\nOnde eu puser as plantas dos meus pés\r\nPossuirei\r\n\r\nPois sobre mim há uma promessa\r\n\r\nPara a direita\r\nPara a esquerda\r\n\r\nA minha frente\r\nE para trás\r\n\r\nToda sorte, de bençãos o Senhor preparou pra mim\r\n\r\nE em todas, as coisas, eu sou mais do que vencedor\r\n\r\nPor todo lado\r\nSou abençoado\r\n\r\nEm tudo o que eu faço\r\nSou abençoado', NULL, 1),
(88, 'Oh Minh''Alma', 'Aleluia\r\nEle me amou primeiro\r\nConquistou meu coração\r\n\r\nAleluia\r\nEu sou do meu amado\r\nE o meu amado é meu\r\n\r\nOh minh''alma\r\nLouve ao Senhor\r\n\r\nEle é bom pra sempre\r\nOh minh''alma\r\nLouve ao Senhor', NULL, 1),
(89, 'Everything', 'Everything\r\nYour love is my everything\r\n(Tudo\r\nSeu amor é tudo)\r\n\r\nI don''t have nothing more\r\nBeyond your love Jesus\r\n(Eu não tenho nada mais\r\nAlem do seu amor)\r\n\r\nAll my days\r\nI wanna live all my days\r\n(Os meus dias\r\nQuero viver os meus dias)\r\n\r\nTo worship your might name\r\nCause I was made for glory\r\n(Pra adorar o seu nome\r\nPois eu fui feito pra glória)\r\n\r\nI could not know\r\nSomeone like You\r\n(Eu não poderia\r\nConhecer alguem assim)\r\n\r\nI would never find\r\nBut You found me\r\n(Nunca encontraria\r\nMas você me encontrou)\r\n\r\nOh God You are\r\nSo much better than I tought\r\n(Deus você é\r\nMuito melhor do que eu pensei)\r\n\r\nI love You\r\n(Te amo)', NULL, 1),
(90, 'Grandes Coisas', 'Tu És o Deus desta Terra\r\nTu És o Rei deste povo\r\nÉs o Senhor da nação\r\nTu És \r\n\r\nTu És a luz deste mundo\r\nEsperança para os perdidos\r\nTu És a paz para os cansados\r\nTu És\r\n\r\nNinguém é como o nosso Deus\r\n\r\nGrandes coisas estão por vir\r\nGrandes coisas vão acontecer neste lugar \r\n\r\nGrandes coisas estão por vir \r\nGrandes coisas vão acontecer aqui', 'Fernandinho', 1),
(91, 'Todas as coisas', 'Eu sei que sempre estás comigo Senhor\r\nTambem sei que nada acontece sem a tua vontade\r\n\r\nMas preciso aprender a confiar em Ti\r\nMas preciso aprender a descansar em Ti\r\n\r\nTu és meu Senhor\r\n\r\nTodas as coisas cooperam para o bem\r\nDaqueles que te amam', 'Fernandinho', 1),
(92, 'Reina em mim', 'Sobre toda a terra\r\nTu és o Rei\r\n\r\nSobre as montanhas\r\ne o pôr-do-sol\r\n\r\nUma coisa só meu desejo é\r\nVem reinar de novo em mim\r\n\r\nReina em mim com Teu poder\r\n\r\nSobre a escuridão\r\nSobre os sonhos meus\r\n\r\nTú és o Senhor de tudo o que sou\r\n\r\nVem reinar em mim Senhor\r\n\r\nSobre o meu pensar\r\nTudo que eu falar\r\n\r\nFaz-me refletir a beleza que há em Ti\r\n\r\nTu és para mim mais que tudo aqui\r\nVem reinar de novo em mim\r\n\r\nReina em mim com Teu poder\r\n\r\nSobre a escuridão\r\nSobre os sonhos meus\r\n\r\nTú és o Senhor de tudo o que sou\r\n\r\nVem reinar em mim Senhor', NULL, 1),
(93, 'Genesis', 'Você me pergunta quem sou\r\nOnde estou\r\n\r\nPara onde vou\r\nSou nascido de Deus \r\n\r\nEstou no centro de sua vontade\r\nO vento sopra pra onde quer...\r\n\r\nAssim sou eu, sou eu\r\nAssim sou eu, sou eu\r\n\r\nEm muitas coisas você acreditou\r\nViveu no mundo de ilusões\r\n\r\nDepositou tua fé em homens\r\nQue não podem responder tuas orações\r\n\r\nAcredite o Sol, a Terra e o Mar\r\nForam feitos pra você, pra você\r\nPra você, pra você\r\n\r\nVem sentir o Poder\r\nVem conhecer os segredos\r\nDe quem te projetou com amor\r\n\r\nCorpo, Alma e Espírito\r\nElo de vida com o criador\r\n\r\nVocê é fruto do sonho de Deus\r\nDe Deus, de Deus, de Deus\r\n\r\nVocê é fruto do sonho de Deus\r\nDe Deus, de Deus, de Deus', NULL, 1),
(95, 'Poderoso Deus', 'Ao que está assentado\r\nNo trono\r\nE ao cordeiro\r\nSeja o louvor\r\n\r\nSeja a honra\r\nSeja a glória\r\nSeja o domínio \r\nPelo séculos dos séculos\r\n\r\nPoderoso Deus\r\nPoderoso Deus\r\nPoderoso Deus\r\nMinh''alma anseia por Ti', NULL, 1),
(96, 'O Senhor é a luz do meu caminho', 'O Senhor é a luz\r\nDo meu caminho\r\nO Senhor é a força\r\nQue salva o seu ungido\r\n\r\nO Senhor\r\nQue guia os meus passos\r\nA ele me entreguei\r\nE Nele confiarei\r\n\r\nÉ Ele quem me abençoa\r\nE me guarda\r\nE sobre mim\r\nResplandece o seu rosto\r\n\r\nPois é Dele\r\nQue vem a minha paz\r\nA Ele me entreguei\r\nE Nele confiarei\r\n\r\nNão temo o mal que possa vir\r\nForça maior pra destruir\r\nAmor e fé no coração\r\nQue do Senhor recebi\r\n\r\nPois é Dele\r\nQue vem a minha força\r\nA Ele me entreguei\r\nE Nele confiarei', NULL, 1),
(97, 'Digno é o Senhor', 'Eu não canto para \r\num Deus que é \r\ndistante, sem glória\r\n\r\nEu canto a quem \r\nme libertou e hoje \r\nhabita em mim\r\n\r\nEu não canto para\r\num Deus que é \r\nsomente uma história\r\n\r\nEu canto a quem\r\nressuscitou e hoje\r\nestá aqui\r\n\r\nDigno é o Senhor\r\nDigno é o Senhor\r\nDigno é o Senhor\r\n\r\nDe Glória em Glória', NULL, 1),
(98, 'Ha um lugar', 'Há um lugar de descanso em Ti\r\nHá um lugar de refrigerio em Ti\r\n\r\nHá um lugar onde a verdade reina\r\nEsse lugar é no Senhor\r\n\r\nHá um lugar onde as pessoas não me influenciam\r\nHá um lugar onde eu ouço Teu Espirito\r\n\r\nHá um lugar de vitória em meio a guerra. \r\nEsse lugar é no Senhor. Esse lugar é no Senhor\r\n\r\nHá um lugar onde a inconstância não me domina\r\nHá um lugar onde minha Fé é fortalecida\r\n\r\nHá um lugar onde a paz é quem governa\r\nEsse lugar é no Senhor\r\n\r\nHá um lugar onde os sonhos não se abortam\r\n\r\nHá um lugar onde o temor não me enriquece\r\n\r\nHá um lugar que quando se perde é quem se ganha\r\nEsse lugar é no Senhor\r\n\r\nEsse Lugar é no Senhor', NULL, 1),
(99, 'Há um Lugar', 'Há um lugar de descanço em Ti\r\nHá um lugar de refrigerio em Ti\r\nHá um lugar onde a verdade reina\r\nEsse lugar é no Senhor\r\n\r\nHá um lugar onde as pessoas não\r\nme influenciam\r\nHá um lugar onde eu ouço\r\nTeu Espirito\r\n\r\nHá um lugar de vitória \r\nem meio a guerra\r\nEsse lugar é no Senhor\r\n\r\nEsse lugar é no Senhor\r\n\r\nHá um lugar onde a \r\ninconstância não me dominam\r\nHá um lugar onde minha Fé\r\né fortalecida\r\nHá um lugar onde a paz é quem\r\ngoverna\r\nesse lugar é no Senhor\r\n\r\nHá um lugar onde os sonhos\r\nnão se abortam\r\nHá um lugar onde o temor\r\nnão me enriquece\r\n\r\nHá um lugar que quando se perde\r\né que se ganha\r\nEsse lugar é no Senhor\r\n\r\nEsse lugar é no Senhor', NULL, 1),
(100, 'Grande é o Senhor', 'Grande é o Senhor e mui digno de louvor\r\n\r\nNa cidade do nosso Deus Teu Santo monte\r\n\r\nAlegria de toda a Terra\r\n\r\nGrande é o Senhor em quem nós temos a vitória\r\n\r\nQue nos ajuda contra o inimigo\r\n\r\nPor isso diante dEle nos prostramos\r\n\r\nQueremos o Teu nome engrandecer\r\n\r\nE agradecer-te por tua obra em nossas vidas\r\n\r\nConfiamos em Teu inifinito amor\r\n\r\nPois só Tú és o Deus Eterno\r\nSobre toda Terra e Céu', NULL, 1),
(102, 'A Alegria está no coração', 'A alegria está no coração, \r\nde quem já conhece a Jesus\r\n\r\nA verdadeira paz só tem aquele, \r\nque já conhece a Jesus.\r\n\r\nO sentimento mais precioso \r\nque vem do nosso Senhor\r\n\r\nÉ o amor que só tem, \r\nquem já conhece a Jesus.\r\n\r\nPosso pisar as tropas\r\ne saltar as muralhas\r\naleluia, aleluia.\r\n\r\nCristo é a rocha da minha salvação,\r\ncom ele não há mais condenação\r\n\r\nPosso pisar as tropas\r\ne saltar as muralhas,\r\nAleluia, aleluia.\r\n\r\nAleluia, aleluia.', NULL, 1),
(103, 'A cada manhã', 'A cada manhã as misericórdias se renovam\r\nA cada manhã as misericórdias se renovam\r\nA cada manhã\r\nA cada manhã\r\n\r\nQuando as minhas forças se acabam\r\nE a minha esperança no Senhor\r\nParecem não re-sis-tir\r\nMinh''alma se abate dentro de mim\r\n\r\nMeu coração começa a se lembrar\r\nDaquilo que renova a esperança\r\nAs misericórdias do Senhor\r\nNão têm fim, Grande é o seu amor\r\n\r\nA cada manhã as misericórdias se renovam\r\nA cada manhã as misericórdias se renovam\r\nA cada manhã\r\nA cada manhã\r\n\r\nMinha porção é o Senhor\r\nA minha força é sua a-le-gri-a\r\nEle é a razão do meu louvor\r\nInspiração da minha melodia\r\nEspero nEle a cada novo dia\r\n\r\nA cada manhã as misericórdias se renovam\r\nA cada manhã as misericórdias se renovam\r\nA cada manhã\r\nA cada manhã', NULL, 1),
(104, 'A unção de Deus', 'A unção de Deus está sobre mim\r\nMe ungiu posso profetizar\r\n\r\nEnfermidades vão embora\r\nQuando ordeno sai na hora\r\n\r\nPor que a unção de Deus está sobre mim\r\n\r\nAo levantar as minhas mãos o inimigo vem ao chão\r\nPorque estou debaixo da unção\r\n\r\nA unção de Deus está sobre mim\r\nMe ungiu agora posso ordenar\r\n\r\nE as barreiras cai por terra, \r\no mal não pode resistir\r\n\r\nPois a unção de Deus está sobre mim\r\n\r\nPosso saltar as muralhas\r\nPosso tomar Jerico\r\n\r\nPosso debaixo da unção gritar \r\ne a fortaleza se tornarem pó\r\n\r\nDebaixo da unção posso ordenar\r\nenfermidades vão ter que sair\r\n\r\nTodos os problemas \r\ne angustias Deus vai destruir\r\n\r\nToda barreira cai por terra \r\nnão vai resistir\r\n\r\nDebaixo da unção posso abrir \r\nmuitas prisões só com o meu louvor\r\n\r\nPosso tirar milhões do abismo \r\nem nome do Senhor\r\n\r\nA unção de Deus está sobre mim\r\n\r\nA unção de Deus está sobre mim\r\nMe ungiu e agora posso ordenar\r\n\r\nE as barreiras cai por terra \r\no mal não pode resistir\r\n\r\nPois a unção de Deus está sobre mim\r\n\r\nPosso saltar as muralhas\r\nPosso tomar Jerico\r\n\r\nPosso debaixo da unção gritar \r\ne a fortaleza se tornarem pó\r\n\r\nDebaixo da unção posso ordenar \r\nenfermidades vão ter que sair\r\n\r\nTodos os problemas \r\ne angustias Deus vai destruir\r\n\r\nToda barreira cai por terra \r\nnão vai resistir\r\n\r\nDebaixo da unção posso abrir \r\nmuitas prisões só com o meu louvor\r\n\r\nPosso tirar milhões do abismo \r\nem nome do Senhor\r\n\r\nA unção de Deus está sobre mim\r\n\r\nEnfermidades vão embora,\r\n eu te ordeno sai agora\r\n\r\nA unção de Deus está sobre mim\r\n\r\nAo levantar as minhas mãos \r\no inimigo vem ao chão\r\n\r\nA unção de Deus está sobre mim\r\n\r\nCom poder e autoridade agora \r\neu ordeno o inimigo sai na hora\r\n\r\nA unção de Deus está sobre mim', 'Gerd', 1),
(105, 'Aclame ao Senhor', 'Meu Jesus, Salvador\r\nOutro igual não há\r\n\r\nTodos os dias quero louvar\r\nAs maravilhas de teu amor\r\n\r\nConsolo, abrigo\r\nForça e refúgio é o Senhor\r\n\r\nCom todo o meu ser\r\nCom tudo o que sou\r\nSempre te adorarei\r\n\r\nAclame ao Senhor toda \r\na terra e cantemos\r\n\r\nPoder, majestade \r\ne louvores ao Rei\r\n\r\nMontanhas se prostrem \r\ne rujam os mares\r\n\r\nAo som de teu nome\r\n\r\nAlegre te louvo por \r\nteus grandes feitos\r\n\r\nFirmado estarei, \r\nsempre te amarei\r\n\r\nIncomparáveis são \r\ntuas promessas \r\npra mim', 'Diante do Trono', 1),
(106, 'Ainda que a figueira', 'Tu és a minha porção\r\nTu és a minha herança\r\n\r\nTu és o meu socorro\r\nNos dias de tribulação\r\n\r\nMesmo que meus pais me deixem\r\nMesmo que amigos me traiam\r\n\r\nEu sei que em Seus braços\r\nEu encontro salvação\r\n\r\nAinda que a figueira não floresça\r\nAinda que a videira não dê o seu fruto\r\n\r\nMesmo que não haja \r\nalimento nos campos\r\nEu me alegrarei em Ti', 'Fernandinho', 1),
(107, 'Ao erguermos as mãos', 'Ao erguermos as \r\nmãos pra adorar\r\n\r\nE clamarmos a ti, \r\ntu viras.\r\n\r\nNós viemos aqui, \r\nte adorar\r\n\r\nDeus de graça e favor\r\n\r\nÉs tu meu senhor\r\nDigno és do meu louvor \r\nsó tu és e todos dirão \r\nQue és tu, sim és tu\r\n\r\nSanto, Santo é o \r\nSenhor poderoso\r\nSanto, somente \r\no seu nome é\r\n\r\nAo erguermos as \r\nmãos pra adorar\r\n\r\nE clamarmos a ti, \r\ntu viras.\r\n\r\nNós viemos aqui, \r\nte adorar\r\n\r\nDeus de graça e favor\r\n\r\nÉs tu meu senhor\r\nDigno és do meu louvor \r\nsó tu és e todos dirão \r\nQue és tu, sim és tu\r\n\r\nSanto, Santo é o \r\nSenhor poderoso\r\nSanto, somente \r\no seu nome é\r\n\r\n\r\nAo erguermos as mãos \r\ne clamarmos a ti\r\n\r\nTu virás, até nós\r\nPor tua graça e favor', 'Aline Barros', 1),
(108, 'Bate na minha mão', 'Bate na minha mão,\r\nBate na minha mão,\r\nBate na minha mão,\r\n\r\nSe você tem Jesus \r\nNo seu coração\r\n\r\nDiga Amém,\r\nDiga Amém,\r\nDiga Amém,\r\n\r\nSe você vai morar\r\nLá no céu também\r\n\r\nAbrace o seu irmão,\r\nAbrace o seu irmão,\r\nAbrace o seu irmão,\r\n\r\nE diga pra ele\r\nQue Deus é bom!', 'Maurão ', 1),
(109, 'Bem mais que tudo', 'Bem mais que \r\nas forças\r\nPoder e reis\r\n\r\nQue a natureza \r\ne tudo que se fez\r\n\r\nBem mais que tudo, \r\ncriado por tuas mãos\r\n\r\nDeus, tu és o início, \r\nmeio e fim\r\n\r\nBem mais que os mares\r\nBem mais que o sol\r\n\r\nE as maravilhas que \r\no mundo conheceu\r\n\r\nE as riquezas e \r\ntesouros desta Terra\r\n\r\nIncomparável és pra mim\r\n\r\nPor amor, sua vida entregou\r\nMeu senhor, humilhado foi\r\n\r\nComo a flor machucada no jardim\r\nMorreu por mim, pensou em mim\r\nMe amou', 'Aline Barros', 1),
(110, 'Bendito serás', 'Não, este não pode ser teu fim\r\nNão é o que Deus sonhou pra ti\r\n\r\nNão podes aceitar\r\n\r\nVer tua família se acabar\r\nTua esperança se apagar\r\n\r\nTua fé esmoreceu\r\n\r\nSei que é difícil crer que sim\r\nSe tudo em volta diz que não\r\n\r\nE a angústia te faz esquecer\r\n\r\nDe tudo o que Deus prometeu\r\nQue sobre a tua vida estão\r\n\r\nAs mesmas bençãos de Abraão\r\n\r\nBendita será tua casa\r\nBenditos serão teus filhos\r\n\r\nE Deus engrandecerá teu nome', 'Toque no Altar', 1),
(111, 'Celebre', 'Alegra teu coração\r\nContigo está o teu Deus\r\n\r\nCom seu amor renovará \r\nas tuas forças\r\n\r\nEle anulou tua culpa\r\nTeu inimigo já derrotou\r\n\r\nE no lugar da vergonha \r\nte cobrirá de honra\r\n\r\nCante alegremente filha de Sião\r\nPois Ele mudará tua sorte\r\n\r\nCom brados de vitória\r\nSaltos de alegria diante do Senhor\r\n\r\nCelebre,\r\nCelebre.', 'Trazendo a arca', 1),
(112, 'Com amor eterno', 'Mesmo que as vezes tenhas \r\nque passar por aflições, \r\n\r\nSaibas que eu venci o mundo\r\ne assim como eu \r\nte fiz vencedor.\r\n\r\nSe no vale tu passares \r\njunto a ti eu estarei\r\n\r\nE se no deserto andares, \r\ntua sombra eu serei\r\n\r\nSeja no calor do dia \r\nou se a noite fria está\r\n\r\nNão temas filho meu, \r\nminha mão te guardará\r\n\r\nCom amor eterno eu te amei, \r\nmeu poder eterno, \r\neu te entreguei\r\nTe dei a minha vida\r\n\r\nCom benignidade eu te atraí\r\nCom meu próprio sangue, \r\neu te remi\r\nChamei te pelo nome\r\n\r\nTu és meu', 'Ministério Koinonya de Louvor', 1);
INSERT INTO `songs` (`id`, `title`, `lyric`, `Author`, `statusSongs`) VALUES
(113, 'Comigo estás', 'Sei que um dia saberei\r\nTudo o que tens pra mim\r\n\r\nFace a face te verei\r\nRodeado de poder\r\n\r\nTodo o meu temor se vai\r\nCom a luz do Teu amor\r\n\r\nOnde posso encontrar\r\nTua eterna paz Senhor\r\n\r\nOnde flui adoração\r\nE as ruas de ouro são\r\n\r\nLevantamos nossa voz a Ti Senhor\r\n\r\nSem pranto, \r\nAngústia e dor\r\nSem morte\r\n\r\nComigo estás\r\nComigo estás\r\n\r\nSem noite, \r\nSem aflição,\r\nSem culpa\r\n\r\nComigo estás\r\nComigo estás\r\n\r\nNesta vida passarei\r\nPor tristezas e dor\r\n\r\nEsperando ver chegar\r\nO grande dia do Senhor\r\n\r\nQuando todos vão cantar\r\nEm sublime adoração\r\n\r\nPra Teu nome exaltar\r\nSei que o dia chegará\r\n\r\nOnde reina eterna paz\r\nNão há guerras nem temor\r\n\r\nLevantamos nossa voz a Ti Senhor\r\n\r\nSem pranto, \r\nAngústia e dor\r\nSem morte\r\n\r\nComigo estás\r\nComigo estás\r\n\r\nSem noite, \r\nSem aflição,\r\nSem culpa\r\n\r\nComigo estás\r\nComigo estás\r\n\r\nO meu coração\r\nSempre dará\r\nToda glória \r\na Ti Senhor\r\n\r\nSem pranto, \r\nAngústia e dor\r\nSem morte\r\n\r\nComigo estás\r\nComigo estás\r\n\r\nSem noite, \r\nSem aflição,\r\nSem culpa\r\n\r\nComigo estás\r\nComigo estás', 'Comunidade Evangélica de Maringá', 1),
(114, 'Como é bom', 'Só o Senhor daria\r\nSua vida pra me salvar\r\n\r\nNenhum outro Deus\r\nMorreria em meu lugar\r\n\r\nCom Sua graça e Seu amor\r\nLivrou-me dos braços do pecado\r\n\r\nFez de mim herdeiro\r\nDas bençãos de Deus\r\n\r\n(Herdeiro de Deus)\r\n\r\nComo é bom Te louvar\r\nComo é bom Te adorar\r\n\r\nE dizer que és Senhor\r\nSobre toda a Terra\r\n\r\n\r\nToda Terra te louva Senhor\r\nPor Teus feitos e Teu grande amor\r\n\r\nPois grande és Senhor\r\nGrande és Senhor,\r\nGrande és Senhor,\r\nGrande és Senhor!', 'Paulo César Baruk', 1),
(115, 'Corpo e Família', 'Recebi um novo coração do Pai\r\nCoração regenerado, \r\n\r\nCoração transformado,\r\nCoração que é inspirado por Jesus\r\n\r\nComo fruto desse novo coração\r\nEu declaro a paz de Cristo, \r\nTe abençôo, meu irmão,\r\nPreciosa é a nossa comunhão.\r\n\r\nSomos corpos \r\ne assim bem ajustados\r\n\r\nTotalmente ligados, \r\nunidos, vivendo em amor.\r\n\r\nUma família \r\nsem qualquer falsidade\r\n\r\nVivendo a verdade, \r\nexpressando a glória do Senhor.\r\n\r\nUma família, \r\nvivendo o compromisso\r\nDo grande amor de Cristo. \r\n\r\nEu preciso de ti querido irmão. \r\nPrecioso és para mim.\r\nQuerido irmão.', 'Diante do Trono', 1),
(116, 'Dançar na chuva', 'Novo caminho para andar,\r\nNovo sonho pra sonhar,\r\n\r\nO deserto não é o meu lugar.\r\nNova vida Ele prometeu,\r\n\r\nA todo aquele que Nele crê,\r\nO mar a nossa frente se abrirá.\r\n\r\nNova criatura sou,\r\nO velho homem já morreu,\r\n\r\nEu vou dançar naquela terra\r\nQue Ele prometeu\r\n\r\nEu vou,\r\nEu vou dançar na chuva.\r\n\r\nDance, dance, dance\r\nDance, dance, dance\r\n\r\nNova criatura eu sou\r\nO velho homem já morreu,\r\n\r\nVou dançar naquela terra,\r\nQue Ele prometeu\r\n\r\nEu vou,\r\nEu vou dançar na chuva.', 'Fernandinho', 1),
(117, 'Declaramos', 'Declaramos que aqui \r\nreina a Glória do Senhor\r\n\r\nSua graça é manifesta, \r\nestá presente o seu amor\r\n\r\nCorações se quebrarão, \r\nalmas vão se derramar\r\n\r\nE o Espírito de Deus reinará\r\n\r\nProfetizamos salvação, \r\ntotal libertação\r\n\r\nQuebramos as cadeias \r\nem nome de Jesus\r\n\r\nAqui não há lugar pro mal, \r\naqui reina a luz\r\n\r\nEntronizamos Cristo, \r\nEle é o nosso Rei\r\n\r\nNos rendemos aos teus pés, \r\npois sabemos que tu és\r\nPoderoso para transformar o mundo\r\n\r\nNada pode impedir teu Espírito de agir\r\nOperando maravilhas, \r\nTransformando corações.', 'Frutos do Espírito', 1),
(118, 'Deixa o Espírito operar', 'Eu sinto que o Espírito esta aqui \r\nE ele quer seu povo renovar \r\n\r\nE se uma lagrima descer \r\nDeixa o Espírito operar\r\n\r\nOnde há o Espírito de Deus \r\nHá liberdade pra sentir \r\n\r\nHa liberdade pra louvar \r\nCom ele transportamos as montanhas \r\n\r\nAs alegrias sao tamanhas \r\nFaz o caido levantar \r\n\r\nDeixa o Espirito operar\r\n\r\nEu sinto que a sua mao esta aqui \r\nO braço forte de Deus jeova \r\n\r\nE se o ceu aqui descer \r\nDeixa o espirito operar', 'Gerd', 1),
(119, 'Dê-nos mãos limpas', 'Dobro os joelhos e o coração\r\nEspírito nos quebranta\r\n\r\nNos afastamos de todo o mal\r\nDeixamos a iniqüidade\r\n\r\nDê-nos mãos limpas, \r\ne um puro coração \r\n\r\nNão nos deixe \r\ncair em vaidade\r\n\r\nOh! Deixe-nos ser a geração que Te busca \r\nQue busca Tua face, ó Deus de Jacó\r\n\r\nBuscamos Tua face, \r\nó Deus de Jacó', 'Filhos do Homem', 1),
(120, 'Deus da minha vida', 'Deus meu,\r\nPai meu, \r\nAmor meu\r\nTudo, razão de tudo\r\n\r\nDeus meu,\r\nAr meu,\r\nFarol, o farol que eu preciso\r\nComo eu preciso\r\n\r\nEu preciso te sentir todo dia\r\nE olhar pra tua luz pra não me perder\r\n\r\nMeu Senhor tu és a minha alegria\r\nEu preciso...\r\n\r\nDeus da minha vida, fica comigo\r\nSou a sua casa, mora em mim\r\n\r\nDeixa eu te dizer, o que eu preciso pai\r\nEu preciso do Senhor', 'Thalles Roberto', 1),
(121, 'Deus de milagres', 'Quem é este que \r\nfaz o mar se acalmar\r\nE a tempestade \r\nobedece a sua voz?\r\n\r\nQuem é este que \r\nanda sobre as águas\r\nE por onde passa \r\ntudo pode transformar?\r\n\r\nQuem é este que \r\ntodas as coisas pode ver\r\nE o coração do homem\r\npode sondar?\r\n\r\nQuem é este que \r\ncura as doenças\r\nE o corpo e alma \r\npode restaurar?\r\n\r\nO seu nome é Jesus\r\nO filho de Deus\r\nMorreu na cruz em meu lugar\r\n\r\nMas a morte venceu e ressuscitou\r\nIgual ao meu Deus não há\r\n\r\nO meu Deus é um Deus de milagres\r\nNão há limites para o Seu poder agir\r\n\r\nRealiza o impossível\r\nPara Ele nada é tão difícil\r\n\r\nE o maior milagre já operou em mim\r\nE o maior milagre quer operar em ti\r\n\r\nQuem é este que \r\nfecha a boca dos leões\r\nE na fornalha ardente \r\npode passear?\r\n\r\nQuem é esse que \r\nressuscita os mortos\r\nE uma nova vida faz \r\no homem experimentar?\r\n\r\nQuem é este que \r\nabre as portas das prisões\r\nE em liberdade faz \r\nseus servos caminhar?\r\n\r\nQuem é este que \r\ndiz: "Venho sem demora\r\nPra minha amada \r\nnoiva buscar"?\r\n\r\nO seu nome é Jesus\r\nO filho de Deus\r\nMorreu na cruz em meu lugar\r\n\r\nMas a morte venceu e ressuscitou\r\nIgual ao meu Deus não há\r\n\r\nO meu Deus é um Deus de milagres\r\nNão há limites para o Seu poder agir\r\n\r\nRealiza o impossível\r\nPara Ele nada é tão difícil\r\n\r\nE o maior milagre já operou em mim\r\nE o maior milagre quer operar em ti', 'Diante do Trono', 1),
(122, 'Diante da cruz', 'Ó Deus, me sondas, \r\nconheces-me\r\n\r\nMesmo quando falho, \r\neu sei, me amas\r\n\r\nEstás presente \r\na me cercar\r\n\r\nEm todo tempo, \r\nEu sei, me amas\r\nEu sei, me amas\r\n\r\nProstro-me diante da cruz\r\nVejo o sangue de Jesus\r\nNunca houve amor assim\r\n\r\nSobre a morte já venceu\r\nSua glória o céu encheu\r\nNada irá me separar\r\n\r\nTu és meu Guia, \r\nmeu Protetor\r\n\r\nTua mão me firma\r\nEu sei, me amas\r\nEu sei, me amas\r\n\r\nProstro-me diante da cruz\r\nVejo o sangue de Jesus\r\nNunca houve amor assim\r\n\r\nSobre a morte já venceu\r\nSua glória o céu encheu\r\nNada irá me separar\r\n\r\nProstro-me diante da cruz\r\nVejo o sangue de Jesus\r\nNunca houve amor assim\r\n\r\nSobre a morte já venceu\r\nSua glória o céu encheu\r\nNada irá me separar\r\n\r\nO véu rasgou, \r\no caminho abriu\r\nTudo consumado está\r\n\r\nO véu rasgou, \r\no caminho abriu\r\nTudo consumado está\r\n\r\nE quando tudo, \r\nse acabar, \r\nestarei seguro\r\n\r\nPois sei, me amas\r\nPois sei, me amas\r\n\r\nProstro-me diante da cruz\r\nVejo o sangue de Jesus\r\nNunca houve amor assim\r\n\r\nSobre a morte já venceu\r\nSua glória o céu encheu\r\nNada irá me separar\r\n\r\nProstro-me diante da cruz\r\nVejo o sangue de Jesus\r\nNunca houve amor assim\r\n\r\nSobre a morte já venceu\r\nSua glória o céu encheu\r\nNada irá me separar\r\n\r\nProstro-me diante da cruz\r\nVejo o sangue de Jesus\r\nNunca houve amor assim\r\n\r\nSobre a morte já venceu\r\nSua glória o céu encheu\r\nNada irá me separar', 'Aline Barros', 1),
(123, 'Dias de Elias', 'Estes são dias de elias, \r\nPregando a palavra de Deus. \r\n\r\nE estes são dias do servo, Moisés, \r\nJustiça reinando outra vez. \r\n\r\nSão dias de tribulações, \r\nDe trevas e perseguições. \r\n\r\nMas somos a voz no deserto que clama: \r\nPrepare o caminho do senhor\r\n\r\nEle vem, \r\nA trombeta soará! \r\n\r\nClaro como o sol, \r\nNas nuvens voltará. \r\n\r\nDeclare alto, \r\nEste é o ano do perdão, \r\n\r\nDe sião virá a salvação.\r\n\r\nEstes são dias de Ezequiel, \r\nNo vale dos ossos secos. \r\n\r\nE estes são dias do servo Davi, \r\nRestaurando a adoração.\r\n \r\nSão dias de grande colheita, \r\nOs campos brancos estão. \r\n\r\nNós somos os trabalhadores na vinha, \r\nPregando a salvação.', 'Paul Wilbur', 1),
(124, 'Dívida de amor', 'Sou feliz\r\nNão ando na \r\nescuridão da solidão\r\n\r\nNão vou chorar \r\nde tristeza\r\n\r\nSou feliz\r\nA Tua luz encheu \r\nmeu coração\r\n\r\nVou me alegrar\r\nPosso até chorar \r\nde gratidão\r\n\r\nComo posso expressar\r\nTão grande amor por mim?\r\n\r\nComo posso pagar \r\nessa dívida de amor?\r\n\r\nSó posso entregar\r\nMinha vida em Teu altar', 'André Valadão', 1),
(125, 'Em Espírito e em verdade', 'Em espírito, \r\nEm verdade\r\nTe adoramos.\r\n\r\nRei dos Reis \r\ne Senhor\r\nTe entregamos \r\no nosso viver\r\n\r\nPra Te adorar \r\nÓ Rei dos Reis!\r\nFoi que eu nasci \r\nÓ Rei Jesus!\r\n\r\nMeu prazer é Te louvar\r\nMeu prazer é estar\r\nNos átrios do senhor\r\n\r\nMeu prazer é viver \r\nna casa de Deus\r\nOnde flui o amor', 'Rosa de Saron', 1),
(126, 'Estou só', 'Estou só, \r\nPosso ouvir \r\na Tua voz\r\n\r\nMeu coração é Teu, \r\nQuero em teus braços \r\ndescansar\r\n\r\nSou teu filho, \r\ne é bom estar contigo \r\ncomo um filho\r\n\r\nQue sente a presença \r\ndo pai ao seu redor.\r\n\r\nVou olhar em teus olhos, \r\nvou jogar-me em teus braços\r\nComo criança me gira no ar\r\n\r\nEste é o instante que eu espero\r\nEste é o momento que eu mais quero\r\nA hora de te encontrar.', 'Filhos do Homem', 1),
(127, 'Eu Navegarei', 'Eu navegarei \r\nNo oceano do Espirito\r\n\r\nE ali adorarei\r\nAo Deus do meu amor\r\n\r\nEspirito, Espirito\r\nQue desce como fogo\r\n\r\nVem como em pentecostes\r\nE enche-me de novo\r\n\r\nEu adorarei \r\nAo Deus da minha vida\r\n\r\nQue me compreendeu\r\nSem nenhuma explicação\r\n\r\nEspirito, Espirito\r\nQue desce como fogo\r\n\r\nVem como em pentecostes\r\nE enche-me de novo\r\n\r\nEu navegarei', NULL, 1),
(128, 'Vim para adorar-te', 'Luz do mundo \r\nvieste a terra\r\nPara que eu\r\nPudesse te ver\r\n\r\nTua beleza \r\nMe leva a adorar-te\r\nQuero contigo viver\r\n\r\nVim para adorar-te\r\nVim para prostrar-me\r\nVim para dizer \r\nQue és meu Deus\r\n\r\nÉs totalmente amável\r\nTotalmente digno\r\nTão maravilhoso \r\npara mim\r\n\r\nEterno rei \r\nExaltado nas alturas\r\nGlorioso no céu\r\n\r\nHumilde vieste \r\nA terra que criaste\r\nPor amor \r\nPobre se fez\r\n\r\nVim para adorar-te\r\nVim para prostrar-me\r\nVim para dizer \r\nQue és meu Deus\r\n\r\nÉs totalmente amável\r\nTotalmente digno\r\nTão maravilhoso\r\npara mim\r\n\r\nEu nunca \r\nsaberei o preço\r\nDos meus pecados \r\nlá na cruz', NULL, 1),
(129, 'Eu me rendo aos teus pés', 'A Ti eu vou clamar\r\nPois tudo vem de Ti\r\nE tudo está em Ti\r\n\r\nPor Ti vou caminhar\r\nTu és a direção\r\nO sol a me guiar\r\n\r\nTudo pode passar\r\nTeu amor jamais \r\nme deixará\r\n\r\nSempre há de existir\r\nNovo amanhã \r\npreparado pra mim\r\n\r\nEu me rendo \r\naos teus pés\r\n\r\nÉs tudo que \r\neu preciso \r\npra viver\r\n\r\nEu me lanço \r\naos teus braços\r\n\r\nOnde encontro \r\nmeu refúgio\r\n\r\nJesus, eis-me aqui\r\nJesus, eis-me aqui', NULL, 1),
(130, 'Renova-me', 'Renova-me Senhor Jesus\r\nJá não quero ser igual\r\n\r\nRenova-me Senhor Jesus\r\nPõe em mim seu coração\r\n\r\nPor que tudo o que há \r\nDentro de mim\r\nNecessita ser mudado Senhor\r\n\r\nPor que tudo o que há\r\nDentro do meu coração\r\n\r\nNecessita mais de Ti', NULL, 1),
(131, 'Incalculavel', 'Maior que o mais alto monte\r\nÉ o amor de Deus o Pai\r\nProfundo como o oceano\r\nTua beleza me atrai\r\n\r\nTão largo quanto o universo\r\nComo posso entender\r\nQue um Deus assim tão grande\r\nVeio ao mundo pra morrer\r\n\r\nOoou\r\nDeus de tal maneira me amou\r\nOoou\r\nQue seu próprio filho entregou\r\n\r\nO seu amor\r\nÉ sem medidas\r\nInfinito é\r\n\r\nO seu amor\r\nPagou a minha divida\r\nNa cruz\r\n\r\nHoje tenho vida em Cristo Jesus\r\n\r\nAlto é, Intransponível\r\nProfundo também\r\nMas acescível\r\nA sua extensão é imcomparável\r\nO amor de Jesus é incalculável', NULL, 1),
(132, 'Espírito ', 'Espírito\r\nEspírito\r\nEspírito \r\nSanto de Deus\r\n\r\nVem controlar \r\ntodo o meu ser\r\nVem dirigir \r\no meu viver\r\n\r\nO meu pensar\r\nO meu falar\r\nO meu sentir\r\nO meu agir', NULL, 1),
(133, 'alegria do senhor é a nossa força', 'Vento sopra forte Tuas águas não podem me afogar\r\n Vento sopra forte\r\n E em suas mãos vou segurar \r\nE não me guio pelo que vejo \r\nMas eu sigo pelo que creio \r\nEu não olho as circunstâncias \r\nEu vejo o teu amor \r\nA alegria do Senhor é a nossa força (4x)  \r\nEssa alegria não vai mais sair\r\n Essa alegria não vai mais sair\r\n Essa alegria não vai mais sair \r\nDe dentro do meu coração', NULL, 1),
(134, 'Dependo de Ti.', 'Louvar-Te é bem mais\r\nDo que eu mereço\r\nAdorar-Te mais profundo\r\nDo que eu pensei\r\n\r\nServir-Te é tudo\r\nO que eu espero\r\nE o que mais quero\r\nÉ estar mais junto a Ti\r\n\r\nDerramar perante Ti\r\nOs meus anseios\r\nA minha oferta nessa hora\r\nÉ a verdade\r\n\r\nE te contar os meus\r\nMaiores sonhos\r\nAh Senhor\r\nComo eu dependo de Ti\r\n\r\nPara me curar\r\nPara me ensinar\r\nO caminho que devo seguir\r\n\r\nPodes me usar\r\nComo Tu bem queres\r\nEu abro o meu coração', NULL, 1),
(135, 'Eu Vejo o Céu', 'Eu vejo o Céu\r\nDescendo sobre nós\r\n\r\nEu vejo os anjos\r\nCantando o Teu santo nome\r\n\r\nEu te louvo, eu te louvo\r\nE toda honra para ti Jesus\r\n\r\nEu vejo a Glória\r\nDescendo sobre nós\r\n\r\nE a esperança que trás cura\r\nA todo mal\r\n\r\nEu te louvo, eu te louvo\r\nE toda honra para ti Jesus\r\n\r\nTe dou louvor \r\ne toda honra\r\nTu és meu Deus\r\n\r\nEu vivo pra ti\r\nTe dou louvor \r\ne toda glória Deus\r\n\r\nToda Glória\r\n\r\nVem encher esse lugar\r\nQue venha o Céu\r\n\r\nCom os anjos vou cantar\r\nQue venha o Céu\r\n\r\nFace a face te encontrar\r\nQue venha o Céu\r\n\r\nAos Teus pés pra te adorar\r\nQue venha o Céu', NULL, 1),
(136, 'Se eu apenas te tocar', 'Se eu apenas te tocar\r\nUm milagre viverei \r\nEm minha vida\r\n\r\nMinha fé vai me levar \r\nOnde o impossivel torna-se real\r\n\r\nMeu milagre vim buscar\r\nSó você pode mudar minha história\r\n\r\nJa sofri demais eu sei\r\nMais ouvi que existe alguém\r\nQue me ama\r\n\r\nJa sofri demais eu sei\r\nMais ouvi que existe alguém\r\nQue me ama, me ama\r\n\r\nSe eu apenas te tocar eu sei\r\nSerei curado\r\n\r\nSe eu apenas te tocar eu sei\r\nSerei sarado\r\n\r\nSe eu apenas te tocar eu sei\r\nSerei curado\r\n\r\nSe eu apenas te tocar eu sei\r\nSerei sarado\r\n\r\nJa sofri demais eu sei\r\nMais ouvi que existe alguém\r\nQue me ama\r\n\r\nJa sofri demais eu sei\r\nMais ouvi que existe alguém\r\nQue me ama, me ama\r\n\r\nSe eu apenas te tocar eu sei\r\nSerei curado\r\n\r\nSe eu apenas te tocar eu sei\r\nSerei sarado\r\n\r\nSe eu apenas te tocar eu sei\r\nSerei curado\r\n\r\nSe eu apenas te tocar eu sei\r\nSerei sarado\r\n\r\nTeu amor me alcançou \r\nMinha fé me salvou...', NULL, 1),
(137, 'Se eu me Humilhar', 'Neste lugar, Tu és Real\r\nVou me entregar totalmente\r\nO teu toque abriu os olhos do meu coração\r\nE eu posso enxergar e entender\r\n\r\nSe eu me humilhar diante do teu altar\r\nE sacrificar aquilo que me custar\r\nTu inclinarás os teus ouvidos ao meu clamor\r\n\r\nMais vale um dia no centro do teu querer\r\nQue toda a vida sem jamais te conhecer\r\n\r\nTu és a minha fonte\r\nMinha colheita\r\nMinha herança\r\n\r\nA minha Fonte é o teu amor\r\nMinha colheita é o teu amor\r\nMinha herança é o teu amor', NULL, 1),
(138, 'Nosso Deus é soberano', 'Nosso Deus é soberano\r\nEle reina antes da fundação do mundo\r\n\r\nA terra era sem forma e vazia\r\nE o Espírito do nosso Deus se movia \r\nsobre a face das águas\r\n\r\nFoi Ele quem criou o céu dos céus\r\nquem fez separação das águas\r\nda terra seca\r\n\r\nFoi Ele quem criou os luminares\r\nquem criou a natureza\r\ne formou o homem\r\n\r\nGlória a Deus por suas maravilhas\r\nPela sua grandeza, Gloria Deus', NULL, 1),
(139, 'Ele é exaltado', 'Ele é exaltado \r\no Rei exaltado no céu\r\nEu louvarei\r\n\r\nEle é exaltado \r\nPra sempre exaltado \r\no seu nome, \r\nLouvarei\r\n\r\nEle é o Senhor\r\nTua verdade vai \r\nsempre Reinar\r\n\r\nTerra e céu glorificam \r\nseu Santo nome\r\n\r\nEle é exaltado \r\no Rei exaltado,\r\nNo céu', NULL, 1),
(140, 'Santo, Toda terra está cheia', 'Santo, \r\nToda terra está cheia\r\nSanto, \r\nDa sua Glória\r\n\r\nSanto, \r\nToda terra está cheia\r\nSanto é o Senhor!', NULL, 1),
(141, 'Vou deixar na cruz', 'Sua morte ali na cruz\r\nCarregando a minha dor\r\nVocê se expôs por mim\r\n\r\n\r\nSe entregou em meu lugar\r\n\r\nE me deu uma nova chance\r\nEu vou recomeçar\r\n\r\n\r\nVou deixar na cruz tudo o que passou\r\nTudo o que ficou pra trás\r\n\r\n\r\nOlho pro Senhor, corro pro Senhor\r\nO seu amor me chama\r\n\r\n\r\nEu dependo desse amor, do Seu amor mais que tudo\r\n\r\n\r\nEu confio que esse amor tem poder\r\nPra curar as dores', NULL, 1),
(142, 'Por que Ele vive ( Deus enviou ) - XAMA', 'Por que Ele vive, posso crer no amanhã\r\nPor que Ele vive, temor não há\r\n\r\nMais eu bem sei, eu sei\r\nQue a minha vida\r\nEstás nas mãos do meu Jesus\r\nQue vivo está\r\n\r\nDeus enviou seu filho amado\r\nPara morrer no meu lugar\r\nNa cruz sofreu, por meus pecados\r\n\r\nMais o sepulcro vazio está\r\nPor que Ele vive\r\n\r\nE quando enfim, chegar a hora\r\nEm que a morte enfrentarei\r\n\r\nSem medo então, terei Vitória\r\nVerei na Glória o meu Jesus\r\nQue vivo está', NULL, 1),
(143, 'Leva-me além', 'Render a Ti, adoração\r\nE derramar o meu ser\r\nÉ o que meu coração\r\nDeseja toda manhã\r\n\r\nTe imaginar, é me inspirar\r\nPra te dizer, estou apaixonado\r\nCada vez mais por Ti\r\n\r\nSenhor Tu és incomparável \r\nTeu nome é Maravilhoso\r\n\r\nLeva-me além\r\nHá um nivel mais profundo\r\nDe intimidade contigo ó Senhor\r\n\r\nLeva-me além\r\nQue a minha vida flua mais da tua unção\r\nMais do teu Poder', NULL, 1),
(144, 'Eu vejo céu (lc)', 'Vejo o Céu\r\nDescendo sobre nós\r\nVejo os anjos\r\nLouvando o Teu santo nome\r\n\r\nEu te louvo\r\nToda honra para ti Jesus\r\n\r\nVejo a Glória\r\nEnchendo esse lugar\r\nE a esperança que trás cura\r\nA todo mal\r\n\r\nTe dou louvor e toda honra\r\nTu és meu Deus\r\nEu vivo pra ti\r\n\r\nTe dou louvor e toda glória Deus\r\n\r\nVem encher esse lugar\r\nQue venha o Céu\r\n\r\nCom os anjos vou cantar\r\nQue venha o Céu\r\n\r\nFace a face te encontrar\r\nQue venha o Céu\r\n\r\nAos Teus pés te adorar\r\nQue venha o Céu', NULL, 1),
(145, 'Sou Livre', 'Em meio a tantas frustações\r\nCaminhos que nos tiram a paz\r\nMesmo em um mundo de ilusões\r\nOnde a fama vale mais\r\n\r\nO Senhor me amou\r\nPra eu ser livre se entregou\r\nEntre os maiores se humilhou\r\nE a certeza me deixou\r\n\r\nSou livre\r\nSou livre\r\nJá não há condenação\r\nSobre mim\r\n\r\nSou livre\r\nSou livre\r\nSou livre\r\nEm ti', 'Vinicius', 1),
(146, 'O Caminho', 'Numa noite rara\r\nClara lua\r\nPensamentos dara\r\nEm amores viz\r\nSe da o tom da melodia\r\n\r\nTentei, lutei\r\nLancei os dardos\r\nE num acaso\r\nNenhum fato exato\r\nO som mútuo ecoou amargo\r\n\r\nSe lá\r\nSei lá ninguém sabe\r\nNovos horizontes\r\nNovo recomeço\r\nUma nova história\r\n\r\nSe lá\r\nSerá um lugar melhor\r\nPara se estar\r\nPor que pensar?\r\nSó desejo ir pra lá', 'Vinicius', 1),
(147, 'Mais uma vez', 'Tudo que eu preciso, tudo que eu procurava\r\nEncontro em teus braços\r\n\r\nEm teus olhos não vejo mágoas\r\nE mesmo com os meus erros, você dizia que me amava\r\nAgora eu percebo o quanto eu sinto a tua falta\r\n\r\nEu tento ser como você\r\nEu tento ver como você vê\r\nTudo parece ser mais simples quando se vê com o coração\r\n\r\nEu vejo como você tem, amor por alguém que te esqueceu\r\nE mesmo assim você insiste em amar mais uma vez\r\n\r\nVocê conseguiu ver\r\nO que há de melhor em mim\r\nAgora eu consigo ver, há muito de você aqui\r\nSem você eu me sinto só, falta uma parte de mim', NULL, 0),
(148, 'Incendeia', 'O Tempo de cantar chegou\r\nO Tempo de dançar chegou\r\nO Tempo de cantar chegou\r\nO Tempo de dançar chegou\r\n\r\nE Ele Vem, E Ele Vem\r\nSaltando pelos montes\r\nE Ele Vem, E Ele Vem\r\nSaltando pelos montes\r\n\r\nOs seus cabelos, os seus cabelos\r\nSão brancos como a neve\r\nOs seus cabelos, os seus cabelos\r\nSão brancos como a neve\r\n\r\nE nos seus olhos,\r\nE nos seus olhos há Fogo...\r\n\r\nIncendeia Senhor a sua Noiva\r\nIncendeia Senhor a sua Igreja\r\nIncendeia Senhor a sua Casa\r\nVem me Incendiar\r\n\r\nIncendeia Senhor a sua Noiva\r\nIncendeia Senhor a sua Igreja\r\nIncendeia Senhor a sua Casa\r\nVem me Incendiar\r\n\r\nE Ele Vem, E Ele Vem\r\nSaltando pelos montes\r\nE Ele Vem, E Ele Vem\r\nSaltando pelos montes\r\n\r\nOs seus cabelos, os seus cabelos\r\nSão brancos como a neve\r\nOs seus cabelos, os seus cabelos\r\nSão brancos como a neve\r\n\r\nE nos seus olhos,\r\nE nos seus olhos há Fogo...\r\n\r\nIncendeia Senhor a sua Noiva\r\nIncendeia Senhor a sua Igreja\r\nIncendeia Senhor a sua Casa\r\nVem me Incendiar\r\n\r\nIncendeia Senhor a sua Noiva\r\nIncendeia Senhor a sua Igreja\r\nIncendeia Senhor a sua Casa\r\nVem me Incendiar...\r\nVem me Incendiar...\r\nVem me Incendiar...\r\n\r\nIncendeia Senhor a sua Noiva\r\nIncendeia Senhor a sua Igreja\r\nIncendeia Senhor a sua Casa\r\nVem me Incendiar\r\n\r\nIncendeia Senhor a sua Noiva\r\nIncendeia Senhor a sua Igreja\r\nIncendeia Senhor a sua Casa\r\nVem me Incendiar...\r\nVem me Incendira...\r\nVem me Incendiar...\r\nVem me Incendiar...', NULL, 1),
(149, 'Pela fé André Valadão', 'Pela fé posso clamar\r\nPela fé posso enxergar\r\n\r\nPela fé posso tocar\r\nPela fé, pela fé\r\n\r\nNão há muralhas \r\nque ficarão de pé\r\nDiante de mim\r\n\r\nEstou firmado em \r\nCristo pra sempre\r\nTenho a luz em mim\r\n\r\nPela fé posso mudar\r\nPela fé posso alcançar\r\n\r\nPela fé posso viver\r\nPela fé, pela fé\r\n\r\nNão há muralhas \r\nque ficarão de pé\r\nDiante de mim\r\n\r\nEstou firmado em \r\nCristo pra sempre\r\nTenho a luz em mim\r\n\r\nPela fé\r\nPela fé', NULL, 1),
(150, 'Te agradeço - kleber Lucas', 'Eu te agradeço, Deus\r\nPor se lembrar de mim, \r\n\r\nE pelo teu favor\r\nE o que me faz crescer\r\n\r\nEu vivo pela fé, \r\ne não vacilo\r\nEu não paro, \r\neu não desisto\r\n\r\nEu sou de Deus, \r\neu sou de Cristo\r\n\r\nVocê mudou a minha história\r\nE fez o que ninguém \r\npodia imaginar\r\n\r\nVocê acreditou \r\ne isso é tudo\r\nSó vivo pra você\r\n\r\nNão sou do mundo, não\r\n\r\nA honra, a glória, a força\r\nO louvor a Deus\r\n\r\nE o levantar das minhas mãos\r\nÉ pra dizer que te pertenço, Deus\r\n\r\nEu te agradeço, Deus\r\nQue no deserto \r\n\r\nNão me deixou morrer\r\nE nem desanimar\r\n\r\nE como aquela mãe, \r\nque não desiste\r\n\r\nVocê não se esqueceu, \r\nVocê insiste\r\n\r\nVocê mudou a minha história\r\nE fez o que ninguém \r\npodia imaginar\r\n\r\nVocê acreditou \r\ne isso é tudo\r\nSó vivo pra você\r\nNão sou do mundo, não\r\n\r\nA honra, a glória, a força\r\nO louvor a Deus\r\n\r\nE o levantar das minhas mãos\r\nÉ pra dizer que te pertenço, Deus', NULL, 1),
(151, 'Graças a Deus/ Cantai ao Senhor', 'Graças a Deus que nos dá a vitória em Cristo\r\nEle restaurou a nossa comunhão com Deus\r\nNosso Pai\r\n\r\nGlória seja a Deus\r\nPois Ele é o Rei\r\nAntes da criação e pra sempre será\r\n\r\nGlória seja a Deus\r\nPois Ele é o Rei\r\nEle é o Rei e pra sempre será\r\n\r\nCantai ao Senhor\r\nUm novo cântico\r\n\r\nPorque o Senhor\r\nTem feito maravilhas\r\nA sua destra e o seu braço santo\r\n\r\nQue alcançaram vitória\r\nO Senhor fez tão grande salvação\r\nRevelou sua justiça entre as nações\r\n\r\nCelebrai com Júbilo\r\nAo Senhor\r\nCom grande alegria\r\nOh povos da Terra\r\n\r\nBatei palmas\r\nDai glória a Deus\r\nCom grandes brados\r\nJesus é nosso Rei', NULL, 1),
(152, 'Abba ', 'És mais real que o chão que eu piso\r\n\r\nÉs mais real que o ar em meus pulmões\r\n\r\nTeus pensamentos me definem\r\n\r\nÉs tudo para mim, és a minha Realidade. És a minha Realidade\r\n\r\nAba, eu pertenço a Ti\r\n\r\nEstás mais perto do que a canção em meus lábios\r\n\r\nEstás mais perto do que a pele em meus ossos\r\n\r\nÉs tudo para mim, és a minha Realidade\r\nÉs a minha Realidade', 'Laura Souguelis', 1),
(153, 'Eu encontrei o amor', 'O espelho já não me diz\r\nMuito sobre quem eu sou\r\n\r\nAs canções por aí\r\nJá não fazem tanto \r\nsentido assim\r\n\r\nA verdade é que ao olhar \r\nem Teus olhos\r\nDescobri quem eu sou\r\n\r\nO Teu amor balançou tudo\r\nDentro de mim\r\n\r\nA Graça de graça\r\nFoi dada a todos\r\nO amor prometeu\r\nUm futuro de esperança\r\n\r\nA vida invadiu o coração\r\nque não batia\r\nE o sorriso se abriu\r\ndiante de tanta alegria.\r\n\r\nEu encontrei Jesus,\r\nQue me faz tão feliz.\r\nEu encontrei o amor,\r\nQue eu sempre quis.\r\n\r\nNão há ninguém igual,\r\nAo meu lindo Jesus.', 'Pedro Spagiari', 1),
(154, 'Jesus em Tua presença', 'Jesus, em tua presença\r\nreunimo-nos aqui\r\n\r\nContemplamos tua face\r\ne rendemo-nos aTi\r\n\r\nPois um dia sua morte trouxe vida a todos nós\r\n\r\nE nos deu completo acesso ao coração do Pai\r\n\r\nO véu que separava\r\njá não separa mais\r\n\r\nA luz que outrora apagada agora brilha e cada dia brilha mais\r\n\r\nSó pra te adorar\r\ne fazer seu nome grande\r\n\r\nE te dar o louvor que é devido\r\nEstamos nós aqui', NULL, 1),
(155, 'Te busco', 'Eu te busco\r\nTe procuro ó Deus\r\nNo silêncio Tu estás\r\n\r\nEu te busco\r\nToda hora espero em Ti\r\n\r\nRevela-te a mim\r\nConhecer-te eu quero mais\r\n\r\nSenhor te quero, quero ouvir tua voz\r\nSenhor te quero mais\r\n\r\nQuero tocar-te, Tua face eu quero ver\r\nSenhor te quero mais\r\n\r\nProsseguindo para o alvo eu vou\r\nA coroa conquistar\r\n\r\nVou seguindo, nada pode me impedir\r\nEu vou te seguir\r\n\r\nConhecer-te eu quero mais', NULL, 1),
(156, 'Deus Grande', 'Os olhos do cego abriu,\r\nA morte Ele resistiu,\r\nPois não há outro como Tú..\r\n\r\nVejo Tua luz a brilhar,\r\nPra sempre eu vou te adorar,\r\nPois não há outro como Tú.\r\n\r\nÉs um Deus grande,\r\nÉs um Deus forte,\r\nNão há ninguém comparado a Ti.\r\n\r\nÉs Deus que cura,\r\nÉs Deus que salva,\r\nTú És meu Deus.\r\n\r\nE se Deus é por nós,\r\nQuem será contra nós?!\r\nSe Ele está aqui,\r\nQuem pode resistir?!', NULL, 1),
(157, 'Em teus braços', 'Seguro estou nos braços Daquele que nunca me deixou\r\n\r\nSeu amor perfeito sempre esteve repousado em mim\r\n\r\nE se eu passar pelo vale. Acharei conforto em teu amor\r\n\r\nPois eu sei que és aquele que me guarda. Me guarda\r\n\r\nEm teus braços é meu descanso', 'Laura Souguelis', 1),
(158, 'Quero louvar-te', 'Quero louvar-te\r\nSempre mais e mais\r\n\r\nQuero louvar-te\r\nSempre mais e mais.\r\n\r\nBuscar o teu querer\r\nTua graça conhecer, quero louvar-te.\r\n\r\nQuero adorar-te\r\nSempre mais e mais\r\n\r\nQuero adorar-te\r\nSempre mais e mais\r\n\r\nBuscar o teu querer\r\nTua graça conhecer, quero adorar-te\r\n\r\nAs aves do céu\r\nCantam para ti,\r\nAs feras do campo\r\nRefletem teu poder.\r\n\r\nQuero louvar, Quero levantar as minhas mãos à ti', NULL, 1),
(159, 'Perto quero estar', 'Perto quero estar\r\nJunto aos teus pés\r\n\r\nPois prazer maior não há\r\nQue me render e te adorar\r\n\r\nTudo que há em mim\r\nQuero te ofertar\r\n\r\nMas ainda é pouco, eu sei\r\nSe comparado ao que ganhei\r\n\r\nNão sou apenas servo\r\nTeu amigo me tornei\r\n\r\nTe louvarei\r\nNão importam as circunstâncias\r\n\r\nAdorarei\r\nSomente a ti, Jesus', NULL, 1),
(160, 'Raridade', 'Não consigo ir além do teu olhar\r\nTudo que eu consigo é imaginar\r\n\r\nA riqueza que existe dentro de você\r\n\r\nO ouro eu consigo só admirar\r\n\r\nMas te olhando eu posso a Deus adorar\r\nSua alma é um bem que nunca envelhecerá\r\n\r\nO pecado não consegue esconder\r\nA marca de Jesus que existe em você\r\n\r\nO que você fez ou deixou de fazer\r\nNão mudou o início: Deus escolheu você\r\n\r\nSua raridade não está naquilo que você possui\r\nOu que sabe fazer. \r\nIsso é mistério de Deus com você.\r\n\r\nVocê é um espelho\r\nQue reflete a imagem do Senhor\r\n\r\nNão chore se o mundo ainda não notou\r\nJá é o bastante Deus reconhecer o seu valor\r\n\r\nVocê é precioso, mais raro que o ouro puro de ofir\r\nSe você desistiu, Deus não vai desistir\r\n\r\nEle está aqui pra te levantar\r\nSe o mundo te fizer cair....', NULL, 1),
(161, 'Vitoria no Deserto', 'Quando a noite fria cair sobre mim (nanana)\r\nE num deserto eu me encontrar\r\n\r\nMe ver cercado por egípcios e por faraó (ohohoh)\r\nSendo impedido de prosseguir\r\n\r\nSei que o teu fogo cairá sobre mim (heyey)\r\nSei que o teu fogo cairá sobre mim\r\n\r\nE me levará a em ti confiar (ahahah)\r\nE me levará a em ti confiar\r\n\r\nEntão eu direi, então eu direi, ououh, abra-se o mar\r\nE eu passarei pulando e dançando em sua presença\r\n\r\nEntão eu direi, então eu direi, ououh, abra-se o mar\r\nE eu passarei pulando e dançando em sua presença\r\n\r\nPor isso eu pulo, pulo, pulo, pulo, pulo\r\nNa presença do rei\r\n\r\nPor isso eu danço, danço, danço, danço, danço\r\nNa presença do rei\r\n\r\nUm grito de júbilo\r\nPor isso eu grito, grito, grito, grito, grito\r\nNa presença do rei\r\n\r\nPor isso eu corro, corro, corro, corro, corro\r\nNa presença do rei\r\n\r\nEntão eu direi, então eu direi, ououh, abra-se o mar\r\nE eu passarei pulando e dançando em sua presença', NULL, 1),
(162, 'Rei das Nações', 'Grandes são as Tuas obras,\r\nSenhor todo poderoso;\r\nJustos e verdadeiros são os Teu caminhos\r\n\r\nÓ, Rei das nações,\r\nQuem não temerá?\r\nQuem não glorificará Teu nome?\r\n\r\nÓ Rei das nações,\r\nQuem não te louvará?\r\nPois só teu nome é santo\r\n\r\nTodas as nações virão\r\nE adorarão diante de Ti,\r\nPois os Teus atos de justiça se fizeram manifestos', NULL, 1),
(163, 'Em Espirito e Em verdade', 'Em Espirito, em Verdade\r\nTe adoramos, Te adoramos\r\n\r\nEm Espirito, em Verdade\r\nTe adoramos, Te adoramos\r\n\r\nReis dos reis e Senhor,\r\nte entregamos o nosso viver\r\n\r\nReis dos reis e Senhor,\r\nte entregamos o nosso viver\r\n\r\nPra te adorar ó Rei dos reis\r\nFoi que eu nasci ó Rei Jesus\r\n\r\nMeu prazer é te louvar\r\nMeu prazer é estar nos átrios do Senhor\r\n\r\nMeu prazer é viver na casa de Deus\r\nOnde flui o Amor\r\n\r\nPra te adorar ó Rei dos reis\r\nFoi que eu nasci ó Rei Jesus\r\n\r\nMeu prazer é te louvar\r\nMeu prazer é estar nos átrios do Senhor\r\n\r\nMeu prazer é viver na casa de Deus\r\nMinistrando o Louvor', NULL, 1),
(164, 'Biblia Joao 5*', '1. Depois disto havia uma festa entre os judeus, e Jesus subiu a Jerusalém.\n\n2. Ora, em Jerusalém há, próximo à porta das ovelhas, um tanque, chamado em hebreu Betesda, o qual tem cinco alpendres.\n\n3. Nestes jazia grande multidão de enfermos, cegos, mancos e ressicados, esperando o movimento da água.\n\n4. Porquanto um anjo descia em certo tempo ao tanque, e agitava a água; e o primeiro que ali descia, depois do movimento da água, sarava de qualquer enfermidade que tivesse.\n\n5. E estava ali um homem que, havia trinta e oito anos, se achava enfermo.\n\n6. E Jesus, vendo este deitado, e sabendo que estava neste estado havia muito tempo, disse-lhe: Queres ficar são?\n\n7. O enfermo respondeu-lhe: Senhor, não tenho homem algum que, quando a água é agitada, me ponha no tanque; mas, enquanto eu vou, desce outro antes de mim.\n\n8. Jesus disse-lhe: Levanta-te, toma o teu leito, e anda.\n\n9. Logo aquele homem ficou são; e tomou o seu leito, e andava. E aquele dia era sábado.\n\n10. Então os judeus disseram àquele que tinha sido curado: É sábado, não te é lícito levar o leito.\n\n11. Ele respondeu-lhes: Aquele que me curou, ele próprio disse: Toma o teu leito, e anda.\n\n12. Perguntaram-lhe, pois: Quem é o homem que te disse: Toma o teu leito, e anda?\n\n13. E o que fora curado não sabia quem era; porque Jesus se havia retirado, em razão de naquele lugar haver grande multidão.\n\n14. Depois Jesus encontrou-o no templo, e disse-lhe: Eis que já estás são; não peques mais, para que não te suceda alguma coisa pior.\n\n15. E aquele homem foi, e anunciou aos judeus que Jesus era o que o curara.\n\n16. E por esta causa os judeus perseguiram a Jesus, e procuravam matá-lo, porque fazia estas coisas no sábado.\n\n17. E Jesus lhes respondeu: Meu Pai trabalha até agora, e eu trabalho também.\n\n18. Por isso, pois, os judeus ainda mais procuravam matá-lo, porque não só quebrantava o sábado, mas também dizia que Deus era seu próprio Pai, fazendo-se igual a Deus.\n\n19. Mas Jesus respondeu, e disse-lhes: Na verdade, na verdade vos digo que o Filho por si mesmo não pode fazer coisa alguma, se o não vir fazer o Pai; porque tudo quanto ele faz, o Filho o faz igualmente.\n\n20. Porque o Pai ama o Filho, e mostra-lhe tudo o que faz; e ele lhe mostrará maiores obras do que estas, para que vos maravilheis.\n\n21. Pois, assim como o Pai ressuscita os mortos, e os vivifica, assim também o Filho vivifica aqueles que quer.\n\n22. E também o Pai a ninguém julga, mas deu ao Filho todo o juízo;\n\n23. Para que todos honrem o Filho, como honram o Pai. Quem não honra o Filho, não honra o Pai que o enviou.\n\n24. Na verdade, na verdade vos digo que quem ouve a minha palavra, e crê naquele que me enviou, tem a vida eterna, e não entrará em condenação, mas passou da morte para a vida.\n\n25. Em verdade, em verdade vos digo que vem a hora, e agora é, em que os mortos ouvirão a voz do Filho de Deus, e os que a ouvirem viverão.\n\n26. Porque, como o Pai tem a vida em si mesmo, assim deu também ao Filho ter a vida em si mesmo;\n\n27. E deu-lhe o poder de exercer o juízo, porque é o Filho do homem.\n\n28. Não vos maravilheis disto; porque vem a hora em que todos os que estão nos sepulcros ouvirão a sua voz.\n\n29. E os que fizeram o bem sairão para a ressurreição da vida; e os que fizeram o mal para a ressurreição da condenação.\n\n30. Eu não posso de mim mesmo fazer coisa alguma. Como ouço, assim julgo; e o meu juízo é justo, porque não busco a minha vontade, mas a vontade do Pai que me enviou.\n\n31. Se eu testifico de mim mesmo, o meu testemunho não é verdadeiro.\n\n32. Há outro que testifica de mim, e sei que o testemunho que ele dá de mim é verdadeiro.\n\n33. Vós mandastes mensageiros a João, e ele deu testemunho da verdade.\n\n34. Eu, porém, não recebo testemunho de homem; mas digo isto, para que vos salveis.\n\n35. Ele era a candeia que ardia e alumiava, e vós quisestes alegrar-vos por um pouco de tempo com a sua luz.\n\n36. Mas eu tenho maior testemunho do que o de João; porque as obras que o Pai me deu para realizar, as mesmas obras que eu faço, testificam de mim, que o Pai me enviou.\n\n37. E o Pai, que me enviou, ele mesmo testificou de mim. Vós nunca ouvistes a sua voz, nem vistes o seu parecer.\n\n38. E a sua palavra não permanece em vós, porque naquele que ele enviou não credes vós.\n\n39. Examinais as Escrituras, porque vós cuidais ter nelas a vida eterna, e são elas que de mim testificam;\n\n40. E não quereis vir a mim para terdes vida.\n\n41. Eu não recebo glória dos homens;\n\n42. Mas bem vos conheço, que não tendes em vós o amor de Deus.\n\n43. Eu vim em nome de meu Pai, e não me aceitais; se outro vier em seu próprio nome, a esse aceitareis.\n\n44. Como podeis vós crer, recebendo honra uns dos outros, e não buscando a honra que vem só de Deus?\n\n45. Não cuideis que eu vos hei de acusar para com o Pai. Há um que vos acusa, Moisés, em quem vós esperais.\n\n46. Porque, se vós crêsseis em Moisés, creríeis em mim; porque de mim escreveu ele.\n\n47. Mas, se não credes nos seus escritos, como crereis nas minhas palavras?\nJoão 5:1-47', NULL, 1),
(165, 'O teu Amor', 'Estou aqui com o coração quebrantado\r\nE de espirito abatido\r\n\r\nSenhor vem me socorrer, sem Ti não posso mais viver,\r\nVem me transformar\r\n\r\nSenhor, sinto falta do Teu Amor, sinto falta do Teu calor\r\n\r\nVem Me envolver com o Teu Espirito\r\nEu quero estar no centro do Teu Amor\r\nNo centro do Teu querer\r\n\r\nEu quero estar no Teu altar, ser cheio da Tua Presença\r\n\r\nEu quero Senhor, eu quero o Teu Amor', NULL, 1),
(166, 'Quão formoso és', 'Quão Formoso És Rei do Universo\r\nTua Glória enche a terra e enche os céus\r\n\r\nTua Glória enche a terra\r\nTua Glória enche os céus\r\n\r\nTua Glória enche minha vida Senhor\r\n\r\nMaravilhoso é estar em tua Presença\r\nMaravilhoso é pode te adorar\r\n\r\nMaravilhoso é poder tocar em tuas vestes\r\nMaravilhoso é te contemplar Senhor', NULL, 1),
(167, 'Te amo Tanto', 'Palavras não podem expressar\r\nO que sinto em meu coração\r\n\r\nNem mesmo as notas das canções\r\nLhe mostram o que há em meu ser\r\n\r\nPode o sol escurecer\r\nE a terra estremecer\r\nEu não vou te deixar\r\n\r\nNão pode apagar \r\nO que o tempo escreveu\r\nDentro de mim\r\n\r\nTe amo tanto Senhor\r\nTe quero tanto meu Deus\r\n\r\nEu te amo tanto Senhor\r\nEu te quero tanto meu Deus', NULL, 1),
(168, 'Ninguém por Perto', 'Momentos de silêncio assim\r\nNinguém por perto mas\r\nUma voz dentro de mim\r\nMe diz que não estou só\r\n\r\nNesse tempo o meu pensamento vai\r\nFicar ligado com a vontade do Pai\r\nE te entender mais\r\nMe faz querer mais\r\nA cada dia\r\n\r\nHoje eu sei o que é deixar\r\nQue o rio de Deus me leve\r\nDesde a fonte ao mar\r\nHá um rumo pra seguir\r\n\r\nEnquanto eu acreditar\r\nO sopro de Deus me move\r\nSe te alegra minha entrega\r\nRecebe minha vida hoje\r\n\r\nEm qualquer parte que eu estiver\r\nA cada passo que eu der\r\nVão ficar tuas marcas e não as minhas\r\nTua luz onde não havia\r\n\r\nTeu nome Teu sangue Tua dor\r\nTuas palavras Tua paz Teu amor\r\nMeu Deus meu Salvador\r\nJesus meu pão de cada dia\r\n\r\nFica aqui bem perto de mim\r\nVem ser uma parte de mim\r\nCompletar com aquilo que eu sinto mais falta\r\nMe leva pra torre mais alta\r\n\r\nVem incendiar essa casa coberta de gelo\r\nA fumaça vai pelo ar\r\nNão dá pra fingir que tá tudo bem aqui\r\nNão dá pra por nada no seu lugar\r\n\r\nNada faz o relógio parar o ponteiro não descansa\r\nSó aumenta a distância\r\nEntre o dia da mudança e as teias de aranha\r\nA noite tamanha a velhice da infância\r\n\r\nEu desisti do plano errado que eu fiz e\r\nOuvi o alarme avisou\r\nQue a hora chegou estou de pé\r\nMe revela agora quem sou\r\n\r\nSabia que era você\r\nQuando ouvi minha porta bater\r\nComo alguém que acaba de nascer\r\nE vem pra ficar contigo\r\n\r\nDeitar e levantar estar ao Teu lado\r\nEnxuga meu rosto molhado\r\nDe tanta saudade e lembrança\r\nEscondi a criança num porta retrato quebrado\r\n\r\nEntão você vem e já tá tudo bem\r\nReconectou-me ao quadro de força\r\nE a falta de cor no meu quadro\r\nRestaurou também\r\n\r\nTudo em ordem\r\nJá descansei e hoje sei\r\nQue vale o empenho\r\nPois a Tua presença é tudo o que tenho', NULL, 0),
(170, 'Chuva de Poder', 'Mais uma vez a solidão bateu á porta\r\nMe encontrou sem lagrimas pra derramar\r\n\r\nO sol se pôs e a escuridão chegou agora\r\nSerá que o meu barco vai naufragar\r\n\r\nO desespero bate forte no meu peito\r\nE faz meu corpo inteiro se arrepiar\r\n\r\nEu tenho que tomar uma atitude agora\r\nPorque o tempo passa e não vai esperar\r\n\r\nVai com vento agora essa minha frustração\r\nDa minha vitória eu não abro mão\r\n\r\nCom dificuldades eu cheguei aqui\r\nNo meio do caminho não vou desistir\r\n\r\nE podem vir as ondas que por cima vou passar\r\nCom Jesus no barco vou atravessar\r\n\r\nNão estou sozinho aqui nesse deserto\r\nSei que a vitória ele vai mandar\r\n\r\nIêiê iêiê chove chove chuva chuva de poder', NULL, 1),
(171, 'Deus enviou IBI', 'Por que Ele vive, posso crer no amanhã\r\n\r\nPor que Ele vive, temor não há\r\n\r\nMas eu bem sei, eu sei\r\nQue a minha vida\r\n\r\nEstás nas mãos do meu Jesus\r\nQue vivo está\r\n\r\nDeus enviou seu filho amado\r\nPara me salvar e perdoar\r\n\r\nNa cruz morreu, por meus pecados\r\n\r\nMas ressurgiu e vivo com o \r\nPai está\r\n\r\nE quando enfim, chegar a hora\r\nEm que a morte enfrentarei\r\n\r\nSem medo então, terei Vitória\r\nVerei na Glória o meu Jesus\r\n\r\nQue vivo está', NULL, 1),
(172, 'Eu sou Teu', 'Não tenho nada pra Te oferecer\r\nAs minhas mãos vazias estão\r\n\r\nPreciso tanto do Teu amor\r\nSó Tu me aceitas como eu sou\r\n\r\nToca os meus olhos\r\nEu quero te ver\r\nMostra-me o caminho\r\nPra te conhecer\r\n\r\nEu sou Teu\r\nTu és meu\r\nMinha vida está em Teu altar\r\n\r\nPra que o Senhor venha me tocar\r\nVenha me encher \r\nVenha me usar', NULL, 1),
(173, 'Encontro com Deus (Delma)', 'Nesse dia feliz,\r\nNesse Santo lugar\r\nEu marquei um encontro com Deus\r\n\r\nTeu Amor é real,\r\nTua Paz gozarei,\r\nEu marquei um encontro com Deus\r\n\r\nEu marquei um encontro com Deus\r\nNesse Santo lugar de oração\r\n\r\nSeu Amor é real,\r\nTua Paz gozarei\r\nEu marquei um encontro com Deus', NULL, 1),
(174, 'O amor ama você', 'Se o Amor ainda não encontrou lugar no seu coração\r\n\r\nSe as paixões ainda te fazem sofrer\r\n\r\nSe você muitas vezes chorou e ninguem te consolou\r\n\r\nSaiba que o Amor ama você\r\n\r\nCristo quer te consolar \r\nSua vida vai mudar\r\n\r\nE o Amor você vai encontrar\r\n\r\nSe as pessoas não entendem você\r\nCristo vai te entender\r\n\r\nSe não consegues mais caminhar\r\nCristo vai te ajudar\r\n\r\nSó tente entender\r\nQue o Amor ama você\r\n\r\nEntregue sua vida pra Deus\r\nEle vai te ajudar \r\n\r\nCom seu infinito Amor\r\nE o seu abraço de Pai\r\n\r\nAbra o teu coração e se alegre com a Salvação', NULL, 1),
(175, 'Eu vim buscar uma benção', 'Eu vim buscar uma benção\r\nJesus tem benção para dar\r\n\r\nVocê veio buscar uma benção\r\nEspera Jesus vai te dar\r\n\r\nLevante as mãos para cima\r\nReceba um raio de Luz\r\n\r\nE cante comigo Aleluia\r\nO Dono da benção é Jesus', NULL, 1),
(176, 'Fala comigo (Delma)', 'Em tuas mãos Senhor\r\nEu me coloco agora\r\n\r\nFala comigo \r\nNesta hora,\r\nEm que meu coração \r\nAnseia ouvir tua voz\r\n\r\nFala comigo agora, Senhor', NULL, 1),
(177, 'Vem esta é a hora', 'Vem esta é a hora da adoração\r\n\r\nVem dar a Ele o seu coração\r\n\r\nVem assim como estás para adorar\r\n\r\nVem assim como estás diante do Pai\r\nVem\r\n\r\nToda língua confessará ao Senhor\r\nTodo joelho se dobrará\r\n\r\nMas aquele que a Ti escolher o Tesouro maior terá\r\n\r\nToda língua confessará ao Senhor\r\nTodo joelho se dobrará\r\n\r\nMas aquele que a Ti escolher o Tesouro maior terá\r\n\r\nVem', NULL, 1),
(178, 'Sobre as águas', 'Se o sol se por\r\nE a noite chegar\r\nTu és quem me guia\r\n\r\nSe a tempestade me alcançar\r\nTu és meu abrigo\r\n\r\nSe o mar me submergir\r\nA tua mão\r\nMe traz a tona pra respirar\r\n\r\nE me faz andar\r\nSobre as águas\r\n\r\nTu és o Deus da minha salvação\r\nÉs o meu dono minha paixão\r\nMinha canção e o meu louvor\r\n\r\nAleluia, Aleluia', NULL, 1),
(179, 'Aguas Profundas', 'Vem sobre nós como a chuva\r\nFaça as águas subirem neste lugar\r\n\r\nLibera o Teu rio sem medidas\r\nVem Tuas águas agitar\r\n\r\nPois eu quero nadar\r\nQuero nadar no Teu rio\r\nQuero beber. Quero beber de Tuas águas\r\n\r\nOh! Oh! Oh! Oh!\r\n\r\nExistem águas que dão nos artelhos\r\nExistem águas que dão nos joelhos\r\n\r\nExistem águas que dão nos lombos\r\nMas tem águas mais fundas, eu sei!', NULL, 1),
(180, 'Canto pra Ti', 'Prepare o coração venha celebrar\r\nDeixe a tristeza lá fora\r\n\r\nSai do teu lugar abrace teu irmão\r\nVamos começar a festa\r\n\r\nHoje é dia de celebração\r\nLiberdade e comunhão\r\n\r\nNão se prenda erga a voz para louvar\r\nE este som que sairá daqui\r\n\r\nSe unirá ao som do céu\r\nE a glória encherá este lugar\r\n\r\nEu canto para Ti Senhor\r\nE adoro estar perto de Ti\r\nLevanto minhas mãos aos Céus\r\nPara louvar Teu nome\r\n\r\nEu canto para Ti Senhor\r\nE adoro estar perto de Ti\r\nAo mundo inteiro vou dizer\r\nQue te amo', 'Davi sacer', 1),
(181, 'Deus do Impossivel', 'Quando tudo diz que não\r\nSua voz me encoraja a prosseguir\r\nQuando tudo diz que não\r\nOu parece que o mar não vai se abrir\r\n\r\nEu sei que não estou só\r\nE o que dizem sobre mim\r\nNão pode se frustrar\r\n\r\nVenha em meu favor\r\nE cumpra em mim teu querer\r\n\r\nDeus do impossível\r\nNão desistiu de mim\r\nSua destra me sustenta\r\nE me faz prevalecer\r\n\r\nDeus do impossível\r\nDeus do impossível\r\nDeus do impossível\r\n\r\nE não desiste de um filho seu\r\nO Deus do Impossível', NULL, 1),
(182, 'Deus de aliança', 'Se os teus olhos \r\nSempre atentos permanecem em mim\r\n\r\nE os teus ouvidos\r\nEstão sensiveis para ouvir o meu clamor\r\n\r\nPosso até chorar\r\nMas a alegria vem de manhã\r\n\r\nÉs Deus de perto e não de longe\r\nNunca mudaste, Tu és fiel\r\n\r\nÉ Deus de aliança, és Deus de promessa\r\nDeus que não é homem para mentir\r\n\r\nTudo pode passar, tudo pode mudar, mas tua palavra vai se cumprir', NULL, 1),
(183, 'Meu respirar', 'Este é meu respirar\r\nEste é meu respirar\r\nTeu Santo Espírito,\r\nVivendo em mim\r\n\r\nE este é o meu pão\r\nE este é o meu pão\r\nTua vontade, \r\nFeita em mim\r\n\r\nE eu, \r\nEu nada sou sem Ti\r\n\r\nE eu,\r\nPerdido estou sem TI', NULL, 1),
(184, 'Ele continua sendo Bom', 'Um dia a gente aprende a confiar\r\nEm um Deus que faz milagres\r\nQue ouve a nossa oração\r\nQue se faz presente aqui\r\n\r\nUm dia a gente aprende a dar\r\nUm passo só de cada vez\r\nMas sem duvidar, mas sem duvidar\r\n\r\nQue ele continua sendo bom\r\nEle continua sendo Deus\r\nEle continua sendo bom\r\nEle continua sendo Deus\r\n\r\nEntão chega o dia de viver\r\nTudo o que se aprendeu\r\nE a enxergar até o que não se pode ver\r\nChega o dia de entender\r\nAté ouvir o não de Deus\r\nMas sem duvidar, mas sem duvidar\r\n\r\nQue ele continua\r\nQue ele continua\r\nEle continua sendo bom\r\nEle continua sendo Deus', 'Baruk', 1),
(185, 'atrai o meu coração', 'Tu és minha vida,Jesus\r\nÉs meu amigo\r\n\r\nE a tua vontade,doce Espírito\r\nMeu alimento\r\n\r\nSem ti não há valor em mim\r\nSou como um vaso de barro\r\nPronto a ser quebrado\r\nPara ser o que queres de mim\r\n\r\nA tua presença\r\nÉ tudo que eu preciso\r\nA tua presença\r\nÉ o meu maior valor\r\n\r\nAtrai o meu coração \r\nAtrai o meu coração\r\nÉs tudo o que eu quero\r\n\r\nAtrai o meu coração\r\nAtrai o meu coração\r\nEu posso te tocar\r\n\r\nLink: http://www.vagalume.com.br/filhos-do-homem/atrai-o-meu-coracao.html#ixzz3uKjys0WU', NULL, 1),
(186, 'Oceanos ', 'Tua voz me chama sobre as águas\r\nOnde os meus pés podem falhar\r\n\r\nE ali te encontro no mistério\r\nEm meio ao mar, confiarei\r\n\r\nAo teu nome clamarei\r\nE além das ondas olharei\r\n\r\nSe o mar crescer\r\nSomente em ti descansarei\r\nPois eu sou teu e Tu És meu\r\n\r\nTua graça cobre os meus temores\r\nTua forte mão me guiará\r\n\r\nSe estou cercado pelo medo\r\nTu és fiel, nunca vais falhar\r\n\r\nEspírito guia-me além dos meus limites\r\n\r\ne me leve sobre as águas, \r\nPor onde quer que chames.\r\n\r\nMe conduz ao mais profundo que eu puder\r\n\r\nFortaleça minha fé,\r\nNa presença do meu Senhor!', NULL, 1),
(187, 'Há um lugar', 'Há um lugar de descanso em Ti\r\nHá um lugar de refrigério em Ti\r\nHá um lugar onde a verdade reina\r\nEsse lugar é no Senhor\r\n\r\nHá um lugar onde as pessoas não me influenciam\r\nHá um lugar onde eu ouço teu Espírito\r\nHá um lugar de vitória em meio à guerra\r\nEsse lugar é no Senhor\r\n\r\nEsse lugar é no Senhor\r\nEsse lugar é no Senhor\r\nEsse lugar é no Senhor\r\nEsse lugar é no Senhor\r\n\r\nHá um lugar onde a inconstância não me domina\r\nHá um lugar onde minha fé é fortalecida\r\nHá um lugar onde a paz é quem governa\r\nEsse lugar é no Senhor\r\n\r\nHá um lugar onde os sonhos não se abortam\r\nHá um lugar onde o temor não me enrijece\r\nHá um lugar que quando se perde é que se ganha\r\nEsse lugar é no Senhor\r\n\r\nJesus\r\nTu és tudo o que eu preciso, Jesus!\r\nEu te preciso', NULL, 1),
(188, 'Quero ser como criança', 'Quero ser como criança\r\nTe amar pelo que és\r\n\r\nVoltar à inocência\r\nE acreditar em Ti\r\n\r\nMas às vezes sou levado\r\nPela vontade de crescer\r\n\r\nTorno-me independente\r\nE deixo de simplesmente crer\r\n\r\nQuero ser como criança\r\nTe amar pelo que és\r\n\r\nVoltar à inocência\r\nE acreditar em Ti\r\n\r\nMas às vezes sou levado\r\nPela vontade de crescer\r\n\r\nTorno-me independente\r\nE deixo de simplesmente crer\r\n\r\nNão posso viver longe do teu amor, Senhor\r\n\r\nNão posso viver longe do teu afago, Senhor\r\n\r\nNão posso viver longe do teu abraço, Senhor\r\n\r\nAbraça-me,\r\nAbraça-me,\r\nAbraça-me,\r\nCom Teus braços de amor', NULL, 1),
(189, 'me derramar', 'Eis-me aqui outra vez\r\nDiante de ti abro meu coração\r\n\r\nMeu clamor tu escutas\r\nE fazes cair as barreiras em mim\r\n\r\nÉs fiel senhor e dizes\r\nPalavras de amor e esperança sem fim\r\n\r\nAo sentir teu toque\r\nPor tua bondade libertas meu ser\r\n\r\nNo calor desse lugar\r\nEu venho...\r\n\r\nMe derramar... \r\nDizer que te amo\r\n\r\nMe derramar... \r\nDizer te preciso\r\n\r\nMe derramar... \r\nDizer que sou grato\r\n\r\nMe derramar... \r\nDizer que és formoso', 'vineyard', 1),
(190, 'me derramr', 'Eis-me aqui outra vez\r\nDiante de ti abro meu coração\r\nMeu clamor tu escutas\r\nE fazes cair as barreiras em mim\r\nÉs fiel senhor e dizes\r\nPalavras de amor e esperança sem fim\r\nAo sentir teu toque\r\nPor tua bondade libertas meu ser\r\nNo calor desse lugar\r\nEu venho...\r\n\r\nMe derramar... Dizer que te amo\r\nMe derramar... Dizer te preciso\r\nMe derramar... Dizer que sou grato\r\nMe derramar... Dizer que és formoso', 'vineyard', 1);
INSERT INTO `songs` (`id`, `title`, `lyric`, `Author`, `statusSongs`) VALUES
(191, 'reino de amor', 'Que o Teu Reino  se manifeste \r\nEm nós,  no meio de nós\r\n \r\nTua vontade  seja feita \r\nEm nós,  no meio de nós \r\n\r\nVenha o Teu Reino, Senhor \r\nVenha o Teu Reino, Senhor \r\n\r\nReino de amor, justiça e paz \r\nE alegria no Espírito \r\n\r\nReino de amor, justiça e paz \r\nE alegria no Espírito', 'juliano nogueira', 1),
(192, 'Uma Nova Historia', 'Sai de tua tenda,\r\nOh filho meu.\r\n\r\nTe mostrarei \r\nas estrelas do céu.\r\n\r\nSai de tua tenda ,\r\nOh filho meu.\r\n\r\nTe mostrarei \r\na areia do mar.\r\n\r\nSerá que podes contar?\r\nSerá que podes imaginar?\r\n\r\nTudo aquilo que sonhei para ti, filho meu?\r\n\r\nO que minhas mãos fizeram para ti, filho meu?\r\n\r\nMinha benção será sobre ti\r\n\r\nUma nova história Deus tem pra mim\r\n\r\nUm novo tempo Deus tem pra mim\r\n\r\nTudo aquilo que perdido foi\r\n\r\nOuvirei de sua boca, te abençoarei', 'fernandinho', 1),
(193, 'Jesus, Filho de Deus', 'Deixou os céus pra me encontrar\r\nAqui não é o Seu lugar\r\nUm amor assim, o mundo não conheceu\r\n\r\nNaquela cruz Se entregou\r\nO Teu perdão me alcançou\r\nUm amor assim, o mundo não conheceu\r\n\r\nNo altar de adoração\r\nSeja sempre exaltado\r\nJesus, Filho de Deus\r\n\r\nDeixou a Sua glória\r\nMorreu em meu lugar\r\nJesus, Filho de Deus\r\n\r\nTu És Jesus, Filho de Deus\r\n\r\nLevou sobre si pecado e dor\r\nVenceu a morte e ressuscitou\r\nUm amor assim, o mundo não conheceu\r\n\r\nSeja exaltado, engrandecido\r\nSeu nome é Santo\r\nPoderoso É\r\n\r\nE não há nada que apague o Seu amor\r\nA cruz me libertou (4x)', 'Fernandinho', 1),
(194, 'Muda minha vida', 'Estou aqui na tua casa\r\nPreciso ouvir tua voz\r\nAos seus pés me lanço agora\r\nEu sei que tu podes curar\r\n\r\nEu trago a cicatriz de alguém que tanto errou\r\nMas tentou acertar\r\nEu tentei acertar\r\n\r\nSei que hoje é meu dia, então decidi\r\nQue eu não vou mais chorar ah\r\n\r\nMuda minha vida\r\nMuda minha sorte\r\nPreciso de uma chance, do teu favor\r\n\r\nTu tens o remédio pra minha alma\r\nPreciso de um milagre, hoje senhor!\r\n\r\nNão vou sair daqui\r\nEnquanto não me abençoar\r\n\r\nMeu Deus, Deus meu\r\nEu vou te tocar\r\nCuste o que custar', 'Alex e Alex', 1),
(195, 'Eu creio no poder', 'Eu creio no poder \r\ndos joelhos que se dobram\r\nEu creio no poder da oração\r\n\r\nEu creio no poder \r\ndas mãos que se levantam\r\nEu creio no poder da oração\r\n\r\nVou levar meus problemas pra Deus\r\nEntregar meus problemas pra Deus\r\n\r\nAbençoar minha família, \r\nminha casa, meus irmãos,\r\n\r\nPois eu creio no poder da oração\r\nEu creio no poder da oração\r\n\r\nEu creio no poder \r\nda brasa viva no altar, \r\nincenso misturado a oração\r\n\r\nQuando o anjo oferecer, \r\na trombeta vai tocar\r\nDeus vai mover os céus, \r\na terra e o mar', 'Alda Célia', 1),
(196, 'Buscai primeiro o Reino', 'Buscai primeiro o Reino de Deus\nE a sua justiça\nE as demais coisas vós serão acrescentado\n\nAleluia! Aleluia!\n\nNão só de pão o homem viverá,\nMas de toda palavra\nQue procede da boca de Deus\n\nAleluia! Aleluia!', NULL, 1),
(198, 'Nesta noite Feliz', 'Nesta noite feliz \r\nnesse Santo lugar\r\nEu marquei um \r\nencontro com Deus\r\n\r\nSeu amor é real\r\nSua paz gozarei\r\nEu marquei um \r\nencontro com Deus\r\n\r\nEu marquei um encontro com Deus\r\nNeste Santo lugar de oração\r\n\r\nSeu amor é real\r\nSua paz gozarei \r\nEu marquei um \r\nencontro com Deus', NULL, 1),
(199, 'Minh''alma engrandece ao Senhor', 'Minh''alma \r\nengrandece ao Senhor\r\nMeu espírito se alegra\r\nEm Deus meu Salvador.\r\n\r\nPois com poder tem\r\nfeito grandes coisas\r\nE com misericórdia\r\nDemonstrado amor.\r\n\r\nSanto, Santo\r\nSanto és Senhor.\r\n\r\nTem enchido nossas lâmpadas\r\nCom o óleo do Espírito\r\nTem feito sua vide florescer.', 'Asaph Borba', 1),
(200, 'Vencendo vem Jesus', 'Já refulge a glória eterna \r\nde Jesus, o Rei dos reis\r\n\r\nBreve os reinos deste mundo \r\nseguirão as suas leis!\r\n\r\nOs sinais da sua vinda \r\nmais se mostram cada vez\r\nVencendo vem Jesus!\r\n\r\nGlória, glória! Aleluia!\r\nGlória, glória! Aleluia!\r\nGlória, glória! Aleluia!\r\nVencendo vem Jesus!\r\n\r\nO clarim que chama os crentes \r\nà batalha, já soou\r\n\r\nCristo, à frente do seu povo, \r\nmultidões já conquistou\r\n\r\nO inimigo, em retirada, \r\nseu furor ja demonstrou\r\nVencendo vem Jesus!\r\n\r\nGlória, glória! Aleluia!\r\nGlória, glória! Aleluia!\r\nGlória, glória! Aleluia!\r\nVencendo vem Jesus!\r\n\r\nEis que em glória refulgente \r\nsobre as nuvens descerá\r\n\r\nE as nações e os reis da terra\r\ncom poder governará\r\nSim, em paz e santidade \r\ntoda a terra regerá\r\nVencendo vem Jesus!\r\n\r\nGlória, glória! Aleluia!\r\nGlória, glória! Aleluia!\r\nGlória, glória! Aleluia!\r\nVencendo vem Jesus!\r\n\r\nE por fim entronizado \r\nas nações há de julgar\r\n\r\nTodos, grandes e pequenos, \r\no juiz hão de encarar\r\n\r\nE os remidos triunfantes, \r\nlá no céu irão cantar\r\nVenceu o rei Jesus!\r\n\r\nGlória, glória! Aleluia!\r\nGlória, glória! Aleluia!\r\nGlória, glória! Aleluia\r\nVenceu o rei Jesus', 'Harpa', 1),
(201, 'Rude Cruz', 'Rude cruz se erigiu\r\nDela o dia fugiu\r\nComo emblema de vergonha e dor\r\n\r\nMas contemplo esta cruz\r\nPorque nela Jesus\r\nDeu a vida por mim pecador\r\n\r\nSim eu amo a mensagem da cruz\r\nAté morrer eu a vou proclamar\r\n\r\nLevarei eu também minha cruz\r\nAté por uma coroa trocar\r\n\r\nDesde a glória dos céus\r\nO cordeiro de Deus\r\nAo calvário humilhante baixou\r\n\r\nEssa cruz tem pra mim\r\nAtrativos sem fim\r\nPorque nela Jesus me salvou\r\n\r\nSim eu amo a mensagem da cruz\r\nAté morrer eu a vou proclamar\r\n\r\nLevarei eu também minha cruz\r\nAté por uma coroa trocar\r\n\r\nNesta cruz padeceu\r\nE por mim já morreu\r\nMeu Jesus para dar-me o perdão\r\n\r\nE eu me alegro na cruz\r\nDela vem graça e luz\r\nPara minha santificação\r\n\r\nSim eu amo a mensagem da cruz\r\nAté morrer eu a vou proclamar\r\n\r\nLevarei eu também minha cruz\r\nAté por uma coroa trocar\r\n\r\nE eu aqui com Jesus\r\nA vergonha da cruz\r\nQuero sempre levar e sofrer\r\n\r\nCristo vem me buscar\r\nE com Ele no lar\r\nUma parte da glória hei de ter\r\n\r\nSim eu amo a mensagem da cruz\r\nAté morrer eu a vou proclamar\r\n\r\nLevarei eu também minha cruz\r\nAté por uma coroa trocar', NULL, 1),
(202, 'Teus Olhos ', 'Teus olhos revelam que eu,\r\nNada posso esconder \r\n\r\nQue não sou nada sem Ti,\r\nOh fiel Senhor.\r\n\r\nTudo sabe de mim, \r\nQuando sondas meu coração\r\n\r\nTudo podes ver, \r\nbem dentro de mim\r\n\r\nLeva minha vida, a uma só verdade\r\n\r\nQue quando me sondas, nada posso ocultar\r\n\r\nSei, que tua fidelidade\r\nque leva minha vida mais além,\r\n\r\nDo que eu possa imaginar\r\n\r\nSei, que não posso negar\r\nque os teus olhos sobre mim,\r\n\r\nMe enchem da tua Paz\r\n\r\nQue os teus olhos sobre mim,\r\nme enchem da tua Paz', 'Marcos Witt', 1),
(203, 'Deus Está Aqui', 'Deus está aqui, aleluia\r\n\r\nTão certo como o ar que eu respiro\r\nTão certo como amanhã que se levanta\r\n\r\nTão certo como eu te falo e podes me ouvir\r\n\r\nTu és o meu Senhor, Sei que tu podes me ouvir', 'Desconhecido', 1),
(204, 'Ressuscita-me', 'Mestre eu preciso de um milagre\r\nTransforma minha vida, meu estado\r\n\r\nFaz tempo que eu não vejo a luz do dia\r\nEstão tentando sepultar minha alegria\r\nTentando ver meus sonhos cancelados\r\n\r\nLázaro ouviu a Sua voz, quando aquela pedra removeu\r\nDepois de quatro dias ele reviveu\r\n\r\nMestre não há outro que possa fazer\r\nAquilo que só o Teu nome tem todo poder\r\nEu preciso tanto de um milagre\r\n\r\nRemove minha pedra, me chama pelo nome\r\nMuda minha história, ressuscita os meus sonhos\r\n\r\nTransforma minha vida, me faz um milagre\r\nMe toca nessa hora, me chama para fora\r\nRessuscita-me\r\n\r\nTu és a própria vida, a força que há em mim\r\nTu és o Filho de Deus, Que me ergues pra vencer\r\n\r\nSenhor de tudo em mim, já ouço a Tua voz\r\nMe chamando pra viver, uma história de poder!', NULL, 1),
(205, 'Deus de detalhes', 'Eu tenho um Deus que cuida de mim em cada detalhe\r\n\r\nEu tenho um Deus que já fez em mim o seu milagre\r\n\r\nLivrou-me do que eu nem cheguei a ver\r\n\r\nJá tinha um plano certo, antes de eu nascer\r\n\r\nE pra cada coisa que ia perder\r\n\r\npreparou o dobro pra me devolver\r\n\r\nAbriu cada porta que o mundo fechou\r\n\r\nEu não valia nada ele me deu valor\r\n\r\nMudou minha história e me fez alguém\r\n\r\nE me disse que o seus sonhos vão além\r\n\r\nDo que eu possa imaginar\r\n\r\nDeus está cuidando de mim\r\nnos detalhes cuida de mim\r\n\r\nSeu amor é sobrenatural,\r\nFaz com que eu me sinta especial\r\n\r\nColocou limite nas águas do mar\r\n\r\nDeu altura certa pro pássaro voar\r\n\r\nFez o céu e pôs a estrela a brilhar\r\n\r\nPra gente olhar e admirar\r\n\r\nFez abelha pra gente provar do mel\r\n\r\nPintou de azul o nosso lindo céu\r\n\r\nQuando o sol vai a lua logo vem\r\n\r\nE nos fazer lembrar que mais um dia o Senhor nos fez bem\r\n\r\nDeus está cuidando, cuidando de tudo\r\n\r\ne o meu trabalho é esperar\r\ne descansar no seu amor\r\n\r\nPois Deus está cuidando, cuidado de tudo', NULL, 1),
(206, 'Aos teus pés', 'Meu Jesus me leva pra perto de Ti\r\nMe leva onde eu posso ouvir a tua voz\r\n\r\nPois quando eu escutar de todo coração\r\nObedecerei\r\n\r\nCura meu coração faz em mim Tua vontade\r\n\r\nPois só na tua vontade eu posso me completar\r\nPosso me completar...\r\n\r\nMe leva onde eu possa ouvir tua voz\r\n\r\nAos Teus pés', NULL, 1),
(207, 'Quero que Valorize', 'Quero que valorize o que você tem\r\n\r\nVocê é um ser, você é alguém\r\nTão importante para Deus\r\n\r\nNada de ficar sofrendo angústia e dor\r\nNeste seu complexo inferior\r\n\r\nDizendo ás vezes que não é ninguém\r\nEu venho falar do valor que você que tem\r\n\r\nEle está em você, o Espírito Santo se move em você\r\nAté com gemidos inexpremíveis\r\n\r\nAi você pode então perceber\r\nQue pra Ele há algo importante em você\r\n\r\nPor isso levante e cante, exalte ao Senhor\r\n\r\nVocê tem valor, o Espirito Santo se move em você', NULL, 1),
(208, 'Jesus Cristo Mudou meu viver', 'Jesus Cristo mudou meu viver \r\nJesus Cristo mudou meu viver \r\n\r\nÉ a luz que ilumina meu ser \r\nSim Jesus Cristo mudou, \r\nmeu viver!\r\n\r\nO amor só conhecia em canções \r\nQue falavam de ilusões \r\n\r\nMas tudo agora é diferente \r\nCristo fala a toda gente \r\nPois Cristo deu o seu amor!\r\n\r\nDiferente hoje é o meu coração \r\nDiferente hoje é o meu coração \r\n\r\nCristo deu-me paz e perdão \r\nSim diferente hoje é o meu, \r\ncoração!\r\n\r\nO amor só conhecia em canções \r\nQue falavam de ilusões \r\n\r\nMas tudo agora é diferente \r\nCristo fala a toda gente \r\nPois Cristo deu o seu amor!', 'Aline Barros', 1),
(209, 'Te exalto', '\r\nGrande Deus Consolador, \r\nPríncipe da Paz\r\n\r\nForte Deus, vencedor, \r\nsempre vive e sempre reinará\r\n\r\nEu te exalto, eu te exalto\r\nÉs tudo em minha vida, \r\nprecioso és para mim\r\n\r\nEu te exalto, eu te exalto\r\nTe Exalto a Cristo\r\nTe Exalto a Cristo\r\nMeu Rei', NULL, 1),
(210, 'Bom estarmos aqui', 'Bom estarmos aqui \r\nlouvando a Deus\r\n\r\nPodendo exaltar \r\nteu Santo nome\r\n\r\nTempo para isso, \r\ntempo para louvar-mos a Deus\r\n\r\nNum só amor, \r\nNum só Espirito\r\n\r\nDeus, venha nos abençoar\r\nE que essa união \r\nnunca falte para nós', NULL, 1),
(211, 'Celebrai a Cristo', 'Celebrai a Cristo, celebrai!\r\nCelebrai a Cristo, celebrai!\r\n\r\nRessuscitou,\r\nRessuscitou,\r\nE hoje vive,\r\npara sempre.\r\n\r\nVamos Celebrar!  (Hey!)\r\nVamos Celebrar!  (Ooooh!)\r\nVamos Celebrar,\r\nRessuscitou o Senhor!', NULL, 1),
(212, '1 - Avisos', 'Tema da Mensagem\r\nSem Perseverança \r\nNão Há Sucesso', NULL, 1),
(214, 'Ouve-se um Jubilo', 'Ouve-se o júbilo de todos os povos\r\nOs reis se dobraram ao Senhor\r\n\r\nOuve-se um brado de vitória\r\nO dia do senhor chegou!!!\r\n\r\nOuve-se em todos os povos que um novo rei surgiu\r\n\r\nImpérios reconhecem que a sua destra reinará\r\n\r\nLeão de Judá...\r\nLeão de Judá...\r\nLeão de Judá...\r\nPrevaleceu...\r\n\r\nOuve-se o júbilo de todos os povos\r\nOs reis se dobraram ao Senhor\r\n\r\nOuve-se um brado de vitória\r\nO dia do senhor chegou!!!\r\n\r\nOuve-se em todos os povos que um novo rei surgiu\r\n\r\nImpérios reconhecem que a sua destra reinará\r\n\r\nLeão de Judá...\r\nLeão de Judá...\r\nLeão de Judá...\r\nPrevaleceu...\r\n\r\nE os povos verão e virão a Sião aprender suas leis\r\n\r\nPois a sua Justiça\r\nGovernará\r\n\r\nLeão de Judá...\r\nLeão de Judá...\r\nLeão de Judá...\r\nPrevaleceu...\r\n\r\nLeão de Judá!', NULL, 1),
(215, 'Pisaduras', 'Por suas pisaduras fomos sarados\r\n\r\nNosso coração está incendiado \r\n\r\nHá uma multidão de filhos apaixonados. Clamando venha o teu Reino\r\n\r\nGrande é o Senhor\r\nPoderoso. Digno de todo o meu amor\r\n\r\nTu és a porta para a vida eterna. Tu és o caminho aonde vou\r\n\r\nQuem deu crédito. Quem ouviu\r\n\r\nNenhuma aparência nos atraiu\r\n\r\nComo um de quem esconde o rosto Ele foi levantado\r\n\r\nToda a via o Senhor agradou. Por Ele fazer enfermar \r\n\r\nLevou o pecado de muitos e com os mal feitores foi contado\r\n\r\nE foi subindo como um renovo\r\nComo raiz de uma terra seca\r\n\r\nVerá o fruto do seu trabalho\r\nE se alegrará', NULL, 1),
(216, 'Mar de vidro', 'Quando Deus enviar alguém que irá\r\nOu ouvirá sua voz quando Ele chamar?\r\n\r\nSerá que essas são as mãos que Ele vai usar?\r\n\r\nMeus olhos viram quando por aqui \r\nEle passou\r\n\r\nSerá que o meu coração ardeu quando Ele falou?\r\n\r\nReconhecendo que o Mestre se aproximou\r\n\r\nMesmo que eu não te ouça,\r\nPois o mundo faz de tudo pra te calar\r\n\r\nTu renovas minhas forças, \r\nMe põe de pé e diz pra mim que Tu me aceitas\r\n\r\nSou teu, vou preparar o teu lugar\r\nVem e reina\r\n\r\nTua glória chega a me cegar\r\nComo uma nuvem que me cerca\r\n\r\nSob o vidro deste mar, um dia, eu cantarei\r\n"Jesus é Rei em toda terra!"\r\n\r\nMesmo que eu não Te veja,\r\nSei que estás aqui dentro de mim.\r\n\r\nTodo o meu ser deseja \r\nUm toque a mais do teu amor,\r\nMe incendeia \r\n\r\nSou Teu, vou preparar o Teu lugar, \r\nVem e Reina\r\n\r\nDeus de amor, Santo,\r\nMeu Jesus, vive. Ele é tudo pra mim\r\n\r\nEle é tudo pra mim...\r\n\r\nMeu Deus é maior\r\n\r\nO dia do Senhor virá,\r\nMinha esperança me alimenta\r\n\r\nSou Teu, vou preparar o Teu lugar, \r\nVem e Reina\r\n\r\nDeus de amor, Santo,\r\nMeu Jesus, vive. Ele é tudo pra mim', NULL, 1),
(217, 'Na Beleza da criação', 'Na beleza da criação\r\nEu consigo ver, o amor do Criador\r\n\r\nTu és a luz, que brilha nos meus dias\r\n\r\nTu és a brisa, que enche os meus dias\r\n\r\nTu és o aroma, que eu sinto todos os dias\r\n\r\nTu és a boa chuva, que enche os meus dias\r\n\r\nTu és, o motivo\r\nTu és, a vida dos meus dias\r\n\r\nTu és a luz, que brilha nos meus dias\r\n\r\nTu és a brisa, que enche os meus dias\r\n\r\nTu és o aroma, que eu sinto todos os dias\r\n\r\nTu és a boa...chuva\r\n\r\nTu és, o motivo\r\nTu és, a vida dos meus dias\r\nTu és a vida dos meus dias', NULL, 1),
(218, 'Autoridade e Poder', 'Os que confiam no Senhor\r\nSão como os montes de Sião\r\n\r\nQue não se abalam\r\nMas permanecem para sempre\r\n\r\nComo em volta de Jerusalém\r\nEstão os montes\r\nAssim é o Senhor em volta do seu povo\r\n\r\nAutoridade e Poder\r\nO dominío em suas mãos\r\nPois o Senhor é Rei, o Senhor Rei dos povos\r\n\r\nCale-se diante dele a terra\r\nDobre os joelhos ergam as mãos\r\n\r\nPois o Senhor é Deus\r\nO Senhor é Rei dos povos\r\n\r\nNão dá, sem Jesus não dá', NULL, 1),
(219, 'Não há Deus maior', 'Não há Deus maior\r\nNão há Deus melhor\r\n\r\nNão há Deus tão grande\r\nComo o nosso Deus\r\n\r\nCriou o céus, criou a terra\r\nCriou o sol e as estrelas\r\n\r\nTudo Ele fez, tudo criou\r\nTudo formou\r\n\r\nPara o seu louvor\r\nPara o seu louvor\r\n\r\nPara o seu, para o seu\r\nPara o seu louvor', NULL, 1),
(220, 'Só quero ver você', 'Sim quero sentir \r\nSuas afeições por mim\r\n\r\nNão preciso me esconder\r\nOh não, não preciso mais temer\r\n\r\nO véu já se rasgou\r\nO caminho você trilhou\r\n\r\nPara me aproximar de Ti\r\nE poder te contemplar\r\n\r\nQuando eu te vejo\r\nEu percebo algo diferente em mim\r\n\r\nQuando eu te contemplo\r\nEu me torno cada vez mais\r\nsemelhante a Ti, Jesus\r\n\r\nSó quero, só quero ver Você\r\n\r\nO que é que os anjos vêem\r\nQue os fazem se prostrar\r\n\r\nO que é que os anjos vêem \r\nque os fazem cantar Santo\r\n\r\nQuero olhar a face do meu Criador\r\nQuero olhar a face dAquele que \r\nPrimeiro me amou', 'Laura Souguellis ', 1),
(221, 'Nada mais Satisfaz', 'Senhor, \r\nTemos tanta fome de Ti\r\nTemos  tanta sede de Ti\r\n\r\nNada mais satisfaz \r\nMinh''alma \r\nSó Jesus', 'Jeremiah Bowser', 1),
(222, 'Tudo que eu quero', 'O meu desejo\r\nO que preciso\r\nMeu coração anseia por Ti\r\n\r\nE vem cantar comigo Senhor\r\nE vem dançar comigo \r\nMeu Amor\r\n\r\nPois Tu és tudo que quero\r\n\r\nTudo que eu quero \r\nTudo que eu preciso\r\nTu és Tudo pra mim', 'Nic & Rachel Billman', 1),
(223, 'Ultima Chance', 'Uma chance igual a essa\r\nTalvez eu não tenha mais\r\n\r\nQuero estar em Tua presença,\r\nNem que seja a última vez...\r\n\r\nSe tiver que gritar, eu gritarei\r\nSe tiver que chorar, eu chorarei\r\n\r\nSe tiver que humilhar o meu espírito\r\nAssim o farei, me dá mais uma chance,\r\n\r\nEu quero nascer do Teu Espírito\r\nEu quero matar a minha carne\r\n\r\nFazer Tua vontade, doce Espírito\r\nQue a minha vida seja Tua vida,\r\nJesus...', NULL, 1),
(224, 'Milagres', 'Posso crer \r\nQue em minha vida \r\nUm milagre vai acontecer\r\n\r\nPosso ver \r\nAs promessas \r\nSendo liberadas sobre mim\r\n\r\nPosso crer \r\nQue em minha vida \r\nUm milagre vai acontecer\r\n\r\nPosso ver \r\nAs promessas \r\nSendo liberadas sobre mim\r\n\r\nHoje o meu milagre vai chegar,\r\nEu vou crer não vou duvidar\r\n\r\nO preço que foi pago ali na cruz, \r\nMe dá vitória nessa hora\r\n\r\nHoje o meu milagre vai chegar,\r\nEu vou crer não vou duvidar\r\n\r\nO preço que foi pago ali na cruz, \r\nMe dá vitória nessa hora\r\n\r\nSua morte, sua cruz,\r\nSeu sangue derramado no calvário\r\n\r\nEstá selado, foi consumado,\r\nE vivo hoje livre do pecado\r\n\r\nVivo as promessas de milagres\r\n\r\nHoje o meu milagre vai chegar,\r\nEu vou crer não vou duvidar\r\n\r\nO preço que foi pago ali na cruz, \r\nMe dá vitória nessa hora\r\n\r\nHoje o meu milagre vai chegar,\r\nEu vou crer não vou duvidar\r\n\r\nO preço que foi pago ali na cruz, \r\nMe dá vitória nessa hora', NULL, 1),
(225, 'Profetizo', 'O ambiente preparado está\r\nA glória já encheu esse lugar\r\nO Espírito de Deus soprando está aqui\r\n\r\nEu sinto o derramar de uma unção\r\nEnquanto levantamos nossas mãos\r\n\r\nEu tenho uma palavra, não vou me calar\r\nEu vou profetizar\r\n\r\nProfetizo uma palavra de cura sobre a sua dor\r\nProfetizo um rio de vida no lugar que secou\r\n\r\nEu declaro que hoje o seu choro se acabou\r\nAssim diz o Senhor, assim diz o Senhor\r\n\r\nEu sinto o derramar de uma unção\r\nEnquanto levantamos nossas mãos\r\n\r\nEu tenho uma palavra, não vou me calar\r\nEu vou profetizar\r\n\r\nProfetizo uma palavra de cura sobre a sua dor\r\nProfetizo um rio de vida no lugar que secou\r\n\r\nEu declaro que hoje o seu choro se acabou\r\nAssim diz o Senhor, assim diz o Senhor\r\n\r\nProfetizo, profetizo, profetizo\r\nProfetizo, profetizo, profetizo\r\nA benção do Senhor', NULL, 1),
(226, 'Restitui', 'Os planos que foram embora\r\nO sonho que se perdeu\r\n\r\nO que era festa e agora\r\nÉ luto do que já morreu\r\n\r\nNão podes pensar que este é o teu fim\r\n\r\nNão é o que Deus planejou\r\n\r\nLevante-se do chão, erga um clamor\r\n\r\nRestitui, eu quero de volta o que é meu\r\n\r\nSara-me e põe teu azeite em minha dor\r\n\r\nRestitui e leva-me às águas tranquilas\r\n\r\nLava-me e refrigera minh''alma\r\nRestitui\r\n\r\nE o tempo que roubado foi\r\nNão poderá se comparar\r\n\r\nA tudo aquilo que o Senhor\r\nTem preparado ao que clamar\r\n\r\nCreia por que o poder de um clamor pode ressuscitar', NULL, 1),
(227, 'Se compreendesses o dom de Deus ', 'Se compreendesses como te amo\r\n\r\nDeixarias de viver sem amor\r\n\r\nSe compreendesses como te amo\r\n\r\nDeixarias de mendigar qualquer amor \r\n\r\nSe compreendesses...como te amo, como te amo\r\nSerias mais feliz\r\n\r\nSe compreendesses como te busco, como te busco\r\nEscutarias mais minha voz', NULL, 1),
(228, 'Frutos do Espírito', 'AMOR              ALEGRIA\r\nPAZ                  PACIÊNCIA\r\nDELICADEZA  BONDADE\r\nFIDELIDADE    HUMILDADE\r\nDOMÍNIO PRÓPRIO', NULL, 1),
(229, 'Meu Barquinho', 'O vento balançou meu barco em alto mar\r\n\r\nO medo me cercou e quis me afogar\r\n\r\nMas então eu clamei ao filho de Davi\r\n\r\nEle me escutou, por isso estou aqui\r\n\r\nO vento Ele acalmou, o medo repreendeu\r\n\r\nQuando Ele ordenou, o mar obedeceu\r\n\r\nNão temo mais o mar, pois firme está minha fé\r\n\r\nNo meu barquinho está Jesus de Nazaré\r\n\r\nSe o medo me cercar ou se o vento soprar \r\n\r\nSeu nome eu clamarei, Ele me guardará\r\n\r\nO vento balançou meu barco em alto mar\r\n\r\nO medo me cercou e quis me afogar\r\n\r\nMas então eu clamei ao filho de Davi\r\n\r\nEle me escutou, por isso estou aqui\r\n\r\nO vento Ele acalmou, o medo repreendeu\r\n\r\nQuando Ele ordenou, o mar obedeceu\r\n\r\nNão temo mais o mar, pois firme está minha fé\r\n\r\nNo meu barquinho está Jesus de Nazaré\r\n\r\nSe o medo me cercar ou se o vento soprar \r\n\r\nSeu nome eu clamarei, Ele me socorrerá\r\n\r\nEle me socorrerá', NULL, 1),
(230, '2 - Abertura', 'Estamos felizes com sua presença!!\r\nSEJA BEM VINDO!\r\n#FamiliaConectados', NULL, 1),
(231, 'Filho Amado', 'Eis me aqui eu sou\r\nAquele que tu esquadrinhas\r\nDesde o nascimento\r\n\r\nOs meus anos escrevestes todos \r\nE me demonstras-te o teu amor\r\n\r\nNuvens cinzas se fecharam sobre mim\r\nMas ao olhar pro céu do alto eu ouvi\r\n\r\nÉs meu filho amado em que acho o meu prazer\r\n\r\nEis me aqui eu sou aquele que tu reconheces cada movimento\r\n\r\nMinhas dores Tu conheces todas e em Ti espero redenção\r\n\r\nVeio o dia mal e a Ti eu recorri\r\nMas ao olhar pro céu do alto eu ouvi\r\n\r\nÉs meu filho amado em que acho o meu prazer\r\n\r\nE eu irei por ai levando essa identidade\r\nE eu irei por sabendo ser amado de verdade\r\n\r\nE eu irei por ai sabendo que se eu venho e vou\r\nNum só lugar estou, guardado em Teu amor', NULL, 1),
(232, 'Aleluia', 'Aleluia \r\n\r\nDeus me ama\r\n\r\nJesus Cristo eu te amo\r\n\r\nAleluia', NULL, 1),
(233, 'Esperança (Pedro Spagiari)', 'Cordeiro Santo\r\nDigno de toda adorração\r\nLindo, Poderoso\r\nVocê cativou meu coração\r\n\r\nVivo está a nossa esperança\r\nE ela prometeu vir nos buscar\r\n\r\nO Espirito e a noiva dizem\r\nVenha o Leão da tribo de Judá\r\n\r\nO famoso descendente de Davi\r\nVenceu a morte e o livro abrirá\r\n\r\nNão há mais lágrimas \r\nCantem de alegria\r\nEstamos voltando ao nosso lar', NULL, 1),
(234, 'Só quero ver Você', 'Sim, quero sentir\r\nSuas afeições por mim\r\nNão preciso me esconder, oh não\r\nNão preciso mais temer\r\n\r\nO véu já se rasgou\r\nO caminho Você trilhou\r\nPara me aproximar de Ti\r\nE poder te contemplar\r\n\r\nQuando eu te vejo\r\nEu percebo algo diferente em mim\r\n\r\nQuando eu te contemplo\r\nEu me torno cada vez mais semelhante a Ti\r\nJesus\r\n\r\nSó quero, só quero ver Você\r\n\r\nO que é que os anjos veem\r\nQue os fazem se prostar?\r\nO que é que os anjos veem\r\nQue os fazem cantar: Santo!\r\n\r\nQuero olhar na face do meu Criador\r\nQuero olhar na face d''Aquele que primeiro me amou', NULL, 1),
(235, 'Te agradeço', 'Por tudo o que tens feito\r\nPor tudo que vais fazer\r\n\r\nPor tuas promessas. E tudo o que És, eu quero te agradecer com todo o meu ser\r\n\r\nTe agradeço meu Senhor\r\n\r\nTe agradeço por me libertar e salvar. Por ter morrido em meu lugar te agradeço\r\n\r\nJesus te agradeço\r\nEu te agradeço\r\nTe agradeço', NULL, 1),
(236, 'Sim, eu creio', 'Se disseres a mim\r\nLance a rede ao mar\r\nSobre tua palavra\r\nLançarei minha fé\r\n\r\nSe disseres enfim, \r\nVem e anda até mim\r\nSobre as águas do mar\r\nCertamente andarei\r\n\r\nE se acaso eu vier me afundar\r\nPercebendo a fúria do mar\r\n\r\nMesmo assim posso em Ti confiar\r\nPois sei que Tua mão se estenderá\r\n\r\nE não deixarás que eu venha\r\nDa fé naufragar\r\n\r\nSim, eu creio\r\nSim, eu creio, Senhor\r\nSim, eu creio\r\n\r\nTua palavra encontrou \r\nLugar em meu coração\r\n\r\nMesmo que as circunstâncias\r\nContrárias estão\r\n\r\nSim, eu creio\r\nSim, eu creio, Senhor\r\nSim, eu creio\r\n\r\nOs meus sonhos e planos\r\nNão foram em vão\r\n\r\nTodos os seus propósitos\r\nSe cumprirão\r\n\r\nEu creio em Ti, Senhor', NULL, 1),
(237, 'Sacia-me', 'Minh''alma anseia Teus braços\r\nMeus olhos desejam Teus olhos\r\nO que eu mais quero é Te tocar\r\n\r\nEspero por Tua presença\r\nDe jóias eu me adornei\r\nE já me vesti pra Te encontrar\r\n\r\nSem Ti eu estou num deserto\r\nSedento por Tua presença\r\nEu preciso tanto Te abraçar\r\n\r\nContigo me sinto seguro\r\nNão posso ficar um segundo\r\nSem desfrutar do Teu amor\r\nSacia-me, Senhor\r\n\r\nSacia-me, sacia-me\r\nPois tenho fome do Teu amor\r\n\r\nSacia-me, sacia-me\r\nPois tenho sede de Ti', NULL, 1),
(238, 'Mais Perto', 'Mais perto quero estar\r\nMeu Deus, de Ti\r\nAinda que seja a dor\r\nQue me una a ti,\r\n\r\nSempre hei de suplicar\r\nMais perto quero estar\r\nMais perto quero estar\r\nMeu Deus, de ti!\r\n\r\nAndando triste aqui\r\nNa solidão\r\nPaz e descanso a mim\r\nTeus braços dão\r\n\r\nNas trevas vou sonhar\r\nMais perto quero estar\r\nMais perto quero estar\r\nMeu Deus, de ti\r\n\r\nMinh''alma cantará a ti, Senhor!\r\nE em Betel alçará\r\nPadrão de amor\r\n\r\nEu sempre hei de rogar\r\nMais perto quero estar\r\nMais perto quero estar\r\nMeu Deus, de ti!\r\n\r\nE quando Cristo, enfim\r\nMe vier chamar\r\nNos céus, com serafins\r\nIrei morar\r\n\r\nEntão me alegrarei\r\nPerto de ti, meu Rei\r\nPerto de ti, meu Rei\r\nMeu Deus, de ti!', NULL, 1),
(239, 'Nada Além do Sangue', 'Teu sangue\r\nLeva-me além, a todas as alturas\r\nOnde ouço a Tua voz\r\n\r\nFala de Tua justiça pela minha vida\r\nJesus este é o Teu sangue\r\n\r\nTua cruz\r\nMostra Tua graça, fala do Amor do Pai\r\nQue prepara para nós um caminho para Ele\r\n\r\nOnde posso me achegar\r\nSomente pelo sangue\r\n\r\nQue nos lava dos pecados\r\nQue nos traz restauração\r\n\r\nNada além do sangue\r\nNada além do sangue de Jesus\r\n\r\nQue nos faz brancos como a neve\r\nAceitos como Amigos de Deus\r\n\r\nNada além do sangue\r\nNada além do sangue de Jesus\r\n\r\nTua cruz\r\nMostra Tua graça, fala do Amor do Pai\r\nQue prepara para nós um caminho para Ele\r\n\r\nOnde posso me achegar\r\nSomente pelo sangue\r\n\r\nQue nos lava dos pecados\r\nQue nos traz restauração\r\n\r\nNada além do sangue\r\nNada além do sangue de Jesus\r\n\r\nQue nos faz brancos como a neve\r\nAceitos como Amigos de Deus\r\n\r\nNada além do sangue\r\nNada além do sangue de Jesus\r\n\r\nEu sou livre!\r\nEu sou livre!\r\n\r\nNada além do sangue\r\nNada além do sangue de Jesus\r\n\r\nEu sou livre!\r\nEu sou livre!\r\n\r\nNada além do sangue\r\nNada além do sangue de Jesus\r\n\r\nAlvo mais que a neve\r\nSim, neste sangue lavado\r\nMais alvo que a neve serei\r\n\r\nAlvo mais que a neve\r\nSim, neste sangue lavado\r\nMais alvo que a neve serei\r\n\r\nAlvo mais que a neve\r\nSim, neste sangue lavado\r\nMais alvo que a neve serei\r\n\r\nAlvo mais que a neve\r\nSim, neste sangue lavado\r\nMais alvo que a neve serei\r\n\r\nEu sou livre!\r\nEu sou livre!\r\n\r\nNada além do sangue\r\nNada além do sangue de Jesus\r\n\r\nEu sou livre!\r\nEu sou livre!\r\n\r\nNada além do sangue\r\nNada além do sangue de Jesus\r\n\r\nDe Jesus\r\nDe Jesus\r\nDe Jesus', NULL, 1),
(240, 'Nada Alem do Sangue', 'Teu sangue\r\nLeva-me além, a todas as alturas\r\nOnde ouço a Tua voz\r\nFala de Tua justiça pela minha vida\r\nJesus este é o Teu sangue\r\n\r\nTua cruz\r\nMostra Tua graça, fala do Amor do Pai\r\nQue prepara para nós um caminho para Ele\r\nOnde posso me achegar\r\nSomente pelo sangue\r\n\r\nQue nos lava dos pecados\r\nQue nos traz restauração\r\nNada além do sangue\r\nNada além do sangue de Jesus\r\n\r\nQue nos faz brancos como a neve\r\nAceitos como Amigos de Deus\r\nNada além do sangue\r\nNada além do sangue de Jesus\r\n\r\nTua cruz\r\nMostra Tua graça, fala do Amor do Pai\r\nQue prepara para nós um caminho para Ele\r\nOnde posso me achegar\r\nSomente pelo sangue\r\n\r\nQue nos lava dos pecados\r\nQue nos traz restauração\r\nNada além do sangue\r\nNada além do sangue de Jesus\r\n\r\nQue nos faz brancos como a neve\r\nAceitos como Amigos de Deus\r\nNada além do sangue\r\nNada além do sangue de Jesus\r\n\r\nEu sou livre!\r\nEu sou livre!\r\nNada além do sangue\r\nNada além do sangue de Jesus\r\n\r\nEu sou livre!\r\nEu sou livre!\r\nNada além do sangue\r\nNada além do sangue de Jesus\r\n\r\nAlvo mais que a neve\r\nSim, neste sangue lavado\r\nMais alvo que a neve serei\r\n\r\nAlvo mais que a neve\r\nSim, neste sangue lavado\r\nMais alvo que a neve serei\r\n\r\nAlvo mais que a neve\r\nSim, neste sangue lavado\r\nMais alvo que a neve serei\r\n\r\nAlvo mais que a neve\r\nSim, neste sangue lavado\r\nMais alvo que a neve serei\r\n\r\nEu sou livre!\r\nEu sou livre!\r\nNada além do sangue\r\nNada além do sangue de Jesus\r\n\r\nEu sou livre!\r\nEu sou livre!\r\nNada além do sangue\r\nNada além do sangue de Jesus\r\n\r\nDe Jesus\r\nDe Jesus\r\nDe Jesus', 'Cleiton', 1),
(241, 'Te louvarei', 'Perto quero estar, junto aos teus pés\r\nPois prazer maior não há\r\n\r\nQue me render e te adorar\r\nTudo que há em mim, quero te ofertar\r\n\r\nMas, ainda é pouco eu sei\r\nSe comparado ao que ganhei\r\n\r\nNão sou apenas servo\r\nTeu amigo me tornei\r\n\r\nTe louvarei, não importam\r\nAs circunstâncias\r\nAdorarei, somente a ti Jesus\r\n\r\nTe louvarei, não importam\r\nAs circunstâncias\r\nAdorarei, somente a ti Jesus\r\n\r\nSomente a ti Jesus\r\nSomente a ti Jesus\r\nSomente a ti Jesus', 'Cleiton', 1),
(242, 'Rendido Estou', 'Toma-me, rendido estou\r\n\r\nAos pés da cruz me encontrei\r\n\r\nO que tenho te entrego, oh Deus\r\n\r\nVem limpar as minhas mãos\r\n\r\nPurificar meu coração\r\n\r\nQue eu ande em tudo que tu tens pra mim\r\n\r\nOh, oh, oh, oh\r\nEis me aqui, rendido estou\r\n\r\nOh, oh, oh, oh\r\nEu sou teu e tu és meu, Jesus\r\n\r\nMeus momentos e os dias meus\r\n\r\nMeu respirar e meu viver\r\n\r\nQue sejam todos, pra ti oh Deus\r\n\r\nOh, oh, oh, oh\r\nEis me aqui, rendido estou\r\n\r\nOh, oh, oh, oh\r\nEu sou teu e tu és meu, Jesus\r\n\r\nMinha vida dou a ti, Senhor\r\nRendido a ti, estou\r\n\r\nE pra sempre cantarei\r\nFaz em mim o teu querer\r\n\r\nOh, oh, oh, oh\r\nEis me aqui, rendido estou\r\n\r\nOh, oh, oh, oh\r\nEu sou teu e tu és meu, Jesus\r\n\r\nMinha vida dou a Ti, Senhor\r\nRendido a ti, estou\r\n\r\nE pra sempre cantarei\r\nFaz em mim o teu querer', NULL, 1),
(243, 'aviso culto no lar', 'Culto no Lar / Juliana\r\nRua Felipe Lazarine (Vila Nova) Nº184\r\nAtrás da Igreja São Sebastião\r\n19:30 Quinta-Feira', NULL, 1),
(244, 'Conquistando o Impossivel', 'Acredite é hora de vencer\r\nEssa força vem de dentro de você\r\n\r\nVocê pode até tocar o céu se crer\r\n\r\nAcredite que nenhum de nós\r\n\r\nJá nasceu com jeito pra super herói\r\n\r\nNossos sonhos, a gente é quem constrói\r\nÉ vencendo os limites, escalando as fortalezas.\r\nConquistando o impossível pela fé\r\n\r\nCampeão, vencedor, Deus da asas, faz seu voo - ooo\r\n\r\nCampeão, vencedor, essa fé que te faz imbatível te mostra o seu valor\r\n\r\nTantos recordes, você pode quebrar\r\nAs barreiras você pode ultrapassar e vencer\r\n\r\nCampeão, vencedor, Deus da asas, faz seu voo - ooo\r\n\r\nCampeão, vencedor, essa fé que te faz imbatível te mostra o seu valor', NULL, 1),
(245, 'Tuas Primicias', 'Venho a Ti oh Deus  trazer as primícias que eu preparei \r\npra Te entregar em Teu altar\r\n\r\nDe tudo que me deste  a primeira parte eu quero devolver\r\nPra ti meu Senhor  com muita alegria\r\n\r\nEu reconheço que tudo vem de Ti e sei que Tuas promessas \r\nem minha vida vão se cumprir\r\n\r\nTuas primícias oh meu Deus eu venho trazer\r\nOs primeiros frutos minha fidelidade a Ti eu separei\r\n\r\nO meu melhor para Te honrar\r\nPois em  minha vida o Senhor está\r\n\r\nTuas primícias oh meu Deus eu venho trazer\r\nOs primeiros frutos minha fidelidade a Ti eu separei\r\n\r\nO meu melhor para Te honrar\r\nPois em  minha vida o Senhor está em primeiro lugar\r\n\r\nNão tocarei Senhor naquilo que é Teu\r\n\r\nSerei fiel  a Ti pois este é o meu prazer\r\nE tudo que em minhas mãos ficar o Senhor vai abençoar', NULL, 1),
(246, 'Teu Santo Nome', 'Todo ser que vive \r\nLouve ao nome do Senhor\r\n\r\nToda criatura se derrame aos seus pés\r\n\r\nAo som da Sua voz\r\nO universo se desfaz\r\n\r\nNão há outro nome comparado ao Grande Eu Sou\r\n\r\nE mesmo sendo pó \r\nCom tudo que há em mim confessarei\r\n\r\nQue céus e terra passarão \r\nMas o Teu nome é eterno\r\n\r\nTodo joelho dobrará\r\nAo ouvir Teu nome \r\nTeu Santo nome \r\nTodo ser confessará \r\n\r\nLouvado seja o Teu nome', NULL, 1),
(247, 'Creio que tu és a cura', 'Me escutas quando clamo\r\nE acalma o meu pensar\r\n\r\n\r\nMe levas pelo fogo\r\nCurando todo meu ser\r\n\r\n\r\nConfio   em     Ti,   confio  em  Ti\r\n\r\n\r\nCreio que Tu és a cura\r\nCreio que és tudo para  mim\r\n\r\n\r\nCreio que tu és a vida\r\nCreio que não há outro igual a Ti\r\n\r\n\r\nJesus, eu preciso de Ti\r\n\r\nNão há outro igual a Ti\r\n\r\nSenhor eu preciso de Ti\r\n\r\n\r\nNada é impossível para Ti\r\nNada é impossível\r\n\r\n\r\nNada é impossível para Ti\r\nTens o meu mundo em tuas mãos', NULL, 1),
(248, 'Eu te louvarei ', 'És Tu unica razão da minha adoração ó Jesus\r\n\r\nÉs Tu unica esperança que almejo ter ó Jesus\r\n\r\nConfio em Ti fui ajudado\r\nTua salvação tem me alegrado\r\n\r\nHoje há gozo em meu coração \r\nCom meu canto te louvarei\r\n\r\nEu te louvarei\r\nTe glorificarei \r\n\r\nEu te louvarei\r\nMeu Bom Jesus\r\n\r\nEm todo tempo te louvarei\r\nEm todo tempo te adorarei', NULL, 1),
(250, 'Teste de música nova', 'Testando música nova\r\n\r\nNovo teste', NULL, 1),
(251, 'Imperfeito ', 'Pai perdoa o meu jeito de achar que sou perfeito\r\n\r\nVejo erro nas pessoas, e não olho meus defeitos\r\n\r\nJustifico-me com o bem quando o mal eu pratiquei\r\n\r\nNo final eu reconheço quando eu erro outra vez\r\n\r\nEstou sempre declarando: Quero ser como criança\r\nMas o coração tá cheio de maldade e de vingança\r\n\r\nPai perdoa o meu jeito de achar que sou perfeito\r\nSou tão falho pecador detalhado de defeitos\r\n\r\nPai, meu Pai, tenho muito que mudar\r\nPai, meu Pai, me ajude a melhorar\r\n\r\nPai, a minha oração pode parecer tão dura\r\nÉ que essa realidade no meu peito só machuca\r\n\r\nNão dava pra esperar, amanhã seria tarde\r\nTe entregar meu coração é minha prioridade\r\n\r\nMe olhando nessa hora ainda não sou perfeito\r\n\r\nMas eu sinto que me amas mesmo eu sendo desse jeito\r\n\r\nObrigado por me ouvir, não vou nem me despedir\r\n\r\nFica aqui dentro de mim, para nunca mais sair', NULL, 1),
(252, 'Lindo És', 'Lindo, Lindo, Lindo És\r\nGlória, Glória eu te dou\r\nJesus\r\n\r\nEntão me leva a sala do trono\r\nMostra tua Beleza, quero ver tua face Deus', NULL, 1),
(253, 'Atos 2', 'Nós estamos aqui tão sedentos de Ti\r\nVem oh Deus, vem oh Deus\r\n\r\nEnche este lugar\r\nMeu desejo é sentir teu poder, teu poder\r\n\r\nEntão vem me incendiar\r\nMeu coração é o teu altar\r\nQuero ouvir o som do céu\r\nTua glória contemplar\r\n\r\nTe damos honra\r\nte damos glória\r\nTeu é o poder\r\nPra sempre, amém', NULL, 1),
(254, 'Homem de Guerra é o Jeová', 'Homem de guerra é o Jeová\r\nSeu nome é temido na terra\r\n\r\nA todos os seus inimigos venceu\r\nDeus Forte e Terrível em Louvores\r\n\r\nQuem é como o Senhor\r\nEntre os deuses sobre a terra\r\n\r\nQue nos livra do mal\r\nE nos da poder para guerrear\r\n\r\nPelo Senhor marchamos sim\r\nSeu exército poderoso é\r\n\r\nSua Glória sera vista em toda terra\r\n\r\nVamos cantar o canto da Vitória\r\nGlória a Deus\r\n\r\nVencemos a batalha\r\nToda arma contra nós perecerá\r\n\r\nO nosso General é Cristo\r\nSeguimos os seus passos\r\n\r\nNenhum inimigo nos resistirá\r\n\r\nPelo Messias marchamos sim\r\nEm suas mãos a chave da vitória\r\n\r\nQue nos leva a possuir a terra prometida\r\n\r\nVamos cantar, o canto da vitoria\r\nGlória a Deus, \r\n\r\nvencemos a batalha\r\nToda arma contra nós perecerá\r\n', NULL, 1),
(255, 'Fecha a porta do teu quarto e ora', 'Fecha a porta do Teu quarto e ora\r\n\r\nVou revelar os Meus mistérios\r\nComo ninguém revelou\r\n\r\nVou enxugar as Tuas lágrimas\r\nComo ninguém enxugou\r\n\r\nVou revelar os Meus mistérios\r\nComo ninguém revelou\r\n\r\nVou enxugar as Tuas lágrimas\r\nComo ninguém enxugou\r\n\r\nFalo contigo nas horas amargas\r\nFalo contigo Sou o Teu amor\r\n\r\nFalo contigo nas horas de angustias\r\nSou o Teu pastor\r\n\r\nFalo contigo nas horas amargas\r\nFalo contigo Sou o Teu amor\r\n\r\nFalo contigo nas horas de angustias\r\nSou o Teu pastor\r\n\r\nEu sou Teu Deus\r\nEu Te ouvi\r\nEu Te visitei\r\n\r\nChamei pelo Teu nome lá no meio do deserto\r\nNo meu Lugar Secreto', NULL, 1),
(256, 'Solta o cabo da nau', 'Oh! por que duvidar sobre as ondas do mar\r\n\r\nQuando cristo caminho abriu?\r\n\r\nQuando forçado és contra as ondas lutar\r\n\r\nSeu amor a ti quer revelar\r\n\r\nSolta o cabo da nau\r\nToma os remos nas mãos\r\n\r\nE navega com fé em jesus\r\n\r\nE então tu verás que bonança se faz\r\n\r\nPois com ele seguro serás\r\n\r\nTrevas vêm te assustar\r\nTempestades no mar\r\n\r\nDa montanha o mestre te vê\r\n\r\nE a tribulação ele vem socorrer\r\nSua mão bem te pode suster\r\n\r\nSolta o cabo da nau\r\nToma os remos nas mãos\r\n\r\nE navega com fé em jesus\r\n\r\nE então tu verás que bonança se faz\r\n\r\nPois com ele seguro serás\r\n\r\nPodes tu recordar maravilhas sem par?\r\nNo deserto o povo fartou\r\n\r\nE o mesmo poder, ele sempre terá\r\nPois não muda, e não falhará.\r\n\r\nSolta o cabo da nau\r\nToma os remos nas mãos\r\n\r\nE navega com fé em jesus\r\n\r\nE então tu verás que bonança se faz\r\n\r\nPois com ele seguro serás\r\n\r\nQuando pedes mais fé ele ouve, oh crê\r\nMesmo sendo em tribulação\r\n\r\nQuando a mão de poder o teu ego tirar\r\nSobre as ondas poderás andar\r\n\r\nSolta o cabo da nau\r\nToma os remos nas mãos\r\n\r\nE navega com fé em jesus\r\n\r\nE então tu verás que bonança se faz\r\n\r\nPois com ele seguro serás', NULL, 1),
(257, 'Deus é Deus ', 'Minha fé não esta firmada\r\nNas coisas que podes fazer\r\n\r\nEu aprendi a Te adorar pelo que És\r\n\r\nDele vêm o sim e o amém\r\nSomente dele e mais ninguém\r\n\r\nA Deus seja o louvor\r\n\r\nSe Deus fizer, Ele é Deus\r\nSe não fizer, Ele é Deus \r\n\r\nSe a porta abrir, Ele é Deus\r\nMas se fechar, continua sendo Deus\r\n\r\nSe a doença vier, Ele é Deus\r\nSe curado for, Ele é Deus\r\n\r\nSe tudo der certo, Ele é Deus\r\nMas se não der, continua sendo Deus\r\n\r\nNão o adoro pelo que Ele faz\r\nEu adoro pelo que Ele é\r\n\r\nHaja o que houver\r\nSempre será Deus\r\n\r\nDeus é Deus', NULL, 1),
(258, 'Só Tu és Santo', 'Tudo está preparado aqui\r\nA casa e o meu coração também\r\n\r\nTu És o único motivo que me fez chegar\r\n\r\nOs filhos já estão chegando aqui\r\nAgora, somos dois ou três ou mais\r\n\r\nEncontre meu coração disposto a queimar por Ti\r\n\r\nTodos os versos e canções que eu conseguir cantar\r\n\r\nTodas as vezes quebrantado, só quero te falar\r\n\r\nTeu é o Reino e a Glória pra sempre!\r\n\r\nTeu é o Domínio e o Poder, amém e amém!\r\n\r\nSó Tu És Santo! Só Tu És Santo!\r\n\r\nNão há outro como Tu\r\nNão há outro como Jesus', NULL, 1),
(259, 'Glória te darei a glória', 'O meu coração se inclina a adorar\r\nAquele que primeiro me amou\r\n\r\nFez de um perdido um filho a enxergar\r\nA majestade do seu redentor\r\n\r\nImenso prazer\r\nPra mim é te ter, Jesus\r\n\r\nDesde agora e para sempre \r\nTe adorarei\r\n\r\nGlória, te darei a Glória\r\nTodos os meus dias\r\nEnquanto eu viver\r\n\r\nEu quero dar-te Glória\r\n\r\nPreço muito alto ele pagou por mim\r\nTrouxe-me alegria de viver\r\n\r\nEnvia-me ao mundo para apregoar\r\nA graça e o amor que vem de Ti\r\n\r\nImenso prazer \r\nPra mim é te ter, Jesus\r\n\r\nDesde agora e para sempre\r\nTe adorarei\r\n\r\nGlória, te darei a Glória\r\nTodos os meus dias\r\nEnquanto eu viver\r\n\r\nEu quero dar-te Glória', NULL, 1),
(260, 'Rei da Glória', 'Nós temos tanta fome\r\nNós temos tanta sede de ti\r\n\r\nNão conseguimos conter\r\nLançamos nossas coroas diante do teu trono ó Deus\r\n\r\nNão conseguimos reter\r\n\r\nE junto com os anjos\r\nCantamos em uma só voz\r\n\r\nRei da Glória\r\nRei da Glória\r\nNós te amamos\r\nNós te amamos\r\n\r\nE não há ninguém mais Belo como Tu\r\nJesus ', NULL, 1),
(261, 'De volta ao lar - Pedro', 'Cordeiro Santo, Digno de toda adoração\r\n\r\nLindo Poderoso, você cativou meu coração\r\n\r\nVivo está nossa esperança\r\nE ela prometeu vir nos buscar\r\n\r\nO Espirito e noiva dizem\r\nVenha o Leão da tribo de Judá\r\n\r\nO famoso descendente de Davi\r\nVenceu a morte e o livro abrirá\r\n\r\nNão há mais lagrimas \r\nCantem de alegria\r\nEstamos voltando ao nosso lar', NULL, 1),
(262, 'Não sou daqui - Pedro', 'Eu não sou mais eu\r\nTeu me chamo agora\r\n\r\nE o eu se esqueceu\r\nDe quem eu fui outrora\r\n\r\nPois se vivo ou se morro\r\nEu faço à Ti\r\n\r\nNão pensando em mim mesmo \r\nPorque eu não sou daqui\r\n\r\nDocemente me chamou à morte\r\nTrouxe alegria na dor\r\n\r\nMe atraiu pra longe da minha corrupção \r\nE me inseriu em seu amor\r\n\r\nQuem é como o Senhor?\r\n\r\nÉ tudo sobre Ti\r\nSobre Ti\r\nNada sobre mim\r\nPorque eu não sou daqui\r\n', NULL, 1),
(263, 'Plano Perfeito', 'Um plano pra salvar, um pacto pra selar\r\nSilencio no céu\r\n\r\nResgate e salvação, encheu Seu coração\r\nEle nem hesitou\r\n\r\nNo palco do amor, o autor anunciou\r\nA vida e salvação\r\n\r\nTudo o que Ele fez foi obedecer \r\nO plano que Deus escreveu\r\n\r\nE quando a minha história parecia ter chegado ao fim\r\nA sua graça me alcançou\r\n\r\nE quando tudo parecia estar perdido naquela cruz\r\nO seu sangue me libertou\r\n\r\nJesus o plano perfeito \r\nMistério da graça que me transformou\r\n\r\nO seu nome é Jesus, nome sobre todos\r\nVeio pra salvar, Ele me salvou\r\n\r\n', NULL, 1),
(264, 'A casa é sua - Casa Worship', 'Você É Bem-vindo aqui\r\nA casa é Sua, pode entrar\r\n\r\nMe esvazio de mim\r\nMe esvazio de mim\r\n\r\nSopra o Teu vento aqui\r\nToma o Teu trono\r\nVem reinar\r\n\r\nNós queremos Te ouvir\r\nNós queremos Te ouvir\r\nEssa casa é Sua casa\r\nNós deixamos ela pra Você, Jesus!\r\n\r\nEssa casa é Sua casa\r\nNós deixamos ela pra Você, Jesus!\r\n\r\nEssa casa é Sua casa\r\nNós deixamos ela pra Você, Jesus!\r\n\r\nEssa casa é Sua casa\r\nNós deixamos ela pra Você, Jesus!\r\n\r\nApareça, que o Teu nome cresça\r\nEnche este lugar\r\nEnche este lugar\r\n\r\nApareça, que o Teu nome cresça\r\nVem me incendiar\r\nVem me incendiar\r\n\r\nApareça, que o Teu nome cresça\r\nEnche este lugar\r\nEnche este lugar\r\n\r\nApareça, que o Teu nome cresça\r\nVem me incendiar\r\nVem me incendiar\r\n\r\nEssa casa é Sua casa\r\nNós deixamos ela pra Você\r\nJesus\r\n\r\nEssa casa é Sua casa\r\nNós deixamos ela pra Você\r\nJesus\r\n\r\nEssa casa é Sua casa\r\nNós deixamos ela pra Você\r\nJesus\r\n\r\nEssa casa é Sua casa\r\nNós deixamos ela pra Você\r\nJesus\r\n\r\nApareça, que o Teu nome cresça\r\nEnche este lugar\r\nEnche este lugar\r\n\r\nApareça, que o Teu nome cresça\r\nVem me incendiar\r\nVem me incendiar\r\n\r\nApareça, que o Teu nome cresça\r\nEnche este lugar\r\nEnche este lugar\r\n\r\nApareça, que o Teu nome cresça\r\nVem me incendiar\r\nVem me incendiar\r\n\r\nEssa casa é Sua casa\r\nNós deixamos ela pra Você\r\nJesus\r\n\r\nEssa casa é Sua casa\r\nNós deixamos ela pra Você\r\nJesus\r\n\r\nEssa casa é Sua casa\r\nNós deixamos ela pra Você\r\nJesus\r\n\r\nEssa casa é Sua casa\r\nNós deixamos ela pra Você\r\nJesus\r\n\r\nEssa casa é Sua casa\r\nNós deixamos ela pra Você\r\nJesus\r\n\r\nApareça, que o Teu nome cresça\r\nEnche este lugar\r\nEnche este lugar\r\n\r\nApareça, que o Teu nome cresça\r\nVem me incendiar\r\nVem me incendiar\r\n\r\nApareça, que o Teu nome cresça\r\nEnche este lugar\r\nEnche este lugar\r\n\r\nApareça, que o Teu nome cresça\r\nVem me incendiar\r\nVem me incendiar\r\n\r\nEssa casa é Sua casa\r\nNós deixamos ela pra Você', NULL, 1),
(265, 'Que se abram os céus ', 'Tu És bem-vindo, aqui\r\nE em nosso coração\r\nToma a direção\r\n\r\nDeus, fogo consumidor\r\nSem impedimentos, vem\r\nFace a face, Senhor\r\n\r\nSopra em nós...\r\nEspírito\r\nTe queremos, Deus\r\n\r\nHô-ôu-úúh!\r\n\r\nEstou de pé...\r\nNa presença do Rei\r\nTua glória está aqui\r\nTeu amor liberta-me\r\n\r\nEstás aqui...\r\nNo trono de louvor\r\nTua presença me curou\r\nFascinado estou\r\n\r\nSopra em nós...\r\nEspírito\r\nTe queremos, Deus\r\n\r\nSopra em nós...\r\nEspírito\r\nTe queremos, Deus\r\n\r\nSopra em nós...\r\nEspírito\r\nTe queremos, Deus...\r\n\r\nÔ-ô-úh!\r\n\r\nTe queremos, Deus\r\nÔ-ô-ôôh-úh!\r\nTemos sede de Ti, Senhor\r\n\r\nQue se abram os céus...\r\nO Teu reino vem\r\nNossa fé está...\r\nNo nosso Deus\r\n\r\nQue se abram os céus...\r\nO Teu reino vem\r\nNossa fé está...\r\nNo nosso Deus\r\n\r\nQue se abram os céus \r\nO Teu reino vem \r\nNossa fé está\r\nNo nosso Deus\r\n\r\nQue se abram os céus\r\nO Teu reino vem \r\nNossa fé está\r\nNo nosso Deus \r\n\r\nQue os céus fechados, se abram\r\nTeu reino venha mover\r\nA nossa fé e esperança\r\nEstão em Deus\r\nGrande Deus\r\n\r\nÔ-ô-úh!\r\n(Ô-ô-úh)\r\nWôw!\r\n\r\nNossa esperança está em Ti, Jesus (wôw!)\r\nConfiamos, confiamos\r\n\r\nNão há outra rocha além de Ti, Jesus\r\nJesus, Jesus\r\nWôw!\r\n\r\n', NULL, 1),
(266, 'Lugar Secreto', 'Tu és tudo o que eu mais quero\r\nO meu fôlego, Tu és\r\nEm Teus braços, é o meu lugar\r\nEstou aqui, estou aqui\r\n\r\nPai, eu amo Sua presença\r\nTeu sorriso é vida em mim\r\nEu seguro em Suas mãos\r\nConfio em Ti, confio em Ti\r\n\r\nQuero ir mais fundo\r\nLeva-me mais perto\r\nOnde eu Te encontro\r\nNo Lugar Secreto\r\n\r\nAos Teus pés, me rendo\r\nPois a Tua Glória quero ver\r\nPai, eu amo Sua presença\r\nTeu sorriso é vida em mim\r\n\r\nEu seguro em Suas mãos\r\nEu confio em Ti, confio em Ti\r\n\r\nQuero ir mais fundo\r\nLeva-me mais perto\r\nOnde eu Te encontro\r\nNo lugar secreto\r\n\r\nAos Teus pés, me rendo\r\nPois a Tua Glória quero ver\r\n\r\nQuero ir mais fundo\r\nLeva-me mais perto\r\nOnde eu Te encontro\r\nNo lugar secreto\r\n\r\nAos Teus pés, me rendo\r\nPois a Tua Glória quero ver\r\n\r\nTudo o que eu mais quero é Te ver\r\nMe envolva com Tua Glória e poder\r\nTua majestade é real\r\nTua voz ecoa em meu ser\r\n\r\nTudo o que eu mais quero é Te ver\r\nMe envolva com Tua Glória e poder\r\nTua majestade é real\r\nTua voz ecoa em meu ser\r\n\r\nTudo o que eu mais quero é Te ver\r\nMe envolva com Tua Glória e poder\r\nTua majestade é real\r\nTua voz ecoa em meu ser\r\n\r\nTudo o que eu mais quero é Te ver\r\nMe envolva com Tua Glória e poder\r\nTua majestade é real\r\nTua voz ecoa em meu ser\r\n\r\nQuero ir mais fundo\r\nLeva-me mais perto\r\nOnde eu Te encontro\r\nNo lugar secreto\r\n\r\nAos Teus pés, me rendo\r\nPois a Tua Glória eu quero ver ó, Deus\r\n\r\nEu te quero\r\nEu vou, eu vou além\r\nEu vou além\r\nEu vou além\r\n\r\nEu te quero\r\nEu quero mais\r\nEu quero mais\r\n\r\nQuero ir mais fundo\r\nLeva-me mais perto\r\nOnde eu Te encontro\r\nNo lugar secreto\r\n\r\nAos Teus pés, me rendo\r\nPois a Tua Glória quero ver\r\n\r\nTudo o que eu mais quero é Te ver\r\nMe envolva com Tua Glória e poder\r\nTua majestade é real\r\nTua voz ecoa em meu ser\r\n\r\nTudo o que eu mais quero é Te ver\r\nMe envolva com Tua Glória e poder\r\nTua majestade é real\r\nTua voz ecoa em meu ser', NULL, 1),
(267, 'Aviva-nos', 'Enche esse ambiente\r\nEnche esse lugar\r\n\r\nTe damos liberdade\r\nQue venha Teu Espirito\r\n\r\nVem fazer o que nenhum homem fez\r\nVem fazer o que a historia nunca viu\r\n\r\nVem cumprir o descrito em Joel\r\nAviva-nos, aviva-nos\r\n\r\nQue seja hoje que seja agora\r\nVem e derrama o Teu Espirito\r\n\r\nA terra clama ôôô\r\nA terra chama chama\r\nMais uma vez queremos ver\r\nO céu descer\r\n\r\n', NULL, 1),
(268, 'Pra sempre Forever IBI', 'O universo chora\r\nO sol se apagou\r\nAli estava morto o Salvador\r\n\r\nSeu corpo lá na cruz\r\nSeu sangue derramou\r\nO peso do pecado Ele levou\r\n\r\nDeus Pai o abandonou\r\nCessou seu respirar\r\nEm trevas se encontrou o Filho\r\n\r\nA guerra começou, a morte enfrentou\r\nTodo o poder das trevas vencido foi\r\n\r\nA terra estremeceu, o sepulcro se abriu\r\nE nada vencerá Seu grande amor\r\n\r\nÓ, morte, onde estás?\r\nO Rei ressuscitou\r\nEle venceu pra sempre\r\n\r\nPra sempre Exaltado É\r\nPra sempre Adorado É\r\nPra sempre Ele vive\r\nRessuscitou, ressuscitou\r\n\r\nCantamos Aleluia (3x)\r\nO Cordeiro venceu', NULL, 1);
INSERT INTO `songs` (`id`, `title`, `lyric`, `Author`, `statusSongs`) VALUES
(269, 'Filho do Deus Vivo', 'Verbo vivo viveu entre nós\r\nFilho do homem, Filho de Deus\r\nCurando os enfermos, livrando os cativos\r\n\r\nEra chegado o reino de Deus\r\n\r\nFoi humilhado, crucificado\r\nTão rejeitado pelos seus\r\n\r\nRessuscitado, Glorificado\r\nO inferno e a morte venceu\r\n\r\nE agora que dizem que Tu és\r\nUm profeta, um agitador qualquer\r\n\r\nTeu Espirito me revela quem Tu és\r\nUngido de Deus\r\n\r\nTu és o Cristo, filho do Deus vivo\r\nSobre esta rocha Tua igreja está\r\n\r\nTu és o Cristo, filho do Deus vivo\r\nRocha inabalável, outro igual não há\r\nIgual não há\r\n\r\nFilho do homem, filho de Deus\r\nCurando os enfermos , livrando os cativos\r\n\r\nEra chegado o reino de Deus\r\n\r\nFoi humilhado, crucificado\r\nTão rejeitado pelos seus\r\n\r\nRessuscitado, Glorificado\r\nO inferno e a morte venceu\r\n\r\nE agora que dizem que Tu és\r\nUm profeta, um agitador qualquer\r\n\r\nTeu Espirito me revela quem Tu és\r\nUngido de Deus\r\n\r\nTu és o Cristo, filho do Deus vivo\r\nSobre esta rocha Tua igreja está\r\n\r\nTu és o Cristo, filho do Deus vivo\r\nRocha inabalável, outro igual não há\r\nIgual não há\r\n\r\n\r\n', NULL, 1),
(270, 'Carpinteiro ', 'Esconda-me em Ti, esconda-me em Ti\r\nNas asas do teu amor\r\n\r\nComo eu amo seu Nome, tens o Nome mais lindo que eu conheço\r\n\r\nQual é o som, qual é o som\r\nQuando Deus desce na terra?\r\nQual é o som, qual é o som?\r\n\r\nE esse é o som\r\nO Carpinteiro batendo a porta\r\nO Carpinteiro batendo a porta\r\n\r\nE esse é o som\r\nDo lado de dentro eu abro a porta', NULL, 1),
(271, 'Sou casa', 'Tem liberdade aqui\r\nEspirito de Deus\r\nEspirito de Deus\r\n\r\nTem liberdade aqui\r\nEspirito Santo \r\n\r\nEspirito Santo\r\n\r\nEu sou tua casa, tua morada\r\nEu sou Teu lar\r\n\r\nMude as coisas de lugar\r\nEu sou tua casa, tua morada\r\n\r\nEu sou teu lar\r\nMude as coisas de lugar\r\n\r\nO teu perdão é completo \r\nO teu perdão é completo\r\n\r\nEle sara minha alma\r\nEle sara minha alma\r\n\r\nO teu perdão é completo \r\nO teu perdão é completo\r\n\r\nEle sara minha alma\r\nEle sara minha alma\r\n\r\nEu sou tua casa, tua morada\r\nEu sou Teu lar\r\n\r\nMude as coisas de lugar\r\nEu sou tua casa, tua morada\r\n\r\nEu sou teu lar\r\nMude as coisas de lugar\r\n\r\n', NULL, 1),
(272, 'Deixa queimar', 'Ouvir o meu senhor\r\nBater na porta\r\n\r\nE o meu coração queimou\r\nOuvir o meu senhor\r\n\r\nBater na porta\r\nE o meu coração queimou\r\n\r\nEu não vou parar\r\nGastarei minha vida aos teus pés\r\nE eu não vou parar\r\n\r\nGastarei minha vida aos teus pés\r\nLarara larara lara\r\nLarara larara \r\n\r\nEu tenho um fogo\r\nCerrado em meu peito\r\n\r\nQue não me dar descanso\r\nEu não tenho descanso\r\n\r\nEu tenho um fogo\r\nCerrado em meu peito\r\n\r\nQue não me dar descanso\r\nEu não tenho descanso\r\n\r\nDeixa queimar, Deixa queimar\r\nDeixa queimar, Deixa queimar\r\n\r\nEu tenho um fogo\r\nCerrado em meu peito\r\n\r\nQue não me dar descanso\r\nEu não tenho descanso\r\n\r\nEu tenho um fogo\r\nCerrado em meu peito\r\n\r\nQue não me dar descanso\r\nEu não tenho descanso\r\n\r\nDeixa queimar, Deixa queimar\r\nDeixa queimar, Deixa queimar', NULL, 1),
(273, 'Quero conhecer Jesus', 'Meu orgulho me tirou do jardim\r\nTua humildade colocou o jardim em mim\r\n\r\nE se eu vendesse tudo o que tenho\r\nEm troca do amor, eu falharia\r\n\r\nPois o amor não se compra, nem se merece\r\nO amor se ganha\r\nDe graça o recebe\r\n\r\nE eu quero conhecer Jesus\r\nQuero conhecer Jesus\r\nE ser achado Nele\r\nSer achado Nele\r\n\r\nYeshua\r\nYeshua\r\n\r\n\r\nMeu amado é o mais belo\r\nEntre milhares e milhares\r\n\r\nNão nasci pra encher o bolso de dinheiro\r\nEu não nasci pra encher igreja\r\n\r\nEu nasci pra conhecer Jesus', NULL, 1),
(274, 'Vem cear', 'Cristo já nos preparou\r\nUm manjar que nos comprou\r\nE, agora, nos convida a cear\r\n\r\nE com celestial maná\r\nQue de graça Deus te dá\r\nVem, faminto, tua alma saciar\r\n\r\nVem cear, o Mestre chama, vem cear\r\nMesmo hoje tu te podes saciar\r\n\r\nPoucos pães multiplicou\r\nÁgua em vinho transformou\r\nVem, faminto, a Jesus, vem cear\r\n\r\nEis discípulos a voltar\r\nSem os peixes apanhar\r\nMas Jesus os manda outra vez partir\r\n\r\nAo tornar à praia, então\r\nVeem no fogo peixe e pão\r\nE Jesus, que os convida à ceia vir\r\n\r\nVem cear, o Mestre chama, vem cear\r\nMesmo hoje tu te podes saciar\r\n\r\nPoucos pães multiplicou\r\nÁgua em vinho transformou\r\nVem, faminto, a Jesus, vem cear\r\n\r\nQuem sedento se achar\r\nVenha a Cristo sem tardar\r\nPois o vinho sem mistura Ele dá\r\n\r\nE também dá vida, o pão\r\nQue nos traz consolação\r\nEis que tudo preparado já está\r\n\r\nVem cear, o Mestre chama, vem cear\r\nMesmo hoje tu te podes saciar\r\n\r\nPoucos pães multiplicou\r\nÁgua em vinho transformou\r\nVem, faminto, a Jesus, vem cear\r\n\r\nBreve Cristo vai descer\r\nE a Noiva receber\r\nSeu lugar ao lado do Senhor Jesus\r\n\r\nQuem a fome suportou\r\nE a sede já passou\r\nLá no céu irá cear em santa luz\r\n\r\nVem cear, o Mestre chama, vem cear\r\nMesmo hoje tu te podes saciar\r\n\r\nPoucos pães multiplicou\r\nÁgua em vinho transformou\r\nVem, faminto, a Jesus, vem cear\r\n', NULL, 1),
(275, 'É tempo', 'É tempo de nascer de novo\r\nÉ tempo de buscar Renovo\r\n\r\nÉ tempo de viver a graça\r\nAntes que se passe o tempo\r\n\r\nÉ tempo de Plantar\r\nÉ tempo de colher cem vezes mais\r\n\r\nÉ tempo de apossar dos lugares celestias\r\n\r\nVamos restaurar as brechas que o tempo abriu em nós\r\n\r\nVamos restaurar o templo na unção da nossa voz\r\n\r\nVamos ser a geração que volta pra Jerusalém\r\nA cidade do Grande Rei, Luz de todas as nações\r\n\r\nE quando a Glória encher de novo este lugar\r\nO cetro da Justiça para governar\r\n\r\nHora vem Teu Trono sobre nós\r\n\r\nHosana Hosana', NULL, 1),
(276, 'Campeão', 'Acredite é hora de vencer\r\nEssa força vem de dentro de você\r\nVocê pode até tocar o céu se crer\r\n\r\nAcredite que nenhum de nós\r\nJá nasceu com jeito pra ser super herói\r\n\r\nNossos sonhos, a gente que constrói\r\nÉ vencendo os limites, escalando as fortalezas\r\n\r\nConquistando o impossivel pela fé\r\n\r\nCampeão, vencedor, Deus da asas, faz teu voo\r\n\r\nCampeão, vencedor, essa fé que te faz imbativel te mostra o seu valor\r\n\r\nTantos recordes, você pode quebrar\r\nAs barreiras você pode ultrapassar e vencer\r\n\r\n', NULL, 1),
(277, 'Jesus Filho de Deus', 'Deixou os céus para me encontrar\r\nAqui não é o seu lugar\r\nUm amor assim o mundo não conheceu\r\n\r\nNaquela cruz se entregou\r\nO teu perdão me alcançou\r\nUm amor assim o mundo não conheceu\r\n\r\nNo altar de adoração \r\nSeja sempre exaltado\r\nJesus, Filho de Deus\r\n\r\nDeixou sua glória\r\nMorreu em meu lugar\r\nJesus, Filho de Deus\r\nTu És Jesus, Filho de Deus\r\n\r\nLevou sobre si pecado e dor\r\nVenceu a morte e ressuscitou\r\nUm amor assim o mundo não conheceu\r\n\r\nLevou sobre si pecado e dor\r\nVenceu a morte e ressuscitou\r\nUm amor assim o mundo não conheceu\r\n\r\n\r\nNo altar de adoração \r\nSeja sempre exaltado\r\nJesus, Filho de Deus\r\n\r\nDeixou sua glória\r\nMorreu em meu lugar\r\nJesus, Filho de Deus\r\nTu És Jesus, Filho de Deus\r\n\r\nSeja exaltado, engrandecido\r\nSeu nome é Santo, Poderoso é\r\n\r\nE não há nada que apague o seu amor\r\nA Cruz me libertou\r\nA Cruz me libertou\r\n\r\n\r\nNo altar de adoração \r\nSeja sempre exaltado\r\nJesus, Filho de Deus\r\n\r\nDeixou sua glória\r\nMorreu em meu lugar\r\nJesus, Filho de Deus\r\nTu És Jesus, Filho de Deus\r\n\r\nSeja exaltado, engrandecido\r\nSeu nome é Santo, Poderoso é\r\n\r\nE não há nada que apague o seu amor\r\nA Cruz me libertou\r\nA Cruz me libertou\r\n\r\n\r\n\r\n\r\n', NULL, 1),
(278, 'Faz um milagre em mim', 'Como Zaqueu eu quero subir\r\nO mais alto que eu puder\r\nSó pra te ver, olhar para ti\r\n\r\nE chamar sua atenção para mim\r\nEu preciso de ti senhor\r\nEu preciso de ti o pai\r\n\r\nSou pequeno demais\r\nMe dá a tua paz\r\nLargo tudo pra te seguir\r\n\r\nEntra na minha casa, entra na minha vida\r\nMexe com minha estrutura, sara todas as feridas\r\n\r\nMe ensina a ter santidade\r\nQuero amar somente a Ti\r\nPorque o Senhor é meu bem maior\r\nFaz um milagre em mim\r\n\r\n', NULL, 1);
